// Componente popup per la creazione di un nuovo progetto
const NewProjectDialog = ({ open, onClose, onCreate, workspaces }) => {
  const [newProjectName, setNewProjectName] = useState('');
  const [clientName, setClientName] = useState('');
  const [practiceArea, setPracticeArea] = useState('');
  const [deadline, setDeadline] = useState('');
  const [priority, setPriority] = useState('medium');
  const [confidentiality, setConfidentiality] = useState('standard');
  const [notes, setNotes] = useState('');
  const [selectedWorkspaces, setSelectedWorkspaces] = useState([]);
  const navigate = useNavigate();
  const theme = useTheme();

  // Lista delle aree di pratica comuni per studi legali
  const practiceAreas = [
    'Diritto Civile',
    'Diritto Commerciale',
    'Diritto Penale',
    'Diritto del Lavoro',
    'Diritto Tributario',
    'Diritto Amministrativo',
    'Diritto di Famiglia',
    'Diritto Immobiliare',
    'Proprietà Intellettuale',
    'Diritto Bancario',
    'Diritto delle Assicurazioni',
    'Contenzioso',
    'Diritto dell\'Immigrazione',
    'Diritto Ambientale',
    'Diritto Internazionale',
    'Altro'
  ];

  const handleCreateProject = () => {
    if (newProjectName.trim()) {
      const newProject = {
        id: Date.now().toString(),
        name: newProjectName,
        client: clientName,
        practiceArea,
        deadline,
        priority,
        confidentiality,
        notes,
        workspaces: selectedWorkspaces,
        date: new Date().toLocaleDateString(),
        agents: []
      };

      onCreate(newProject);

      // Reset dei campi
      setNewProjectName('');
      setClientName('');
      setPracticeArea('');
      setDeadline('');
      setPriority('medium');
      setConfidentiality('standard');
      setNotes('');
      setSelectedWorkspaces([]);
    }
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="md" fullWidth>
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        borderBottom: `1px solid ${theme.palette.divider}`,
        px: 3,
        py: 2
      }}>
        <Typography variant="h5" fontWeight={600}>Crea Nuovo Progetto</Typography>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Tooltip title="Dashboard" arrow>
            <IconButton
              size="small"
              onClick={() => navigate('/')}
              sx={{ 
                bgcolor: theme.palette.background.paper,
                boxShadow: 1
              }}
            >
              <HomeIcon />
            </IconButton>
          </Tooltip>
        </Box>
      </Box>
      <DialogContent>
        <Grid container spacing={2} sx={{ mt: 0 }}>
          <Grid item xs={12}>
            <TextField
              autoFocus
              margin="dense"
              label="Nome del progetto"
              fullWidth
              variant="outlined"
              value={newProjectName}
              onChange={(e) => setNewProjectName(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <FolderIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              margin="dense"
              label="Nome del cliente"
              fullWidth
              variant="outlined"
              value={clientName}
              onChange={(e) => setClientName(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <BusinessIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>Area di pratica</InputLabel>
              <Select
                value={practiceArea}
                onChange={(e) => setPracticeArea(e.target.value)}
                label="Area di pratica"
                startAdornment={
                  <InputAdornment position="start" sx={{ ml: -0.5, mr: 0.5 }}>
                    <GavelIcon color="primary" />
                  </InputAdornment>
                }
              >
                {practiceAreas.map((area) => (
                  <MenuItem key={area} value={area}>{area}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={4}>
            <TextField
              margin="dense"
              label="Scadenza"
              type="date"
              fullWidth
              variant="outlined"
              value={deadline}
              onChange={(e) => setDeadline(e.target.value)}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <EventIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12} md={4}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>Priorità</InputLabel>
              <Select
                value={priority}
                onChange={(e) => setPriority(e.target.value)}
                label="Priorità"
              >
                <MenuItem value="low">Bassa</MenuItem>
                <MenuItem value="medium">Media</MenuItem>
                <MenuItem value="high">Alta</MenuItem>
                <MenuItem value="urgent">Urgente</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={4}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>Riservatezza</InputLabel>
              <Select
                value={confidentiality}
                onChange={(e) => setConfidentiality(e.target.value)}
                label="Riservatezza"
                startAdornment={
                  <InputAdornment position="start" sx={{ ml: -0.5, mr: 0.5 }}>
                    <LockIcon color="primary" />
                  </InputAdornment>
                }
              >
                <MenuItem value="standard">Standard</MenuItem>
                <MenuItem value="sensitive">Sensibile</MenuItem>
                <MenuItem value="highly_confidential">Altamente confidenziale</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>Condividi con gruppi</InputLabel>
              <Select
                multiple
                value={selectedWorkspaces}
                onChange={(e) => setSelectedWorkspaces(e.target.value)}
                label="Condividi con gruppi"
                renderValue={(selected) => (
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                    {selected.map((value) => {
                      const workspace = workspaces.find(w => w.id === value);
                      return (
                        <Chip 
                          key={value} 
                          label={workspace ? workspace.name : value} 
                          size="small" 
                        />
                      );
                    })}
                  </Box>
                )}
                startAdornment={
                  <InputAdornment position="start" sx={{ ml: -0.5, mr: 0.5 }}>
                    <GroupsIcon color="primary" />
                  </InputAdornment>
                }
              >
                {workspaces.map((workspace) => (
                  <MenuItem key={workspace.id} value={workspace.id}>
                    {workspace.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12}>
            <TextField
              label="Note aggiuntive"
              multiline
              rows={3}
              fullWidth
              variant="outlined"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              margin="dense"
            />
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions sx={{ px: 3, pb: 3 }}>
        <Button onClick={onClose} variant="outlined">Annulla</Button>
        <Button 
          onClick={handleCreateProject} 
          variant="contained"
          disabled={!newProjectName.trim()}
          startIcon={<AddIcon />}
        >
          Crea Progetto
        </Button>
      </DialogActions>
    </Dialog>
  );
};

// Componente per il dialogo di modifica progetto
const EditProjectDialog = ({ open, onClose, project, workspaces, onUpdate }) => {
  const [projectToEdit, setProjectToEdit] = useState(null);
  const [selectedWorkspaces, setSelectedWorkspaces] = useState([]);
  const theme = useTheme();

  // Inizializza i dati del progetto quando il dialogo viene aperto
  useEffect(() => {
    if (open && project) {
      setProjectToEdit({...project});
      setSelectedWorkspaces(project.workspaces || []);
    }
  }, [open, project]);

  const handleUpdateProject = () => {
    if (projectToEdit) {
      const updatedProject = {...projectToEdit, workspaces: selectedWorkspaces};
      onUpdate(updatedProject);
    }
  };

  if (!projectToEdit) return null;

  return (
    <Dialog 
      open={open} 
      onClose={onClose}
      maxWidth="sm"
      fullWidth
    >
      <DialogTitle>Modifica Progetto</DialogTitle>
      <DialogContent>
        <Grid container spacing={2} sx={{ mt: 0 }}>
          <Grid item xs={12}>
            <TextField
              autoFocus
              margin="dense"
              label="Nome del progetto"
              fullWidth
              variant="outlined"
              value={projectToEdit.name || ''}
              onChange={(e) => setProjectToEdit({...projectToEdit, name: e.target.value})}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <FolderIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              margin="dense"
              label="Nome del cliente"
              fullWidth
              variant="outlined"
              value={projectToEdit.client || ''}
              onChange={(e) => setProjectToEdit({...projectToEdit, client: e.target.value})}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <BusinessIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>Area di pratica</InputLabel>
              <Select
                value={projectToEdit.practiceArea || ''}
                onChange={(e) => setProjectToEdit({...projectToEdit, practiceArea: e.target.value})}
                label="Area di pratica"
                startAdornment={
                  <InputAdornment position="start" sx={{ ml: -0.5, mr: 0.5 }}>
                    <GavelIcon color="primary" />
                  </InputAdornment>
                }
              >
                {['Diritto Civile', 'Diritto Commerciale', 'Diritto Penale', 'Diritto del Lavoro', 
                  'Diritto Tributario', 'Diritto Amministrativo', 'Diritto di Famiglia', 'Altro'].map((area) => (
                  <MenuItem key={area} value={area}>{area}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              margin="dense"
              label="Scadenza"
              type="date"
              fullWidth
              variant="outlined"
              value={projectToEdit.deadline || ''}
              onChange={(e) => setProjectToEdit({...projectToEdit, deadline: e.target.value})}
              InputLabelProps={{
                shrink: true,
              }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <EventIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>Priorità</InputLabel>
              <Select
                value={projectToEdit.priority || 'medium'}
                onChange={(e) => setProjectToEdit({...projectToEdit, priority: e.target.value})}
                label="Priorità"
              >
                <MenuItem value="low">Bassa</MenuItem>
                <MenuItem value="medium">Media</MenuItem>
                <MenuItem value="high">Alta</MenuItem>
                <MenuItem value="urgent">Urgente</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>Condividi con gruppi</InputLabel>
              <Select
                multiple
                value={selectedWorkspaces}
                onChange={(e) => setSelectedWorkspaces(e.target.value)}
                label="Condividi con gruppi"
                renderValue={(selected) => (
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                    {selected.map((value) => {
                      const workspace = workspaces.find(w => w.id === value);
                      return (
                        <Chip 
                          key={value} 
                          label={workspace ? workspace.name : value} 
                          size="small" 
                        />
                      );
                    })}
                  </Box>
                )}
                startAdornment={
                  <InputAdornment position="start" sx={{ ml: -0.5, mr: 0.5 }}>
                    <GroupsIcon color="primary" />
                  </InputAdornment>
                }
              >
                {workspaces.map((workspace) => (
                  <MenuItem key={workspace.id} value={workspace.id}>
                    {workspace.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Annulla</Button>
        <Button 
          onClick={handleUpdateProject} 
          variant="contained"
          disabled={!projectToEdit.name}
        >
          Aggiorna
        </Button>
      </DialogActions>
    </Dialog>
  );
};

// Componente messaggi nell'output
const Message = ({ type, content, timestamp, agent }) => {
  const theme = useTheme();

  // Determina lo stile del messaggio in base al tipo
  const getMessageStyle = () => {
    switch (type) {
      case 'system':
        return {
          bgcolor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#F5F5F5', 0.8),
          borderLeft: `3px solid ${theme.palette.info.main}`,
        };
      case 'agent':
        return {
          bgcolor: theme.palette.mode === 'dark' ? alpha('#2D2B55', 0.6) : alpha('#EDF7FF', 0.8),
          borderLeft: `3px solid ${theme.palette.primary.main}`,
        };
      case 'artifact':
        return {
          bgcolor: theme.palette.mode === 'dark' ? alpha('#253D35', 0.6) : alpha('#E8F5E9', 0.8),
          borderLeft: `3px solid ${theme.palette.success.main}`,
        };
      default:
        return {
          bgcolor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#FFFFFF', 0.8),
        };
    }
  };

  return (
    <Box
      sx={{
        p: 2,
        borderRadius: 2,
        mb: 2,
        ...getMessageStyle(),
      }}
    >
      {agent && (
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <Chip 
            label={agent.name} 
            size="small" 
            color="primary" 
            avatar={<PsychologyIcon />} 
            sx={{ mr: 1 }} 
          />
          {timestamp && (
            <Typography variant="caption" color="text.secondary" sx={{ display: 'flex', alignItems: 'center' }}>
              <AccessTimeIcon fontSize="inherit" sx={{ mr: 0.5 }} />
              {timestamp}
            </Typography>
          )}
        </Box>
      )}

      <Typography
        sx={{
          fontFamily: '"Inter", sans-serif',
          whiteSpace: 'pre-wrap',
          wordBreak: 'break-word',
        }}
      >
        {content}
      </Typography>
    </Box>
  );
};

// Componente per log attività nel quadrante Lexychain
const ActivityLog = ({ activities }) => {
  const theme = useTheme();

  const getStatusIcon = (status) => {
    switch(status) {
      case 'thinking':
        return <ThinkingIcon />;
      case 'viewing':
        return <VisibilityIcon />;
      case 'editing':
        return <EditIcon />;
      case 'browsing':
        return <TerminalIcon />;
      default:
        return <AccessTimeIcon />;
    }
  };

  return (
    <Box sx={{ mb: 2 }}>
      <List dense>
        {activities.map((activity, index) => (
          <ListItem 
            key={index} 
            disablePadding 
            sx={{ 
              mb: 1,
              bgcolor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#F5F5F5', 0.5),
              borderRadius: 1,
             overflow: 'hidden'
            }}
          >
            <ListItemButton dense sx={{ py: 0.75 }}>
              <ListItemIcon sx={{ minWidth: 36 }}>
                {getStatusIcon(activity.status)}
              </ListItemIcon>
              <ListItemText 
                primary={activity.description}
                secondary={activity.timestamp}
                primaryTypographyProps={{ fontSize: '0.9rem' }}
                secondaryTypographyProps={{ fontSize: '0.75rem' }}
              />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

