// src/components/common/Header.js
import React, { useContext } from 'react';
import { AppBar, Toolbar, Typography, IconButton, Box, useTheme } from '@mui/material';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import { ThemeContext } from '../../context/ThemeContext';
import styled from 'styled-components';

const StyledAppBar = styled(AppBar)`
  background: ${props => props.theme.palette.mode === 'dark' 
    ? 'linear-gradient(90deg, #1a237e 0%, #283593 100%)' 
    : 'linear-gradient(90deg, #5c6bc0 0%, #3f51b5 100%)'
  };
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.15);
`;

const LogoText = styled(Typography)`
  font-family: 'Montserrat', sans-serif;
  font-weight: 700;
  letter-spacing: 1.5px;
  background: linear-gradient(45deg, #f8f9fa 30%, #e0e0e0 90%);
  background-clip: text;
  -webkit-background-clip: text;
  color: transparent;
  text-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);
`;

function Header() {
  const theme = useTheme();
  const { toggleColorMode } = useContext(ThemeContext);

  return (
    <StyledAppBar position="static" theme={theme}>
      <Toolbar>
        <Box display="flex" alignItems="center">
          <img src="/logo.svg" alt="Valis Logo" height="40" />
          <LogoText variant="h5" sx={{ ml: 1 }}>
            VALIS
          </LogoText>
          <Typography variant="subtitle1" sx={{ ml: 1, opacity: 0.8 }}>
            by Lexygraph AI
          </Typography>
        </Box>

        <Box sx={{ flexGrow: 1 }} />

        <IconButton onClick={toggleColorMode} color="inherit">
          {theme.palette.mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
        </IconButton>

        <IconButton color="inherit">
          <AccountCircleIcon />
        </IconButton>
      </Toolbar>
    </StyledAppBar>
  );
}

export default Header;
