import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Box, 
  Typography, 
  TextField, 
  InputAdornment, 
  IconButton, 
  Paper, 
  Grid,
  useTheme,
  CircularProgress,
  Chip,
  Avatar,
  Card,
  CardContent,
  Fade
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import MicIcon from '@mui/icons-material/Mic';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import BookmarkIcon from '@mui/icons-material/Bookmark';
import TuneIcon from '@mui/icons-material/Tune';

// VALIS Logo component with hover animations
const ValisLogo = ({ theme }) => (
  <Box sx={{ 
    display: 'flex', 
    alignItems: 'center', 
    position: 'relative',
    cursor: 'pointer',
    '&:hover .valis-dot': {
      transform: 'scale(1.5)',
      boxShadow: `0 0 12px ${theme.palette.primary.main}`,
    },
    '&:hover .valis-line': {
      width: '100%',
      boxShadow: `0 0 8px ${theme.palette.primary.main}`,
    }
  }}>
    {/* Punto viola in alto a destra del logo */}
    <Box 
      className="valis-dot"
      sx={{ 
        position: 'absolute', 
        width: 6, 
        height: 6, 
        borderRadius: '50%', 
        bgcolor: '#9A7CFF', 
        right: -6, 
        top: 0,
        transition: 'all 0.4s cubic-bezier(0.17, 0.67, 0.83, 0.67)',
      }} 
    />

    {/* Punto viola in basso a sinistra del logo */}
    <Box 
      className="valis-dot"
      sx={{ 
        position: 'absolute', 
        width: 6, 
        height: 6, 
        borderRadius: '50%', 
        bgcolor: '#9A7CFF', 
        left: -6, 
        bottom: 8,
        transition: 'all 0.4s cubic-bezier(0.17, 0.67, 0.83, 0.67)',
      }} 
    />

    {/* Logo text */}
    <Typography
      variant="h3"
      fontFamily="'Times New Roman', Times, serif"
      fontWeight={600}
      letterSpacing="0.02em"
      sx={{
        color: theme.palette.mode === 'dark' ? '#FFFFFF' : '#14142B',
        fontSize: '2.5rem',
        position: 'relative',
      }}
    >
      VALIS
    </Typography>

    {/* Linea decorativa sotto il logo - si allunga al passaggio del mouse */}
    <Box 
      className="valis-line"
      sx={{ 
        position: 'absolute', 
        height: 2.5, 
        width: '20%', 
        bgcolor: '#9A7CFF', 
        left: 0, 
        bottom: -4,
        borderRadius: 3,
        transition: 'all 0.4s cubic-bezier(0.17, 0.67, 0.83, 0.67)',
      }} 
    />

    {/* "by Lexygraph AI" text */}
    <Typography
      variant="subtitle2"
      fontFamily="'Inter', sans-serif"
      sx={{
        position: 'absolute',
        color: theme.palette.text.secondary,
        left: 0,
        bottom: -25,
        fontWeight: 400,
        fontSize: '0.875rem',
      }}
    >
      by Lexygraph AI
    </Typography>
  </Box>
);

// Dashboard component with multi-agent search functionality
function Dashboard() {
  const navigate = useNavigate();
  const theme = useTheme();
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [activeAgents, setActiveAgents] = useState([]);
  const [recentQueries, setRecentQueries] = useState([
    "Market analysis for renewable energy",
    "Competitive landscape in AI assistants",
    "Investment trends in biotech 2025"
  ]);

  // Sample agent data
  const agents = [
    { id: 1, name: "Research Agent", avatar: "R", color: "#9A7CFF" },
    { id: 2, name: "Data Analysis Agent", avatar: "D", color: "#4CCEAC" },
    { id: 3, name: "Market Insights Agent", avatar: "M", color: "#F44336" },
    { id: 4, name: "Creative Agent", avatar: "C", color: "#FFA726" }
  ];

  const handleNavigation = (section) => {
    navigate(`/${section}`);
  };

  const handleSearch = () => {
    if (!searchQuery.trim()) return;

    // Mock search process
    setIsSearching(true);
    setActiveAgents([
      agents[0],
      agents[1],
      agents[Math.floor(Math.random() * 2) + 2] // Randomly choose between agents 3 and 4
    ]);

    // Simulate search delay and results
    setTimeout(() => {
      setIsSearching(false);
      setSearchResults([
        {
          id: 1,
          title: `Analysis: ${searchQuery}`,
          content: `Comprehensive analysis of "${searchQuery}" shows significant patterns...`,
          source: "Research Agent",
          confidence: 92
        },
        {
          id: 2,
          title: `Data Insights: ${searchQuery}`,
          content: `Statistical breakdown of key metrics related to "${searchQuery}"...`,
          source: "Data Analysis Agent", 
          confidence: 87
        },
        {
          id: 3,
          title: `Market Context: ${searchQuery}`,
          content: `Market implications and business opportunities for "${searchQuery}"...`,
          source: "Market Insights Agent",
          confidence: 78
        }
      ]);

      // Add to recent queries if not already present
      if (!recentQueries.includes(searchQuery)) {
        setRecentQueries(prev => [searchQuery, ...prev.slice(0, 2)]);
      }
    }, 2500);
  };

  return (
    <Box sx={{ p: 3, maxWidth: '1200px', mx: 'auto' }}>
      {/* Header with logo */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
        <ValisLogo theme={theme} />
        <Box>
          {/* User Avatar or additional controls could go here */}
        </Box>
      </Box>

      {/* Search Section */}
      <Paper 
        elevation={3} 
        sx={{ 
          p: 3, 
          mb: 4, 
          borderRadius: 2,
          background: theme.palette.mode === 'dark' ? 'rgba(35, 35, 50, 0.9)' : 'rgba(255, 255, 255, 0.9)',
        }}
      >
        <Typography variant="h5" fontWeight="500" mb={2}>
          Multi-Agent Search
        </Typography>

        <TextField
          fullWidth
          placeholder="Ask the VALIS multi-agent system..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton>
                  <MicIcon />
                </IconButton>
                <IconButton onClick={handleSearch}>
                  <SearchIcon />
                </IconButton>
              </InputAdornment>
            ),
            sx: { 
              borderRadius: 2,
              py: 0.5,
              '& .MuiOutlinedInput-notchedOutline': {
                borderColor: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.15)' : 'rgba(0, 0, 0, 0.15)'
              }
            }
          }}
          sx={{ mb: 2 }}
        />

        {/* Active Agents */}
        {isSearching && (
          <Fade in={isSearching}>
            <Box sx={{ mt: 3 }}>
              <Typography variant="subtitle2" gutterBottom sx={{ color: 'text.secondary' }}>
                Agents working on your query:
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                {activeAgents.map(agent => (
                  <Chip
                    key={agent.id}
                    avatar={<Avatar sx={{ bgcolor: agent.color }}>{agent.avatar}</Avatar>}
                    label={agent.name}
                    sx={{ 
                      animation: 'pulse 1.5s infinite',
                      '@keyframes pulse': {
                        '0%': { opacity: 0.7 },
                        '50%': { opacity: 1 },
                        '100%': { opacity: 0.7 }
                      }
                    }}
                  />
                ))}
                <Box sx={{ display: 'flex', alignItems: 'center', ml: 2 }}>
                  <CircularProgress size={20} />
                  <Typography variant="body2" sx={{ ml: 1, color: 'text.secondary' }}>
                    Processing query...
                  </Typography>
                </Box>
              </Box>
            </Box>
          </Fade>
        )}

        {/* Recent Queries */}
        {!isSearching && !searchResults.length && (
          <Box sx={{ mt: 3 }}>
            <Typography variant="subtitle2" gutterBottom sx={{ color: 'text.secondary' }}>
              Recent Queries:
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              {recentQueries.map((query, idx) => (
                <Chip 
                  key={idx} 
                  label={query} 
                  onClick={() => {
                    setSearchQuery(query);
                    handleSearch();
                  }}
                  sx={{ 
                    cursor: 'pointer',
                    '&:hover': { bgcolor: theme.palette.action.hover }
                  }}
                />
              ))}
            </Box>
          </Box>
        )}
      </Paper>

      {/* Search Results */}
      {searchResults.length > 0 && (
        <Box sx={{ mt: 4 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
            <Typography variant="h6">
              Results for: "{searchQuery}"
            </Typography>
            <Box>
              <IconButton size="small">
                <TuneIcon />
              </IconButton>
            </Box>
          </Box>

          <Grid container spacing={3}>
            {searchResults.map(result => (
              <Grid item xs={12} key={result.id}>
                <Card sx={{ 
                  borderRadius: 2,
                  border: '1px solid',
                  borderColor: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.12)' : 'rgba(0, 0, 0, 0.12)',
                  transition: 'all 0.3s ease',
                  '&:hover': {
                    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.1)',
                    transform: 'translateY(-2px)'
                  }
                }}>
                  <CardContent>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                      <Typography variant="h6" fontWeight="500">
                        {result.title}
                      </Typography>
                      <Box>
                        <IconButton size="small">
                          <BookmarkIcon fontSize="small" />
                        </IconButton>
                        <IconButton size="small">
                          <MoreVertIcon fontSize="small" />
                        </IconButton>
                      </Box>
                    </Box>

                    <Typography variant="body2" sx={{ mb: 2, color: 'text.secondary' }}>
                      {result.content}
                    </Typography>

                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <Chip 
                        size="small"
                        label={result.source}
                        sx={{ bgcolor: theme.palette.primary.main, color: '#fff' }}
                      />
                      <Typography variant="caption" sx={{ color: 'text.secondary' }}>
                        Confidence: {result.confidence}%
                      </Typography>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      )}
    </Box>
  );
}

export default Dashboard;
