// valis/src/pages/Dashboard.js
import React, { useState, useEffect, useMemo } from 'react';
import { 
  Box, 
  Typography, 
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton,
  Tooltip,
  Container
} from '@mui/material';
import { styled, createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import PersonIcon from '@mui/icons-material/Person';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';
import LogoutIcon from '@mui/icons-material/Logout';

// Tema personalizzato che rispecchia esattamente i colori di VALIS
const getTheme = (mode) => createTheme({
  palette: {
    mode,
    primary: {
      main: mode === 'dark' ? '#9A7CFF' : '#7C4DFF',
      light: mode === 'dark' ? '#B8A2FF' : '#A280FF',
      dark: mode === 'dark' ? '#7356E5' : '#6035CC',
      contrastText: '#FFFFFF',
    },
    secondary: {
      main: mode === 'dark' ? '#03DAC5' : '#00BFA5',
      light: mode === 'dark' ? '#5EFFEE' : '#64FFDA',
      dark: mode === 'dark' ? '#00867D' : '#008975',
      contrastText: mode === 'dark' ? '#000000' : '#000000',
    },
    background: {
      default: mode === 'dark' ? '#14142B' : '#F5F7FC',
      paper: mode === 'dark' ? '#1C1C3C' : '#FFFFFF',
    },
    text: {
      primary: mode === 'dark' ? '#F3F4F6' : '#1E293B',
      secondary: mode === 'dark' ? '#B0B8C4' : '#64748B',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontWeight: 700,
      letterSpacing: '-0.025em',
    },
    h2: {
      fontWeight: 700,
      letterSpacing: '-0.025em',
    },
    h3: {
      fontWeight: 600,
      letterSpacing: '-0.02em',
    },
    h4: {
      fontWeight: 600,
      letterSpacing: '-0.015em',
    },
    h5: {
      fontWeight: 500,
      letterSpacing: '-0.01em',
    },
    h6: {
      fontWeight: 500,
      letterSpacing: '-0.005em',
    },
    button: {
      fontWeight: 500,
      letterSpacing: '0.02em',
      textTransform: 'none',
    },
  },
  shape: {
    borderRadius: 12,
  },
  shadows: [
    'none',
    '0px 2px 4px rgba(0, 0, 0, 0.05), 0px 1px 2px rgba(0, 0, 0, 0.05)',
    '0px 4px 8px rgba(0, 0, 0, 0.05), 0px 2px 4px rgba(0, 0, 0, 0.05)',
    '0px 8px 16px rgba(0, 0, 0, 0.05), 0px 4px 8px rgba(0, 0, 0, 0.05)',
    '0px 12px 24px rgba(0, 0, 0, 0.05), 0px 6px 12px rgba(0, 0, 0, 0.05)',
    '0 15px 30px rgba(27, 10, 59, 0.12), 0 8px 16px rgba(27, 10, 59, 0.08)',
  ],
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 12,
        },
        containedPrimary: {
          background: mode === 'dark' ? 
            'linear-gradient(90deg, #9A7CFF 0%, #7356E5 100%)' : 
            'linear-gradient(90deg, #7C4DFF 0%, #6035CC 100%)',
          '&:hover': {
            background: mode === 'dark' ? 
              'linear-gradient(90deg, #B8A2FF 0%, #9A7CFF 100%)' : 
              'linear-gradient(90deg, #A280FF 0%, #7C4DFF 100%)',
          }
        }
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 16,
          backgroundImage: 'none',
        },
      },
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          scrollbarWidth: 'thin',
          '&::-webkit-scrollbar': {
            width: '6px',
            height: '6px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: mode === 'dark' ? '#4B5563' : '#CBD5E1',
            borderRadius: '3px',
          },
          '&::-webkit-scrollbar-track': {
            backgroundColor: mode === 'dark' ? '#1F2937' : '#F1F5F9',
          },
        },
      },
    },
  },
});

// Logo VALIS esattamente come nella pagina di login
const ValisLogo = ({ theme }) => (
  <Box sx={{ display: 'flex', alignItems: 'center', position: 'relative' }}>
    {/* Punto viola in alto a destra del logo */}
    <Box 
      sx={{ 
        position: 'absolute', 
        width: 8, 
        height: 8, 
        borderRadius: '50%', 
        bgcolor: '#9A7CFF', 
        right: -4, 
        top: 0 
      }} 
    />

    {/* Punto viola in alto a sinistra del logo */}
    <Box 
      sx={{ 
        position: 'absolute', 
        width: 8, 
        height: 8, 
        borderRadius: '50%', 
        bgcolor: '#9A7CFF', 
        left: -4, 
        top: 0 
      }} 
    />

    {/* Logo text */}
    <Typography
      variant="h3"
      fontFamily="'Times New Roman', Times, serif"
      fontWeight={700}
      letterSpacing="0.02em"
      sx={{
        color: theme.palette.mode === 'dark' ? '#FFFFFF' : '#14142B',
        fontSize: '2.5rem'
      }}
    >
      VALIS
    </Typography>

    {/* Linea decorativa sotto il logo */}
    <Box 
      sx={{ 
        position: 'absolute', 
        height: 3, 
        width: '40%', 
        bgcolor: '#9A7CFF', 
        left: 0, 
        bottom: -4 
      }} 
    />

    {/* "by Lexygraph AI" text */}
    <Typography
      variant="subtitle2"
      fontFamily="'Inter', sans-serif"
      sx={{
        position: 'absolute',
        color: theme.palette.text.secondary,
        left: 0,
        bottom: -25,
        fontWeight: 400,
        fontSize: '0.875rem'
      }}
    >
      by Lexygraph AI
    </Typography>
  </Box>
);

// Feature Button - Styled like the authentication page buttons
const FeatureButton = styled(Button)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  padding: theme.spacing(5),
  backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#FFFFFF', 0.8),
  border: `1px solid ${theme.palette.mode === 'dark' ? alpha('#FFFFFF', 0.1) : alpha('#7C4DFF', 0.1)}`,
  borderRadius: 16,
  boxShadow: theme.palette.mode === 'dark' 
    ? '0 8px 24px rgba(0, 0, 0, 0.2)' 
    : '0 8px 24px rgba(124, 77, 255, 0.08)',
  transition: 'all 0.3s ease',
  position: 'relative',
  overflow: 'hidden',
  '&:hover': {
    backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.9) : alpha('#FFFFFF', 1),
    transform: 'translateY(-8px)',
    boxShadow: theme.palette.mode === 'dark' 
      ? '0 12px 28px rgba(0, 0, 0, 0.3)' 
      : '0 12px 28px rgba(124, 77, 255, 0.15)',
    '& .feature-button-icon': {
      transform: 'scale(1.1) translateY(-5px)',
      color: theme.palette.primary.main,
    },
    '& .feature-button-text': {
      transform: 'translateY(3px)',
      color: theme.palette.primary.main,
    }
  },
  '&:active': {
    transform: 'translateY(-4px) scale(0.98)',
  },
  '& .feature-button-icon': {
    fontSize: 60,
    marginBottom: theme.spacing(2.5),
    color: theme.palette.mode === 'dark' ? theme.palette.primary.light : theme.palette.primary.main,
    transition: 'all 0.3s ease',
  },
  '& .feature-button-text': {
    fontWeight: 500,
    fontSize: '1.125rem',
    transition: 'all 0.3s ease',
    color: theme.palette.text.primary,
  }
}));

// Header Action Button
const HeaderActionButton = styled(IconButton)(({ theme }) => ({
  width: 40,
  height: 40,
  backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#FFFFFF', 0.8),
  border: `1px solid ${theme.palette.mode === 'dark' ? alpha('#FFFFFF', 0.1) : alpha('#7C4DFF', 0.1)}`,
  borderRadius: 12,
  transition: 'all 0.2s ease',
  '&:hover': {
    backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.9) : alpha('#FFFFFF', 1),
    transform: 'translateY(-2px)',
    boxShadow: theme.palette.mode === 'dark' 
      ? '0 6px 12px rgba(0, 0, 0, 0.2)' 
      : '0 6px 12px rgba(124, 77, 255, 0.12)',
  },
  '&:active': {
    transform: 'translateY(-1px) scale(0.97)',
  },
  '& .MuiSvgIcon-root': {
    fontSize: 20,
    color: theme.palette.mode === 'dark' ? theme.palette.primary.light : theme.palette.primary.main,
  }
}));

// Enhanced typing effect with longer durations and smoother transitions
const TypingEffect = ({ messages, typingSpeed = 50, deleteSpeed = 25, delayAfterType = 7000 }) => {
  const [displayText, setDisplayText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isWaiting, setIsWaiting] = useState(false);

  useEffect(() => {
    const currentMessage = messages[currentIndex];

    if (isWaiting) {
      const waitTimer = setTimeout(() => {
        setIsWaiting(false);
        setIsDeleting(true);
      }, delayAfterType);
      return () => clearTimeout(waitTimer);
    }

    if (isDeleting && displayText === '') {
      setIsDeleting(false);
      setCurrentIndex((currentIndex + 1) % messages.length);
      return;
    }

    if (!isDeleting && displayText === currentMessage) {
      setIsWaiting(true);
      return;
    }

    const timeout = setTimeout(() => {
      if (isDeleting) {
        setDisplayText(prev => prev.substring(0, prev.length - 1));
      } else {
        setDisplayText(currentMessage.substring(0, displayText.length + 1));
      }
    }, isDeleting ? deleteSpeed : typingSpeed);

    return () => clearTimeout(timeout);
  }, [displayText, isDeleting, currentIndex, messages, typingSpeed, deleteSpeed, delayAfterType, isWaiting]);

  return <span>{displayText}</span>;
};

// Custom Icon Components 
const NetworkGraphIcon = () => (
  <svg width="60" height="60" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M17.5,7A5.5,5.5 0 0,1 23,12.5A5.5,5.5 0 0,1 17.5,18C15.79,18 14.27,17.22 13.26,16H10.74C9.73,17.22 8.21,18 6.5,18A5.5,5.5 0 0,1 1,12.5A5.5,5.5 0 0,1 6.5,7C8.21,7 9.73,7.78 10.74,9H13.26C14.27,7.78 15.79,7 17.5,7M17.5,9A3.5,3.5 0 0,0 14,12.5A3.5,3.5 0 0,0 17.5,16A3.5,3.5 0 0,0 21,12.5A3.5,3.5 0 0,0 17.5,9M6.5,9A3.5,3.5 0 0,0 3,12.5A3.5,3.5 0 0,0 6.5,16A3.5,3.5 0 0,0 10,12.5A3.5,3.5 0 0,0 6.5,9M12,14A1,1 0 0,1 11,13A1,1 0 0,1 12,12A1,1 0 0,1 13,13A1,1 0 0,1 12,14Z" />
  </svg>
);

const MagnifyingGlassIcon = () => (
  <svg width="60" height="60" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M9.5,3A6.5,6.5 0 0,1 16,9.5C16,11.11 15.41,12.59 14.44,13.73L14.71,14H15.5L20.5,19L19,20.5L14,15.5V14.71L13.73,14.44C12.59,15.41 11.11,16 9.5,16A6.5,6.5 0 0,1 3,9.5A6.5,6.5 0 0,1 9.5,3M9.5,5C7,5 5,7 5,9.5C5,12 7,14 9.5,14C12,14 14,12 14,9.5C14,7 12,5 9.5,5Z" />
  </svg>
);

const ButlerIcon = () => (
  <svg width="60" height="60" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M16,1H8A3,3 0 0,0 5,4V5H19V4A3,3 0 0,0 16,1M19,6H5V20A3,3 0 0,0 8,23H16A3,3 0 0,0 19,20V6M11.65,7.15L16.36,11.86L14.93,13.29L13.5,11.86V17H10.5V11.86L9.07,13.29L7.64,11.86L11.65,7.15Z" />
  </svg>
);

const ClientIcon = () => (
  <svg width="60" height="60" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M12,4A4,4 0 0,1 16,8A4,4 0 0,1 12,12A4,4 0 0,1 8,8A4,4 0 0,1 12,4M12,14C16.42,14 20,15.79 20,18V20H4V18C4,15.79 7.58,14 12,14Z" />
  </svg>
);

function Dashboard() {
  const [mode, setMode] = useState(() => {
    // Retrieve theme from local settings or use system preferences
    const savedMode = localStorage.getItem('themeMode');
    if (savedMode) {
      return savedMode;
    }
    return window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
  });

  const theme = useMemo(() => getTheme(mode), [mode]);
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  // Latin legal brocards (Latin only, brief)
  const legalBrocards = [
    "Dura lex, sed lex",
    "Pacta sunt servanda",
    "In dubio pro reo",
    "Ubi societas, ibi ius",
    "Ignorantia legis neminem excusat"
  ];

  // Set theme in localStorage when it changes
  useEffect(() => {
    localStorage.setItem('themeMode', mode);
  }, [mode]);

  // Toggle theme function
  const toggleColorMode = () => {
    setMode(prevMode => prevMode === 'light' ? 'dark' : 'light');
  };

  // Handler for navigation to different sections
  const handleNavigation = (section) => {
    console.log(`Navigating to ${section}`);
    // Add navigation logic here
  };

  // Handle logout - semplificato senza dipendenza da auth
  const handleLogout = () => {
    try {
      // Rimuovi i token dall'archiviazione locale
      localStorage.removeItem('access_token');
      localStorage.removeItem('refresh_token');

      // Reindirizza alla pagina di login
      window.location.href = '/login';
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ 
        display: 'flex', 
        flexDirection: 'column',
        minHeight: '100vh', 
        background: theme.palette.background.default,
        position: 'relative'
      }}>
        {/* Header */}
        <Box sx={{ 
          p: 3, 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          position: 'relative',
          zIndex: 10
        }}>
          <ValisLogo theme={theme} />

          <Box sx={{ display: 'flex', gap: 1.5 }}>
            <Tooltip title="Profilo" arrow>
              <HeaderActionButton onClick={() => handleNavigation('profilo')}>
                <PersonIcon />
              </HeaderActionButton>
            </Tooltip>
            <Tooltip title={mode === 'dark' ? "Modalità chiara" : "Modalità scura"} arrow>
              <HeaderActionButton onClick={toggleColorMode}>
                {theme.palette.mode === 'dark' 
                  ? <LightModeIcon />
                  : <DarkModeIcon />
                }
              </HeaderActionButton>
            </Tooltip>
            <Tooltip title="Logout" arrow>
              <HeaderActionButton onClick={handleLogout}>
                <LogoutIcon />
              </HeaderActionButton>
            </Tooltip>
          </Box>
        </Box>

        {/* Main content */}
        <Container maxWidth="lg" sx={{ 
          position: 'relative', 
          zIndex: 2, 
          flexGrow: 1,
          display: 'flex',
          flexDirection: 'column'
        }}>
          <Box component="main" sx={{ 
            flexGrow: 1,
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            py: 4
          }}>
            {/* Welcome message - Latin brocards with enhanced styling */}
            <Box sx={{ 
              mb: 8, 
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              textAlign: 'center',
              maxWidth: '900px'
            }}>
              <Typography 
                variant="h1" 
                sx={{ 
                  fontWeight: 700,
                  mb: 2,
                  fontSize: { xs: '2.5rem', sm: '3.5rem', md: '4.5rem' },
                  color: theme.palette.mode === 'dark' ? theme.palette.primary.light : theme.palette.primary.main,
                  letterSpacing: '-0.02em',
                  lineHeight: 1.2,
                  fontStyle: 'italic',
                  padding: '0 16px',
                }}
              >
                <TypingEffect messages={legalBrocards} />
              </Typography>
              <Typography 
                variant="h5" 
                color="textSecondary"
                sx={{
                  mt: 4,
                  fontWeight: 400,
                  letterSpacing: '0.01em',
                }}
              >
                Seleziona una funzionalità per iniziare
              </Typography>
            </Box>

            {/* Main navigation - centered with enhanced styling */}
            <Grid container spacing={4} justifyContent="center" sx={{ maxWidth: '1200px', mx: 'auto' }}>
              <Grid item xs={12} sm={6} md={3} sx={{ display: 'flex', justifyContent: 'center' }}>
                <FeatureButton onClick={() => handleNavigation('multiagente')}>
                  <NetworkGraphIcon />
                  <Typography className="feature-button-text">Multiagente</Typography>
                </FeatureButton>
              </Grid>
              <Grid item xs={12} sm={6} md={3} sx={{ display: 'flex', justifyContent: 'center' }}>
                <FeatureButton onClick={() => handleNavigation('ali')}>
                  <MagnifyingGlassIcon />
                  <Typography className="feature-button-text">ALI</Typography>
                </FeatureButton>
              </Grid>
              <Grid item xs={12} sm={6} md={3} sx={{ display: 'flex', justifyContent: 'center' }}>
                <FeatureButton onClick={() => handleNavigation('agenti')}>
                  <ButlerIcon />
                  <Typography className="feature-button-text">Agenti</Typography>
                </FeatureButton>
              </Grid>
              <Grid item xs={12} sm={6} md={3} sx={{ display: 'flex', justifyContent: 'center' }}>
                <FeatureButton onClick={() => handleNavigation('clienti')}>
                  <ClientIcon />
                  <Typography className="feature-button-text">Clienti</Typography>
                </FeatureButton>
              </Grid>
