// src/context/ThemeContext.js
import React, { createContext, useState, useMemo } from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { CssBaseline } from '@mui/material';

export const ThemeContext = createContext({
  toggleColorMode: () => {},
  theme: null,
});

export const ThemeContextProvider = ({ children }) => {
  const [mode, setMode] = useState('light');

  const colorMode = useMemo(
    () => ({
      toggleColorMode: () => {
        setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
      },
    }),
    [],
  );

  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode,
          ...(mode === 'light'
            ? {
                // Tema chiaro - Viola
                primary: {
                  main: '#673ab7', // Viola primario
                  light: '#9575cd',
                  dark: '#4527a0',
                  contrastText: '#ffffff',
                },
                secondary: {
                  main: '#7e57c2', // Viola pi첫 chiaro per accenti
                  light: '#b39ddb',
                  dark: '#4d2c91',
                },
                background: {
                  default: '#f7f5fb', // Bianco con sfumatura viola
                  paper: '#ffffff',
                },
                text: {
                  primary: '#33204c', // Viola molto scuro per testo primario
                  secondary: '#69588a', // Viola medio per testo secondario
                },
                divider: 'rgba(103, 58, 183, 0.12)',
              }
            : {
                // Tema scuro - Viola
                primary: {
                  main: '#9c64ff', // Viola chiaro brillante
                  light: '#b794ff',
                  dark: '#7638dd',
                  contrastText: '#ffffff',
                },
                secondary: {
                  main: '#b39ddb', // Lavanda per accenti
                  light: '#d1c4e9',
                  dark: '#7c52c8',
                },
                background: {
                  default: '#13091d', // Viola scuro profondo
                  paper: '#2a1a3a', // Viola scuro per elementi
                  contrast: '#37255a', // Viola scuro pi첫 chiaro
                },
                text: {
                  primary: '#e9e1ff', // Lavanda chiaro per testo
                  secondary: '#c7b8f3', // Lavanda per testo secondario
                },
                divider: 'rgba(156, 100, 255, 0.12)',
              }),
        },
        typography: {
          fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
        },
        shape: {
          borderRadius: 8,
        },
        components: {
          MuiButton: {
            styleOverrides: {
              root: {
                textTransform: 'none',
                borderRadius: 8,
              },
              containedPrimary: {
                boxShadow: mode === 'light' 
                  ? '0 2px 10px rgba(103, 58, 183, 0.2)' 
                  : '0 2px 10px rgba(156, 100, 255, 0.25)', 
              }
            },
          },
          MuiCard: {
            styleOverrides: {
              root: {
                borderRadius: 12,
              },
            },
          },
          MuiPaper: {
            styleOverrides: {
              elevation1: {
                boxShadow: mode === 'light' 
                  ? '0 2px 8px rgba(103, 58, 183, 0.08)' 
                  : '0 2px 8px rgba(0, 0, 0, 0.3)', 
              },
              elevation3: {
                boxShadow: mode === 'light' 
                  ? '0 4px 15px rgba(103, 58, 183, 0.1)' 
                  : '0 4px 15px rgba(0, 0, 0, 0.4)', 
              }
            },
          },
          MuiSwitch: {
            styleOverrides: {
              switchBase: {
                color: mode === 'light' ? '#9c64ff' : '#b39ddb',
              },
              track: {
                backgroundColor: mode === 'light' ? '#d1c4e9' : '#37255a',
              },
            },
          },
        },
      }),
    [mode],
  );

  return (
    <ThemeContext.Provider value={{ ...colorMode, theme }}>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        {children}
      </ThemeProvider>
    </ThemeContext.Provider>
  );
};
