// valis/src/pages/Dashboard.js
import React, { useState, useEffect, useMemo } from 'react';
import { 
  Box, 
  Typography, 
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton,
  Tooltip,
  Container,
  Paper,
  Fade,
  Grow
} from '@mui/material';
import { styled, createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import PersonIcon from '@mui/icons-material/Person';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';
import LogoutIcon from '@mui/icons-material/Logout';
import NotificationsNoneIcon from '@mui/icons-material/NotificationsNone';

// Tema personalizzato che rispecchia esattamente i colori di VALIS con miglioramenti
const getTheme = (mode) => createTheme({
  palette: {
    mode,
    primary: {
      main: mode === 'dark' ? '#9A7CFF' : '#7C4DFF',
      light: mode === 'dark' ? '#B8A2FF' : '#A280FF',
      dark: mode === 'dark' ? '#7356E5' : '#6035CC',
      contrastText: '#FFFFFF',
    },
    secondary: {
      main: mode === 'dark' ? '#03DAC5' : '#00BFA5',
      light: mode === 'dark' ? '#5EFFEE' : '#64FFDA',
      dark: mode === 'dark' ? '#00867D' : '#008975',
      contrastText: mode === 'dark' ? '#000000' : '#000000',
    },
    background: {
      default: mode === 'dark' ? '#14142B' : '#F5F7FC',
      paper: mode === 'dark' ? '#1C1C3C' : '#FFFFFF',
      contrast: mode === 'dark' ? '#252545' : '#F0F4FA',
    },
    text: {
      primary: mode === 'dark' ? '#F3F4F6' : '#1E293B',
      secondary: mode === 'dark' ? '#B0B8C4' : '#64748B',
    },
    divider: mode === 'dark' ? 'rgba(255, 255, 255, 0.08)' : 'rgba(124, 77, 255, 0.08)',
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontWeight: 700,
      letterSpacing: '-0.025em',
    },
    h2: {
      fontWeight: 700,
      letterSpacing: '-0.025em',
    },
    h3: {
      fontWeight: 600,
      letterSpacing: '-0.02em',
    },
    h4: {
      fontWeight: 600,
      letterSpacing: '-0.015em',
    },
    h5: {
      fontWeight: 500,
      letterSpacing: '-0.01em',
    },
    h6: {
      fontWeight: 500,
      letterSpacing: '-0.005em',
    },
    button: {
      fontWeight: 500,
      letterSpacing: '0.02em',
      textTransform: 'none',
    },
  },
  shape: {
    borderRadius: 12,
  },
  shadows: [
    'none',
    '0px 2px 4px rgba(0, 0, 0, 0.05), 0px 1px 2px rgba(0, 0, 0, 0.05)',
    '0px 4px 8px rgba(0, 0, 0, 0.05), 0px 2px 4px rgba(0, 0, 0, 0.05)',
    '0px 8px 16px rgba(0, 0, 0, 0.05), 0px 4px 8px rgba(0, 0, 0, 0.05)',
    '0px 12px 24px rgba(0, 0, 0, 0.05), 0px 6px 12px rgba(0, 0, 0, 0.05)',
    mode === 'dark' 
      ? '0 15px 30px rgba(0, 0, 0, 0.4), 0 8px 16px rgba(0, 0, 0, 0.2)'
      : '0 15px 30px rgba(124, 77, 255, 0.1), 0 8px 16px rgba(124, 77, 255, 0.06)',
  ],
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          fontWeight: 500,
          textTransform: 'none',
          padding: '10px 20px',
        },
        containedPrimary: {
          background: mode === 'dark' ? 
            'linear-gradient(90deg, #9A7CFF 0%, #7356E5 100%)' : 
            'linear-gradient(90deg, #7C4DFF 0%, #6035CC 100%)',
          boxShadow: mode === 'dark' ?
            '0 4px 12px rgba(154, 124, 255, 0.3)' :
            '0 4px 12px rgba(124, 77, 255, 0.2)',
          '&:hover': {
            background: mode === 'dark' ? 
              'linear-gradient(90deg, #B8A2FF 0%, #9A7CFF 100%)' : 
              'linear-gradient(90deg, #A280FF 0%, #7C4DFF 100%)',
            boxShadow: mode === 'dark' ?
              '0 6px 16px rgba(154, 124, 255, 0.4)' :
              '0 6px 16px rgba(124, 77, 255, 0.3)',
            transform: 'translateY(-2px)',
          },
          '&:active': {
            transform: 'translateY(1px)',
            boxShadow: mode === 'dark' ?
              '0 2px 8px rgba(154, 124, 255, 0.3)' :
              '0 2px 8px rgba(124, 77, 255, 0.2)',
          }
        }
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 16,
          backgroundImage: 'none',
          transition: 'box-shadow 0.3s ease-in-out, transform 0.3s ease-in-out',
        },
        elevation1: {
          boxShadow: mode === 'dark' ?
            '0 4px 20px rgba(0, 0, 0, 0.2)' :
            '0 4px 20px rgba(124, 77, 255, 0.06)',
          border: mode === 'dark' ?
            '1px solid rgba(255, 255, 255, 0.05)' :
            '1px solid rgba(124, 77, 255, 0.05)',
        },
      },
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          scrollbarWidth: 'thin',
          '&::-webkit-scrollbar': {
            width: '6px',
            height: '6px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: mode === 'dark' ? '#4B5563' : '#CBD5E1',
            borderRadius: '3px',
          },
          '&::-webkit-scrollbar-track': {
            backgroundColor: mode === 'dark' ? '#1F2937' : '#F1F5F9',
          },
          transition: 'background-color 0.3s ease-in-out',
        },
      },
    },
    MuiTooltip: {
      styleOverrides: {
        tooltip: {
          backgroundColor: mode === 'dark' ? '#252545' : '#1E293B',
          color: '#FFFFFF',
          fontSize: '0.75rem',
          padding: '8px 12px',
          borderRadius: 8,
          boxShadow: mode === 'dark' ?
            '0 4px 16px rgba(0, 0, 0, 0.3)' :
            '0 4px 16px rgba(30, 41, 59, 0.1)',
        },
        arrow: {
          color: mode === 'dark' ? '#252545' : '#1E293B',
        }
      }
    },
  },
});

// Logo VALIS avanzato con animazioni sottili
const ValisLogo = ({ theme }) => (
  <Box sx={{ 
    display: 'flex', 
    alignItems: 'center', 
    position: 'relative',
    '&:hover .valis-dot': {
      transform: 'scale(1.2)',
      boxShadow: `0 0 10px ${theme.palette.primary.main}`,
    },
    '&:hover .valis-line': {
      width: '45%',
      boxShadow: `0 0 8px ${theme.palette.primary.main}`,
    }
  }}>
    {/* Punto viola in alto a destra del logo */}
    <Box 
      className="valis-dot"
      sx={{ 
        position: 'absolute', 
        width: 8, 
        height: 8, 
        borderRadius: '50%', 
        bgcolor: theme.palette.primary.main, 
        right: -4, 
        top: 0,
        transition: 'all 0.3s ease-in-out',
        boxShadow: `0 0 5px ${theme.palette.primary.main}`,
      }} 
    />

    {/* Punto viola in alto a sinistra del logo */}
    <Box 
      className="valis-dot"
      sx={{ 
        position: 'absolute', 
        width: 8, 
        height: 8, 
        borderRadius: '50%', 
        bgcolor: theme.palette.primary.main, 
        left: -4, 
        top: 0,
        transition: 'all 0.3s ease-in-out',
        boxShadow: `0 0 5px ${theme.palette.primary.main}`,
      }} 
    />

    {/* Logo text */}
    <Typography
      variant="h3"
      fontFamily="'Times New Roman', Times, serif"
      fontWeight={700}
      letterSpacing="0.02em"
      sx={{
        color: theme.palette.mode === 'dark' ? '#FFFFFF' : '#14142B',
        fontSize: '2.5rem',
        position: 'relative',
        transition: 'all 0.3s ease',
        '&:hover': {
          textShadow: `0 0 15px ${alpha(theme.palette.primary.main, 0.5)}`,
        }
      }}
    >
      VALIS
    </Typography>

    {/* Linea decorativa sotto il logo */}
    <Box 
      className="valis-line"
      sx={{ 
        position: 'absolute', 
        height: 3, 
        width: '40%', 
        bgcolor: theme.palette.primary.main, 
        left: 0, 
        bottom: -4,
        transition: 'all 0.3s ease-in-out',
        borderRadius: 4,
      }} 
    />

    {/* "by Lexygraph AI" text */}
    <Typography
      variant="subtitle2"
      fontFamily="'Inter', sans-serif"
      sx={{
        position: 'absolute',
        color: theme.palette.text.secondary,
        left: 0,
        bottom: -25,
        fontWeight: 400,
        fontSize: '0.875rem',
        transition: 'all 0.3s ease',
      }}
    >
      by Lexygraph AI
    </Typography>
  </Box>
);

// Feature Button - Styled like the authentication page buttons but with premium finishes
const FeatureButton = styled(Button)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  padding: theme.spacing(5),
  height: '100%',
  backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#FFFFFF', 0.8),
  backdropFilter: 'blur(10px)',
  border: `1px solid ${theme.palette.mode === 'dark' ? alpha('#FFFFFF', 0.1) : alpha('#7C4DFF', 0.1)}`,
  borderRadius: 16,
  boxShadow: theme.palette.mode === 'dark' 
    ? '0 8px 24px rgba(0, 0, 0, 0.2)' 
    : '0 8px 24px rgba(124, 77, 255, 0.08)',
  transition: 'all 0.3s cubic-bezier(0.17, 0.67, 0.83, 0.67)',
  position: 'relative',
  overflow: 'hidden',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
