// src/App.js - Versione corretta senza ALI
import React, { useState, useMemo } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeContext } from './context/ThemeContext';

// Pages & Components
import Dashboard from './pages/Dashboard';
import Multiagent from './components/multiagent/Multiagent';
import ProfileSection from './components/profile/ProfileSection'; 

// Tema personalizzato che rispecchia i colori di VALIS
const getTheme = (mode) => createTheme({
  palette: {
    mode,
    primary: {
      main: mode === 'dark' ? '#9A7CFF' : '#7C4DFF',
      light: mode === 'dark' ? '#B8A2FF' : '#A280FF',
      dark: mode === 'dark' ? '#7356E5' : '#6035CC',
      contrastText: '#FFFFFF',
    },
    secondary: {
      main: mode === 'dark' ? '#03DAC5' : '#00BFA5',
      light: mode === 'dark' ? '#5EFFEE' : '#64FFDA',
      dark: mode === 'dark' ? '#00867D' : '#008975',
      contrastText: mode === 'dark' ? '#000000' : '#000000',
    },
    background: {
      default: mode === 'dark' ? '#14142B' : '#F5F7FC',
      paper: mode === 'dark' ? '#1C1C3C' : '#FFFFFF',
    },
    text: {
      primary: mode === 'dark' ? '#F3F4F6' : '#1E293B',
      secondary: mode === 'dark' ? '#B0B8C4' : '#64748B',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
  },
  shape: {
    borderRadius: 12,
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          textTransform: 'none',
        },
      },
    },
  },
});

// Auth Guard Component
const PrivateRoute = ({ children }) => {
  // Per semplificare il test, consideriamo tutti autenticati
  return children;

  // In implementazione reale:
  // const isAuthenticated = localStorage.getItem('access_token');
  // return isAuthenticated ? children : <Navigate to="/" />;
};

function App() {
  const [mode, setMode] = useState(() => {
    // Retrieve theme from local settings or use system preferences
    const savedMode = localStorage.getItem('themeMode');
    if (savedMode) {
      return savedMode;
    }
    return window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
  });

  const theme = useMemo(() => getTheme(mode), [mode]);

  // Toggle theme function
  const toggleColorMode = () => {
    setMode(prevMode => {
      const newMode = prevMode === 'light' ? 'dark' : 'light';
      localStorage.setItem('themeMode', newMode);
      return newMode;
    });
  };

  // Theme context value
  const themeContextValue = useMemo(() => ({
    toggleColorMode,
    theme
  }), [toggleColorMode, theme]);

  return (
    <ThemeContext.Provider value={themeContextValue}>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Router>
          <Routes>
            <Route 
              path="/" 
              element={
                <PrivateRoute>
                  <Dashboard />
                </PrivateRoute>
              } 
            />
            <Route 
              path="/multiagente" 
              element={
                <PrivateRoute>
                  <Multiagent />
                </PrivateRoute>
              } 
            />
            <Route 
              path="/profile" 
              element={
                <PrivateRoute>
                  <ProfileSection />
                </PrivateRoute>
              } 
            />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </Router>
      </ThemeProvider>
    </ThemeContext.Provider>
  );
}

export default App;
