// valis/src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  useTheme, 
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton
} from '@mui/material';
import { styled, createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Sidebar from '../components/common/Sidebar';
import MenuIcon from '@mui/icons-material/Menu';
import GroupWorkIcon from '@mui/icons-material/GroupWork';
import AutoAwesomeIcon from '@mui/icons-material/AutoAwesome';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';

// Tema personalizzato futuristico
const getTheme = (mode) => createTheme({
  palette: {
    mode,
    primary: {
      main: mode === 'dark' ? '#8C52FF' : '#6200EE',
      light: mode === 'dark' ? '#B38AFF' : '#9E7CFF',
      dark: mode === 'dark' ? '#5A2CA0' : '#3A1078',
      contrastText: '#FFFFFF',
    },
    secondary: {
      main: mode === 'dark' ? '#03DAC5' : '#00BFA5',
      light: mode === 'dark' ? '#5EFFEE' : '#64FFDA',
      dark: mode === 'dark' ? '#00867D' : '#008975',
      contrastText: mode === 'dark' ? '#000000' : '#000000',
    },
    background: {
      default: mode === 'dark' ? '#121212' : '#F5F7FA',
      paper: mode === 'dark' ? '#1E1E1E' : '#FFFFFF',
