// src/pages/Login.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthContext';
import { useTheme } from '../context/ThemeContext';
import './Login.css'; // Importiamo gli stili che abbiamo estratto dall'HTML

const Login = () => {
  const navigate = useNavigate();
  const { login, signup, resetPassword, verifyOTP, signInWithGoogle, user } = useAuth();
  const { isDarkTheme, toggleTheme } = useTheme();

  // State per il controllo delle tab
  const [activeTab, setActiveTab] = useState('login');
  // State per il controllo delle notifiche
  const [notification, setNotification] = useState({ show: false, message: '', type: '' });
  // State per i form
  const [loginFormData, setLoginFormData] = useState({ email: '', password: '', remember: false });
  const [registerFormData, setRegisterFormData] = useState({ 
    email: '', 
    password: '', 
    confirmPassword: '', 
    termsAccepted: false 
  });
  const [recoveryEmail, setRecoveryEmail] = useState('');
  const [verificationCode, setVerificationCode] = useState(['', '', '', '', '', '']);
  const [verificationEmail, setVerificationEmail] = useState('');

  // Refs per inputs di verifica
  const digitRefs = useRef([]);

  // State per password visibility
  const [passwordVisibility, setPasswordVisibility] = useState({
    loginPassword: false,
    registerPassword: false,
    confirmPassword: false
  });

  // State per password strength
  const [passwordStrength, setPasswordStrength] = useState(0);

  // State per resend countdown
  const [resendCountdown, setResendCountdown] = useState(0);

  // Effetto per reindirizzare se l'utente è già loggato
  useEffect(() => {
    if (user) {
      navigate('/');
    }
  }, [user, navigate]);

  // Funzione per mostrare notifiche
  const showNotification = (message, type) => {
    setNotification({ show: true, message, type });

    // Auto-hide dopo 5 secondi
    setTimeout(() => {
      setNotification({ ...notification, show: false });
    }, 5000);
  };

  // Cambio tab
  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setNotification({ show: false, message: '', type: '' });
  };

  // Toggle password visibility
  const togglePasswordVisibility = (field) => {
    setPasswordVisibility({
      ...passwordVisibility,
      [field]: !passwordVisibility[field]
    });
  };

  // Gestione password strength
  const calculatePasswordStrength = (password) => {
    let strength = 0;

    if (password.length >= 8) strength += 1;
    if (password.match(/[a-z]/) && password.match(/[A-Z]/)) strength += 1;
    if (password.match(/[0-9]/)) strength += 1;
    if (password.match(/[^a-zA-Z0-9]/)) strength += 1;

    setPasswordStrength(strength);
  };

  // Funzione per gestire il login
  const handleLogin = async (e) => {
    e.preventDefault();

    const { email, password } = loginFormData;
    const result = await login(email, password);

    if (result.success) {
      showNotification('Accesso effettuato con successo', 'success');
      setTimeout(() => navigate('/'), 1500);
    } else {
      showNotification(result.error || 'Errore durante l\'accesso', 'error');
    }
  };

  // Funzione per gestire la registrazione
  const handleRegister = async (e) => {
    e.preventDefault();

    const { email, password, confirmPassword, termsAccepted } = registerFormData;

    if (!termsAccepted) {
      showNotification('Per favore accetta i termini e condizioni per continuare', 'error');
      return;
    }

    if (password !== confirmPassword) {
      showNotification('Le password non corrispondono', 'error');
      return;
    }

    const result = await signup(email, password);

    if (result.success) {
      if (result.data?.user?.identities?.length === 0) {
        // Email già registrata ma non confermata
        showNotification('Email già registrata. Controlla la tua email per confermare', 'success');
      } else if (result.data?.user?.confirmation_sent_at) {
        // Email confirmation required
        showNotification('Registrazione completata! Controlla la tua email', 'success');
        setVerificationEmail(email);
        setActiveTab('verification');
        startResendCountdown();
      } else {
        // Auto-confirmed signup
        showNotification('Registrazione completata con successo', 'success');
        setTimeout(() => navigate('/'), 1500);
      }
    } else {
      showNotification(result.error || 'Errore durante la registrazione', 'error');
    }
  };

  // Funzione per gestire il recupero password
  const handlePasswordRecovery = async (e) => {
    e.preventDefault();

    const result = await resetPassword(recoveryEmail);

    if (result.success) {
      showNotification('Link di recupero inviato con successo', 'success');
      setTimeout(() => setActiveTab('login'), 2000);
    } else {
      showNotification(result.error || 'Errore durante l\'invio del link di recupero', 'error');
    }
  };

  // Funzione per gestire la verifica OTP
  const handleVerification = async (e) => {
    e.preventDefault();

    const token = verificationCode.join('');

    if (verificationCode.some(digit => digit === '')) {
      showNotification('Inserisci il codice di verifica completo', 'error');
      return;
    }

    const result = await verifyOTP(verificationEmail, token);

    if (result.success) {
      showNotification('Verifica completata con successo', 'success');
      setTimeout(() => navigate('/'), 1500);
    } else {
      showNotification(result.error || 'Codice di verifica non valido', 'error');
      // Reset verification fields
      setVerificationCode(['', '', '', '', '', '']);
      if (digitRefs.current[0]) {
        digitRefs.current[0].focus();
      }
    }
  };

  // Funzione per gestire l'input di verifica
  const handleVerificationInput = (e, index) => {
    const value = e.target.value;

    if (!/^[0-9]$/.test(value) && value !== '') {
      return;
    }

    const newVerificationCode = [...verificationCode];
    newVerificationCode[index] = value;
    setVerificationCode(newVerificationCode);

    // Auto focus to next input
    if (value !== '' && index < 5) {
      digitRefs.current[index + 1].focus();
    }

    // Auto submit when all filled
    if (value !== '' && index === 5 && !newVerificationCode.includes('')) {
      setTimeout(() => {
        const form = document.getElementById('verification-form');
        if (form) form.requestSubmit();
      }, 100);
    }
  };

  // Funzione per gestire il backspace nella verifica
  const handleVerificationKeyDown = (e, index) => {
    if (e.key === 'Backspace') {
      if (verificationCode[index] === '' && index > 0) {
        digitRefs.current[index - 1].focus();
        const newVerificationCode = [...verificationCode];
        newVerificationCode[index - 1] = '';
        setVerificationCode(newVerificationCode);
      }
    }
  };

  // Funzione per gestire paste nella verifica
  const handleVerificationPaste = (e) => {
    e.preventDefault();
    const pastedData = e.clipboardData.getData('text').trim();

    if (/^\d+$/.test(pastedData) && pastedData.length <= 6) {
      const newVerificationCode = [...verificationCode];

      for (let i = 0; i < pastedData.length && i < 6; i++) {
        newVerificationCode[i] = pastedData[i];
      }

      setVerificationCode(newVerificationCode);

      // Focus appropriate field
      if (pastedData.length < 6) {
        digitRefs.current[pastedData.length].focus();
      } else {
        // Auto submit if complete
        setTimeout(() => {
          const form = document.getElementById('verification-form');
          if (form) form.requestSubmit();
        }, 100);
      }
    }
  };

  // Funzione per gestire il resend countdown
  const startResendCountdown = () => {
    setResendCountdown(60);

    const interval = setInterval(() => {
      setResendCountdown(prev => {
        if (prev <= 1) {
          clearInterval(interval);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  // Funzione per resend
  const handleResendCode = async () => {
    if (resendCountdown > 0) return;

    showNotification('Invio nuovo codice in corso...', 'success');

    // Reimplementare con la funzione reale di Supabase
    const result = await signup(verificationEmail, 'temp-password-replaced-later');

    if (result.success) {
      showNotification('Nuovo codice inviato con successo', 'success');
      setVerificationCode(['', '', '', '', '', '']);
      startResendCountdown();
      if (digitRefs.current[0]) digitRefs.current[0].focus();
    } else {
      showNotification(result.error || 'Errore durante l\'invio del nuovo codice', 'error');
    }
  };

  return (
    <div className={`auth-page ${isDarkTheme ? 'dark-theme' : ''}`}>
      {/* Decorative elements */}
      <div className="decoration decoration-1"></div>
      <div className="decoration decoration-2"></div>
      <div className="legal-pattern"></div>

      {/* Logo */}
      <div className="logo-container">
        <div className="logo">
          <span className="logo-embellishment top-right"></span>
          <div className="logo-main">VALIS</div>
          <span className="logo-embellishment bottom-left"></span>
        </div>
        <div className="logo-tagline">by Lexygraph AI</div>
      </div>

      {/* Theme toggle button */}
      <button 
        className="theme-toggle" 
        aria-label="Toggle dark/light mode"
        onClick={toggleTheme}
      >
        <i className={`fas ${isDarkTheme ? 'fa-sun' : 'fa-moon'}`}></i>
      </button>

      {/* Main Authentication Container */}
      <div className="auth-container">
        <div className="tabs">
          <div 
            className={`tab-btn ${activeTab === 'login' ? 'active' : ''}`} 
            onClick={() => handleTabChange('login')}
          >
            Accesso
          </div>
          <div 
            className={`tab-btn ${activeTab === 'register' ? 'active' : ''}`} 
            onClick={() => handleTabChange('register')}
          >
            Registrazione
          </div>
          <div 
            className="tab-indicator" 
            style={{ transform: `translateX(${activeTab === 'login' ? '0%' : '100%'})` }}
          ></div>
        </div>

        {notification.show && (
          <div className={`notification ${notification.type}`}>
            <i className={`fas ${notification.type === 'success' ? 'fa-check-circle' : 'fa-exclamation-circle'}`}></i>
            <span>{notification.message}</span>
          </div>
        )}

        {/* Login Form */}
        <div className={`tab-content ${activeTab === 'login' ? 'active' : ''}`} id="login-content">
          <form id="login-form" onSubmit={handleLogin}>
            <div className="form-group">
              <div className="input-container">
                <input 
                  type="email" 
                  className="form-control" 
                  id="login-email" 
                  placeholder=" " 
                  required
                  value={loginFormData.email}
                  onChange={e => setLoginFormData({...loginFormData, email: e.target.value})}
                />
                <label className="form-label" htmlFor="login-email">Indirizzo Email</label>
              </div>
            </div>

            <div className="form-group">
              <div className="input-container">
                <input 
                  type={passwordVisibility.loginPassword ? 'text' : 'password'} 
                  className="form-control" 
                  id="login-password" 
                  placeholder=" " 
                  required
                  value={loginFormData.password}
                  onChange={e => setLoginFormData({...loginFormData, password: e.target.value})}
                />
                <label className="form-label" htmlFor="login-password">Password</label>
                <i 
                  className={`fas ${passwordVisibility.loginPassword ? 'fa-eye-slash' : 'fa-eye'} password-toggle`}
                  onClick={() => togglePasswordVisibility('loginPassword')}
                ></i>
              </div>
            </div>

            <div className="remember-forgot">
              <label className="remember-me">
                <input 
                  type="checkbox" 
                  className="remember-checkbox" 
                  id="remember"
                  checked={loginFormData.remember}
                  onChange={e => setLoginFormData({...loginFormData, remember: e.target.checked})}
                />
                <div className="custom-checkbox">
                  <i className="fas fa-check"></i>
                </div>
                <span className="remember-text">Ricordami</span>
              </label>

              <a 
                href="#" 
                className="forgot-link" 
                onClick={e => {
                  e.preventDefault();
                  handleTabChange('recovery');
                }}
              >
                Password dimenticata?
              </a>
            </div>

            <button type="submit" className="btn btn-primary">
              Accedi
            </button>

            <button 
              type="button" 
              className="btn btn-social" 
              onClick={signInWithGoogle}
            >
              <i className="fab fa-google"></i>
              Accedi con Google
            </button>
          </form>
        </div>

        {/* Register Form */}
        <div className={`tab-content ${activeTab === 'register' ? 'active' : ''}`} id="register-content">
          <form id="register-form" onSubmit={handleRegister}>
            <div className="form-group">
              <div className="input-container">
                <input 
                  type="email" 
                  className="form-control" 
                  id="register-email" 
                  placeholder=" " 
                  required
                  value={registerFormData.email}
                  onChange={e => setRegisterFormData({...registerFormData, email: e.target.value})}
                />
                <label className="form-label" htmlFor="register-email">Indirizzo Email</label>
              </div>
            </div>

            <div className="form-group">
              <div className="input-container">
                <input 
                  type={passwordVisibility.registerPassword ? 'text' : 'password'} 
                  className="form-control" 
                  id="register-password" 
                  placeholder=" " 
                  required
                  value={registerFormData.password}
                  onChange={e => {
                    setRegisterFormData({...registerFormData, password: e.target.value});
                    calculatePasswordStrength(e.target.value);
                  }}
                />
                <label className="form-label" htmlFor="register-password">Password</label>
                <i 
                  className={`fas ${passwordVisibility.registerPassword ? 'fa-eye-slash' : 'fa-eye'} password-toggle`}
                  onClick={() => togglePasswordVisibility('registerPassword')}
                ></i>
              </div>
              <div className="password-strength" style={{ display: registerFormData.password ? 'block' : 'none' }}>
                <div 
                  className="password-strength-meter" 
                  style={{ 
                    width: `${passwordStrength * 25}%`,
                    backgroundColor: passwordStrength <= 1 ? '#e53e3e' : passwordStrength === 2 ? '#ed8936' : '#38a169'
                  }}
                ></div>
              </div>
              <div className="form-help">Usa almeno 8 caratteri con lettere, numeri e simboli</div>
            </div>

            <div className="form-group">
              <div className="input-container">
                <input 
                  type={passwordVisibility.confirmPassword ? 'text' : 'password'} 
                  className="form-control" 
                  id="confirm-password" 
                  placeholder=" " 
                  required
                  value={registerFormData.confirmPassword}
                  onChange={e => setRegisterFormData({...registerFormData, confirmPassword: e.target.value})}
                />
                <label className="form-label" htmlFor="confirm-password">Conferma Password</label>
                <i 
                  className={`fas ${passwordVisibility.confirmPassword ? 'fa-eye-slash' : 'fa-eye'} password-toggle`}
                  onClick={() => togglePasswordVisibility('confirmPassword')}
                ></i>
              </div>
            </div>

            <label className="terms-checkbox">
              <input 
                type="checkbox" 
                id="terms-accept" 
                required
                checked={registerFormData.termsAccepted}
                onChange={e => setRegisterFormData({...registerFormData, termsAccepted: e.target.checked})}
              />
              <div className="terms-custom-checkbox">
                <i className="fas fa-check"></i>
              </div>
              <span className="terms-text">
                Accetto i <a href="#" target="_blank">Termini di Servizio</a> e la <a href="#" target="_blank">Privacy Policy</a> 
              </span>
            </label>

            <button type="submit" className="btn btn-primary">
              Registrati
            </button>

            <button 
              type="button" 
              className="btn btn-social" 
              onClick={signInWithGoogle}
            >
              <i className="fab fa-google"></i>
              Registrati con Google
            </button>
          </form>
        </div>

        {/* Password Recovery Form */}
        <div className={`tab-content ${activeTab === 'recovery' ? 'active' : ''}`} id="recovery-content">
          <div className="verification-message">
            Inserisci la tua email per ricevere un link di recupero password
          </div>
          <form id="recovery-form" onSubmit={handlePasswordRecovery}>
            <div className="form-group">
              <div className="input-container">
                <input 
                  type="email" 
                  className="form-control" 
                  id="recovery-email" 
                  placeholder=" " 
                  required
                  value={recoveryEmail}
                  onChange={e => setRecoveryEmail(e.target.value)}
                />
                <label className="form-label" htmlFor="recovery-email">Indirizzo Email</label>
              </div>
            </div>

            <button type="submit" className="btn btn-primary">
              Invia Link di Recupero
            </button>

            <div className="divider">
              <span className="divider-text">oppure</span>
            </div>

            <button 
              type="button" 
              className="btn btn-social" 
              onClick={() => handleTabChange('login')}
            >
              <i className="fas fa-arrow-left"></i>
              Torna al Login
            </button>
          </form>
        </div>

        {/* Email Verification Form */}
        <div className={`tab-content ${activeTab === 'verification' ? 'active' : ''}`} id="verification-content">
          <div className="verification-message">
            Abbiamo inviato un codice di verifica all'indirizzo<br/>
            <span className="verification-email">{verificationEmail}</span>
          </div>
          <form id="verification-form" onSubmit={handleVerification}>
            <div className="verification-code-container">
              {verificationCode.map((digit, index) => (
                <input 
                  key={index}
                  type="text" 
                  className="verification-digit" 
                  maxLength="1" 
                  pattern="[0-9]" 
                  inputMode="numeric" 
                  required
                  value={digit}
                  onChange={e => handleVerificationInput(e, index)}
                  onKeyDown={e => handleVerificationKeyDown(e, index)}
                  onPaste={index === 0 ? handleVerificationPaste : undefined}
                  ref={el => digitRefs.current[index] = el}
                />
              ))}
            </div>

            <button type="submit" className="btn btn-primary">
              Verifica
            </button>

            <div className="resend-code">
              <button 
                type="button" 
                className="resend-button" 
                disabled={resendCountdown > 0}
                onClick={handleResendCode}
              >
                Invia nuovo codice
              </button>
              {resendCountdown > 0 && (
                <span className="countdown">
                  Riprova tra {resendCountdown} secondi
                </span>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
