// src/App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeContextProvider } from './context/ThemeContext';

// Pages & Components
import Dashboard from './pages/Dashboard';
import Multiagent from './components/multiagent/Multiagent';
import ProfileSection from './components/profile/ProfileSection';
import Login from './pages/Login';

// Auth Guard Component
const PrivateRoute = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Controlla se l'utente è autenticato (usando localStorage per il test)
    const checkAuth = () => {
      const testUser = localStorage.getItem('testUser');
      if (testUser) {
        try {
          const userData = JSON.parse(testUser);
          setIsAuthenticated(userData.isAuthenticated === true);
        } catch (e) {
          setIsAuthenticated(false);
        }
      } else {
        setIsAuthenticated(false);
      }
      setLoading(false);
    };

    checkAuth();
  }, []);

  if (loading) {
    // Puoi mostrare uno spinner o un indicatore di caricamento qui
    return <div className="loading-container">
      <div className="loading-spinner"></div>
    </div>;
  }

  return isAuthenticated ? children : <Navigate to="/login" />;
};

function App() {
  return (
    <ThemeContextProvider>
      <Router>
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route 
            path="/" 
            element={
              <PrivateRoute>
                <Dashboard />
              </PrivateRoute>
            } 
          />
          <Route 
            path="/multiagente" 
            element={
              <PrivateRoute>
                <Multiagent />
              </PrivateRoute>
            } 
          />
          <Route 
            path="/profile" 
            element={
              <PrivateRoute>
                <ProfileSection />
              </PrivateRoute>
            } 
          />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </Router>
    </ThemeContextProvider>
  );
}

export default App;
