// src/components/common/DocumentUploader.js - Versione avanzata
import React, { useState, useCallback } from 'react';
import { 
  Box, 
  Button, 
  Typography, 
  Paper, 
  List, 
  ListItem, 
  ListItemText, 
  ListItemIcon, 
  ListItemSecondaryAction,
  IconButton,
  LinearProgress,
  Snackbar,
  Alert,
  Menu,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
  Tooltip,
  TextField,
  FormControl,
  InputLabel,
  Select,
  useTheme
} from '@mui/material';
import UploadFileIcon from '@mui/icons-material/UploadFile';
import FileUploadIcon from '@mui/icons-material/FileUpload';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';
import DeleteIcon from '@mui/icons-material/Delete';
import PictureAsPdfIcon from '@mui/icons-material/PictureAsPdf';
import ArticleIcon from '@mui/icons-material/Article';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import SearchIcon from '@mui/icons-material/Search';
import PreviewIcon from '@mui/icons-material/Preview';
import AnalyticsIcon from '@mui/icons-material/Analytics';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import WarningIcon from '@mui/icons-material/Warning';
import { useDropzone } from 'react-dropzone';
import styled from 'styled-components';

const UploadArea = styled(Paper)`
  border: 2px dashed ${props => props.isDragActive ? props.theme.palette.primary.main : props.theme.palette.divider};
  background-color: ${props => props.isDragActive ? 
    (props.theme.palette.mode === 'dark' ? 'rgba(66, 66, 255, 0.08)' : 'rgba(63, 81, 181, 0.04)') : 
    'transparent'
  };
  padding: 2rem;
  text-align: center;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    border-color: ${props => props.theme.palette.primary.main};
    background-color: ${props => 
      props.theme.palette.mode === 'dark' ? 'rgba(66, 66, 255, 0.04)' : 'rgba(63, 81, 181, 0.02)'
    };
  }
`;

const getFileIcon = (fileType) => {
  if (fileType.includes('pdf')) return <PictureAsPdfIcon color="error" />;
  if (fileType.includes('word') || fileType.includes('document')) return <ArticleIcon color="primary" />;
  return <InsertDriveFileIcon color="action" />;
};

const getFileStatus = (status) => {
  switch(status) {
    case 'success':
      return <CheckCircleIcon color="success" />;
    case 'warning':
      return <WarningIcon color="warning" />;
    default:
      return null;
  }
};

function DocumentUploader() {
  const theme = useTheme();
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [alert, setAlert] = useState({ show: false, message: '', severity: 'success' });
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedFileIndex, setSelectedFileIndex] = useState(null);
  const [openAnalysisDialog, setOpenAnalysisDialog] = useState(false);
  const [analysisType, setAnalysisType] = useState('basic');
  const [tagsInput, setTagsInput] = useState('');
  const [openPreviewDialog, setOpenPreviewDialog] = useState(false);

  const handleDrop = useCallback(acceptedFiles => {
    const newFiles = acceptedFiles.map(file => ({
      file,
      status: null,
      tags: [],
      progress: 0
    }));
    setFiles(prevFiles => [...prevFiles, ...newFiles]);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop: handleDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/msword': ['.doc'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    }
  });

  const handleFileDelete = (index) => {
    setFiles(prevFiles => prevFiles.filter((_, i) => i !== index));
  };

  const handleOpenMenu = (event, index) => {
    setAnchorEl(event.currentTarget);
    setSelectedFileIndex(index);
  };

  const handleCloseMenu = () => {
    setAnchorEl(null);
    setSelectedFileIndex(null);
  };

  const handleOpenAnalysisDialog = () => {
    setOpenAnalysisDialog(true);
    handleCloseMenu();
  };

  const handleCloseAnalysisDialog = () => {
    setOpenAnalysisDialog(false);
  };

  const handleAnalysisTypeChange = (event) => {
    setAnalysisType(event.target.value);
  };

  const handleStartAnalysis = () => {
    if (selectedFileIndex !== null) {
      // Imposta lo stato "warning" per indicare analisi in corso
      setFiles(prevFiles => 
        prevFiles.map((fileObj, index) => 
          index === selectedFileIndex 
            ? { ...fileObj, status: 'warning' } 
            : fileObj
        )
      );

      setOpenAnalysisDialog(false);

      // Simulazione dell'analisi completa dopo 3 secondi
      setTimeout(() => {
        // Cambia lo stato in "success" quando l'analisi è completata
        setFiles(prevFiles => 
          prevFiles.map((fileObj, index) => 
            index === selectedFileIndex 
              ? { 
                  ...fileObj, 
                  status: 'success',
                  tags: tagsInput.split(',').filter(tag => tag.trim() !== '').map(tag => tag.trim())
                } 
              : fileObj
          )
        );

        setAlert({
          show: true,
          message: 'Analisi completata con successo!',
          severity: 'success'
        });

        setTagsInput('');
      }, 3000);
    }
  };

  const handlePreview = () => {
    setOpenPreviewDialog(true);
    handleCloseMenu();
  };

  const handleClosePreview = () => {
    setOpenPreviewDialog(false);
  };

  const handleTagDelete = (fileIndex, tagIndex) => {
    setFiles(prevFiles => 
      prevFiles.map((fileObj, index) => 
        index === fileIndex 
          ? { 
              ...fileObj, 
              tags: fileObj.tags.filter((_, tIndex) => tIndex !== tagIndex)
            } 
          : fileObj
      )
    );
  };

  const handleUpload = () => {
    if (files.length === 0) {
      setAlert({
        show: true,
        message: 'Nessun file selezionato da caricare',
        severity: 'warning'
      });
      return;
    }

    setUploading(true);
    setProgress(0);

    // Simulazione del caricamento
    const totalSteps = 10;
    const interval = setInterval(() => {
      setProgress(prevProgress => {
        const nextProgress = prevProgress + (100 / totalSteps);

        // Aggiorna il progresso individuale dei file
        setFiles(prevFiles => 
          prevFiles.map(fileObj => ({
            ...fileObj,
            progress: nextProgress
          }))
        );

        if (nextProgress >= 100) {
          clearInterval(interval);
          setUploading(false);
          // Non cancelliamo più i file, ma impostiamo lo stato di successo
          setFiles(prevFiles => 
            prevFiles.map(fileObj => ({
              ...fileObj,
              status: 'success',
              progress: 100
            }))
          );
          setAlert({
            show: true,
            message: 'File caricati con successo!',
            severity: 'success'
          });
          return 100;
        }
        return nextProgress;
      });
    }, 300);
  };

  const handleCloseAlert = () => {
    setAlert(prev => ({ ...prev, show: false }));
  };

  return (
    <Box sx={{ mt: 3 }}>
      <Typography variant="h6" sx={{ mb: 2 }}>Gestione Documenti</Typography>

      <UploadArea
        {...getRootProps()}
        isDragActive={isDragActive}
        elevation={0}
        theme={theme}
      >
        <input {...getInputProps()} />
        <UploadFileIcon sx={{ fontSize: 64, color: 'primary.main', mb: 2 }} />
        <Typography variant="h6" gutterBottom>
          {isDragActive
            ? "Rilascia i file qui..."
            : "Trascina qui i tuoi file o fai click per selezionarli"}
        </Typography>
        <Typography variant="body2" color="textSecondary" gutterBottom>
          Supporta PDF, DOCX, TXT e altri formati di testo
        </Typography>
        <Box sx={{ mt: 2 }}>
          <Chip 
            label="PDF" 
            variant="outlined" 
            color="error" 
            size="small" 
            sx={{ m: 0.5 }} 
          />
          <Chip 
            label="DOCX" 
            variant="outlined" 
            color="primary" 
            size="small" 
            sx={{ m: 0.5 }} 
          />
          <Chip 
            label="TXT" 
            variant="outlined" 
            color="default" 
            size="small" 
            sx={{ m: 0.5 }} 
          />
        </Box>
      </UploadArea>

      {files.length > 0 && (
        <Box sx={{ mt: 4 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="subtitle1" fontWeight="medium">
              File selezionati ({files.length})
            </Typography>
            <TextField
              variant="outlined"
              placeholder="Cerca file..."
              size="small"
              InputProps={{
                startAdornment: <SearchIcon color="action" sx={{ mr: 1 }} />,
              }}
              sx={{ width: 200 }}
            />
          </Box>

          <Paper variant="outlined" sx={{ borderRadius: 2, overflow: 'hidden' }}>
            <List disablePadding>
              {files.map((fileObj, index) => (
                <ListItem 
                  key={index}
                  divider={index !== files.length - 1}
                  sx={{
                    transition: 'background-color 0.2s',
                    '&:hover': {
                      backgroundColor: theme.palette.mode === 'dark' ? 'rgba(255, 255, 255, 0.05)' : 'rgba(0, 0, 0, 0.03)',
                    },
                  }}
                >
                  <ListItemIcon>
                    {getFileIcon(fileObj.file.type)}
                  </ListItemIcon>
                  <ListItemText 
                    primary={fileObj.file.name} 
                    secondary={
                      <>
                        {`${(fileObj.file.size / 1024).toFixed(1)} KB`}
                        {fileObj.progress > 0 && fileObj.progress < 100 && (
                          <LinearProgress 
                            variant="determinate" 
                            value={fileObj.progress} 
                            sx={{ mt: 1, mb: 1, height: 4, borderRadius: 2 }}
                          />
                        )}
                        <Box sx={{ mt: 1, display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                          {fileObj.tags.map((tag, tagIndex) => (
                            <Chip
                              key={tagIndex}
                              label={tag}
                              size="small"
                              onDelete={() => handleTagDelete(index, tagIndex)}
                              sx={{ maxWidth: 120, fontSize: '0.7rem' }}
                            />
                          ))}
                        </Box>
                      </>
                    }
                  />

                  {fileObj.status && (
                    <Tooltip 
                      title={fileObj.status === 'success' ? 'Analizzato con successo' : 'Analisi in corso'} 
                      arrow
                    >
                      <Box sx={{ mr: 2 }}>
                        {getFileStatus(fileObj.status)}
                      </Box>
                    </Tooltip>
                  )}

                  <ListItemSecondaryAction>
                    <IconButton
                      edge="end"
                      aria-label="more"
                      onClick={(e) => handleOpenMenu(e, index)}
                    >
                      <MoreVertIcon />
                    </IconButton>
                    <IconButton 
                      edge="end" 
                      aria-label="delete"
                      onClick={() => handleFileDelete(index)}
                      sx={{ ml: 1 }}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>
          </Paper>

          {uploading ? (
            <Box sx={{ width: '100%', mt: 3 }}>
              <LinearProgress 
                variant="determinate" 
                value={progress} 
                sx={{ height: 8, borderRadius: 4 }}
              />
              <Typography variant="body2" color="textSecondary" align="center" sx={{ mt: 1 }}>
                Caricamento in corso... {Math.round(progress)}%
              </Typography>
            </Box>
          ) : (
            <Button 
              variant="contained" 
              color="primary" 
              startIcon={<FileUploadIcon />}
              onClick={handleUpload}
              sx={{ mt: 3, py: 1, px: 3, borderRadius: 2 }}
              disableElevation
            >
              Carica documenti
            </Button>
          )}
        </Box>
      )}

      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleCloseMenu}
        PaperProps={{
          elevation: 3,
          sx: { borderRadius: 2 }
        }}
      >
        <MenuItem onClick={handlePreview}>
          <ListItemIcon>
            <PreviewIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText primary="Anteprima" />
        </MenuItem>
        <MenuItem onClick={handleOpenAnalysisDialog}>
          <ListItemIcon>
            <AnalyticsIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText primary="Analizza documento" />
        </MenuItem>
      </Menu>

      {/* Dialog per l'analisi del documento */}
      <Dialog 
        open={openAnalysisDialog} 
        onClose={handleCloseAnalysisDialog}
        PaperProps={{
          sx: { borderRadius: 2 }
        }}
      >
        <DialogTitle>Analisi Documento</DialogTitle>
        <DialogContent sx={{ minWidth: 400 }}>
          {selectedFileIndex !== null && files[selectedFileIndex] && (
            <Typography variant="subtitle1" gutterBottom>
              {files[selectedFileIndex].file.name}
            </Typography>
          )}

          <FormControl fullWidth margin="dense">
            <InputLabel id="analysis-type-label">Tipo di Analisi</InputLabel>
            <Select
              labelId="analysis-type-label"
              value={analysisType}
              label="Tipo di Analisi"
              onChange={handleAnalysisTypeChange}
            >
              <MenuItem value="basic">Analisi Base</MenuItem>
              <MenuItem value="advanced">Analisi Avanzata</MenuItem>
              <MenuItem value="comprehensive">Analisi Completa</MenuItem>
            </Select>
          </FormControl>

          <TextField
            margin="dense"
            label="Tag (separati da virgola)"
            fullWidth
            variant="outlined"
            value={tagsInput}
            onChange={(e) => setTagsInput(e.target.value)}
          />

          <Typography variant="body2" color="textSecondary" sx={{ mt: 2 }}>
            {analysisType === 'basic' && 'Analisi delle informazioni di base del documento.'}
            {analysisType === 'advanced' && 'Analisi approfondita con estrazione di clausole principali.'}
            {analysisType === 'comprehensive' && 'Analisi completa con confronto normativo e suggerimenti.'}
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseAnalysisDialog} color="inherit">Annulla</Button>
          <Button onClick={handleStartAnalysis} variant="contained" color="primary">
            Inizia Analisi
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog per l'anteprima del documento */}
      <Dialog 
        open={openPreviewDialog} 
        onClose={handleClosePreview}
        fullWidth
        maxWidth="md"
        PaperProps={{
          sx: { borderRadius: 2 }
        }}
      >
        <DialogTitle>
          Anteprima Documento
          <IconButton
            aria-label="close"
            onClick={handleClosePreview}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
            <DeleteIcon />
          </IconButton>
        </DialogTitle>
        <DialogContent dividers>
          {selectedFileIndex !== null && files[selectedFileIndex] && (
            <Box sx={{ textAlign: 'center', p: 2 }}>
              {files[selectedFileIndex].file.type.includes('pdf') ? (
                <Box sx={{ height: '70vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                  <Typography>
                    L'anteprima del PDF sarà disponibile qui
                  </Typography>
                </Box>
              ) : (
                <Box sx={{ textAlign: 'left', p: 3, bgcolor: theme.palette.background.paper }}>
                  <Typography variant="body1">
                    Contenuto del documento: {files[selectedFileIndex].file.name}
                  </Typography>
                  <Typography variant="body2" color="textSecondary" sx={{ mt: 2 }}>
                    (Qui verrebbe visualizzato il contenuto effettivo del documento)
                  </Typography>
                </Box>
              )}
            </Box>
          )}
        </DialogContent>
      </Dialog>

      <Snackbar 
        open={alert.show} 
        autoHideDuration={6000} 
        onClose={handleCloseAlert}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseAlert} severity={alert.severity} variant="filled" 
          sx={{ borderRadius: 2, boxShadow: '0 4px 12px rgba(0,0,0,0.15)' }}
        >
          {alert.message}
        </Alert>
      </Snackbar>
    </Box>
  );
}

export default DocumentUploader;
