// valis/src/pages/Dashboard.js
import React, { useState, useEffect, useContext } from 'react';
import { 
  Box, 
  Typography, 
  useTheme, 
  Paper,
  Button,
  SpeedDial,
  SpeedDialAction,
  SpeedDialIcon,
  Fade,
  useMediaQuery,
  alpha,
  IconButton  // Aggiunta questa importazione mancante
} from '@mui/material';
import { styled } from '@mui/system';
import Sidebar from '../components/common/Sidebar';
import MenuIcon from '@mui/icons-material/Menu';
import GroupWorkIcon from '@mui/icons-material/GroupWork';
import AutoAwesomeIcon from '@mui/icons-material/AutoAwesome';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import CloseIcon from '@mui/icons-material/Close';
import { ThemeContext } from '../context/ThemeContext';

// Animated Container for Dashboard
const DashboardContainer = styled(Box)(({ theme, open, isMobile }) => ({
  flexGrow: 1,
  paddingTop: theme.spacing(8),
  paddingBottom: theme.spacing(3),
  paddingLeft: theme.spacing(4),
  paddingRight: theme.spacing(4),
  transition: theme.transitions.create(['margin', 'padding'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  marginLeft: 0,
  ...(open && {
    transition: theme.transitions.create(['margin', 'padding'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: isMobile ? 0 : 280,
  }),
  [theme.breakpoints.down('sm')]: {
    paddingLeft: theme.spacing(2),
    paddingRight: theme.spacing(2),
  }
}));

// Styled button for sidebar toggle
const SidebarToggle = styled(IconButton)(({ theme }) => ({
  position: 'fixed',
  top: 20,
  left: 20,
  zIndex: 1200,
  backgroundColor: theme.palette.background.paper,
  boxShadow: theme.palette.mode === 'dark'
    ? '0 2px 10px rgba(0, 0, 0, 0.5)'
    : '0 2px 10px rgba(103, 58, 183, 0.2)',
  '&:hover': {
    backgroundColor: theme.palette.mode === 'dark'
      ? alpha(theme.palette.primary.main, 0.2)
      : alpha(theme.palette.primary.main, 0.1),
    transform: 'scale(1.05)',
  },
  transition: 'all 0.2s ease'
}));

// Feature Button
const FeatureButton = styled(Button)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  width: '100%',
  height: 180,
  padding: theme.spacing(3),
  borderRadius: 16,
  backgroundColor: theme.palette.mode === 'dark' 
    ? alpha(theme.palette.primary.dark, 0.2) 
    : alpha(theme.palette.primary.light, 0.1),
  border: `1px solid ${theme.palette.mode === 'dark' 
    ? alpha(theme.palette.primary.main, 0.3) 
    : alpha(theme.palette.primary.main, 0.2)}`,
  transition: 'all 0.3s ease',
  '&:hover': {
    transform: 'translateY(-10px)',
    backgroundColor: theme.palette.mode === 'dark' 
      ? alpha(theme.palette.primary.dark, 0.3) 
      : alpha(theme.palette.primary.light, 0.2),
    boxShadow: theme.palette.mode === 'dark'
      ? `0 15px 30px ${alpha(theme.palette.primary.dark, 0.3)}`
      : `0 15px 30px ${alpha(theme.palette.primary.main, 0.15)}`,
  },
  '& .MuiSvgIcon-root': {
    fontSize: 60,
    marginBottom: theme.spacing(1),
    color: theme.palette.primary.main,
  },
  '& .MuiTypography-root': {
    fontWeight: 600,
    marginTop: theme.spacing(1),
  }
}));

// Styled floating action buttons
const StyledSpeedDial = styled(SpeedDial)(({ theme }) => ({
  position: 'fixed',
  bottom: 20,
  right: 20,
  '& .MuiFab-primary': {
    boxShadow: theme.palette.mode === 'dark'
      ? '0 4px 20px rgba(156, 100, 255, 0.4)'
      : '0 4px 20px rgba(103, 58, 183, 0.25)',
    backgroundColor: theme.palette.primary.main,
    '&:hover': {
      backgroundColor: theme.palette.primary.dark,
    }
  }
}));

// Welcome section with time-based greeting
const WelcomeSection = () => {
  const theme = useTheme();
  const [greeting, setGreeting] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const updateGreeting = () => {
      const now = new Date();
      const hour = now.getHours();

      // Set appropriate greeting based on time
      if (hour >= 5 && hour < 12) {
        setGreeting('Buongiorno');
      } else if (hour >= 12 && hour < 18) {
        setGreeting('Buon pomeriggio');
      } else {
        setGreeting('Buonasera');
      }
    };

    updateGreeting();

    // Show the greeting with a small delay for animation effect
    const timer = setTimeout(() => {
      setLoading(false);
    }, 500);

    return () => {
      clearTimeout(timer);
    };
  }, []);

  return (
    <Paper 
      elevation={0} 
      sx={{ 
        p: 3, 
        mb: 4, 
        borderRadius: 3,
        background: theme.palette.mode === 'dark' 
          ? `linear-gradient(135deg, ${alpha(theme.palette.primary.dark, 0.4)} 0%, ${alpha(theme.palette.background.paper, 0.8)} 100%)`
          : `linear-gradient(135deg, ${alpha(theme.palette.primary.main, 0.1)} 0%, ${alpha(theme.palette.background.paper, 0.8)} 100%)`,
        backdropFilter: 'blur(10px)',
        border: `1px solid ${alpha(theme.palette.primary.main, theme.palette.mode === 'dark' ? 0.2 : 0.1)}`,
        position: 'relative',
        overflow: 'hidden'
      }}
    >
      {/* Content */}
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', position: 'relative', zIndex: 1 }}>
        <Fade in={!loading} timeout={800}>
          <Box>
            <Typography 
              variant="h3" 
              sx={{ 
                fontWeight: 700,
                mb: 0.5,
                background: theme.palette.mode === 'dark'
                  ? 'linear-gradient(90deg, #9c64ff 0%, #b794ff 100%)'
                  : 'linear-gradient(90deg, #673ab7 0%, #9575cd 100%)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                color: 'transparent',
              }}
            >
              {greeting}
            </Typography>
            <Typography variant="h6" color="textSecondary">
              Seleziona una funzionalità per iniziare
            </Typography>
          </Box>
        </Fade>
      </Box>
    </Paper>
  );
};

function Dashboard() {
  const theme = useTheme();
  const { toggleColorMode } = useContext(ThemeContext);
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <Box sx={{ display: 'flex', minHeight: '100vh', background: theme.palette.background.default }}>
      {/* Sidebar */}
      <Sidebar 
        open={sidebarOpen} 
        onClose={() => setSidebarOpen(false)}
      />

      {/* Sidebar toggle button */}
      {!sidebarOpen && (
        <SidebarToggle
          color="inherit"
          aria-label="open drawer"
          onClick={handleSidebarToggle}
          edge="start"
        >
          <MenuIcon />
        </SidebarToggle>
      )}

      {/* Main content */}
      <DashboardContainer open={sidebarOpen} isMobile={isMobile}>
        <Box component="main" sx={{ flexGrow: 1 }}>
          {/* Welcome Section with Time-based Greeting */}
          <WelcomeSection />

          {/* Main Button Navigation */}
          <Box sx={{ 
            display: 'grid', 
            gridTemplateColumns: {
              xs: '1fr',
              sm: '1fr 1fr',
              md: '1fr 1fr 1fr'
            },
            gap: 4,
            mt: 6
          }}>
            {/* Multiagente Button */}
            <FeatureButton>
              <GroupWorkIcon />
              <Typography variant="h6">Multiagente</Typography>
            </FeatureButton>

            {/* ALI Button */}
            <FeatureButton>
              <AutoAwesomeIcon />
              <Typography variant="h6">ALI</Typography>
            </FeatureButton>

            {/* Agenti Button */}
            <FeatureButton>
              <SmartToyIcon />
              <Typography variant="h6">Agenti</Typography>
            </FeatureButton>
          </Box>
        </Box>
      </DashboardContainer>

      {/* Floating Action Button for profile and theme */}
      <StyledSpeedDial
        ariaLabel="SpeedDial menu"
        icon={<SpeedDialIcon icon={<AccountCircleIcon />} openIcon={<CloseIcon />} />}
        direction="up"
        FabProps={{
          sx: {
            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
            '&:hover': {
              transform: 'scale(1.05)'
            },
            transition: 'all 0.2s ease'
          }
        }}
      >
        <SpeedDialAction
          icon={theme.palette.mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
          tooltipTitle={theme.palette.mode === 'dark' ? 'Modalità chiara' : 'Modalità scura'}
          onClick={toggleColorMode}
          FabProps={{
            sx: {
              bgcolor: theme.palette.background.paper,
              '&:hover': {
                bgcolor: theme.palette.mode === 'dark' 
                  ? alpha(theme.palette.primary.main, 0.2)
                  : alpha(theme.palette.primary.main, 0.1),
              }
            }
          }}
        />
      </StyledSpeedDial>
    </Box>
  );
}

export default Dashboard;
