// valis/src/pages/Dashboard.js
import React, { useState, useEffect, useContext, useRef } from 'react';
import { 
  Box, 
  Typography, 
  useTheme, 
  Paper,
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton
} from '@mui/material';
import { styled } from '@mui/system';
import Sidebar from '../components/common/Sidebar';
import MenuIcon from '@mui/icons-material/Menu';
import GroupWorkIcon from '@mui/icons-material/GroupWork';
import AutoAwesomeIcon from '@mui/icons-material/AutoAwesome';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import { ThemeContext } from '../context/ThemeContext';

// Animated Container for Dashboard
const DashboardContainer = styled(Box)(({ theme, open, isMobile }) => ({
  flexGrow: 1,
  paddingTop: theme.spacing(8),
  paddingBottom: theme.spacing(3),
  paddingLeft: theme.spacing(4),
  paddingRight: theme.spacing(4),
  transition: theme.transitions.create(['margin', 'padding'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  marginLeft: 0,
  ...(open && {
    transition: theme.transitions.create(['margin', 'padding'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: isMobile ? 0 : 280,
  }),
  [theme.breakpoints.down('sm')]: {
    paddingLeft: theme.spacing(2),
    paddingRight: theme.spacing(2),
  }
}));

// Styled button for sidebar toggle
const SidebarToggle = styled(IconButton)(({ theme }) => ({
  position: 'fixed',
  top: 20,
  left: 20,
  zIndex: 1200,
  backgroundColor: theme.palette.background.paper,
  boxShadow: theme.palette.mode === 'dark'
    ? '0 2px 10px rgba(0, 0, 0, 0.5)'
    : '0 2px 10px rgba(103, 58, 183, 0.2)',
  '&:hover': {
    backgroundColor: theme.palette.mode === 'dark'
      ? alpha(theme.palette.primary.main, 0.2)
      : alpha(theme.palette.primary.main, 0.1),
    transform: 'scale(1.05)',
  },
  transition: 'all 0.2s ease'
}));

// Feature Button - stile avanzato e interattivo
const FeatureButton = styled(Button)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  width: '100%',
  height: 180,
  padding: theme.spacing(3),
  borderRadius: 24,
  backgroundColor: theme.palette.mode === 'dark' 
    ? alpha(theme.palette.primary.dark, 0.15) 
    : alpha(theme.palette.primary.light, 0.1),
  border: `2px solid ${theme.palette.mode === 'dark' 
    ? alpha(theme.palette.primary.main, 0.2) 
    : alpha(theme.palette.primary.main, 0.15)}`,
  transition: 'all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275)',
  overflow: 'hidden',
  position: 'relative',
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    background: `linear-gradient(135deg, ${alpha(theme.palette.primary.main, 0.2)} 0%, transparent 50%, transparent 100%)`,
    opacity: 0,
    transition: 'opacity 0.4s ease',
  },
  '&:hover': {
    transform: 'translateY(-12px) scale(1.03)',
    boxShadow: theme.palette.mode === 'dark'
      ? `0 20px 40px ${alpha(theme.palette.primary.dark, 0.4)}`
      : `0 20px 40px ${alpha(theme.palette.primary.main, 0.2)}`,
    borderColor: theme.palette.primary.main,
    '&::before': {
      opacity: 1,
    },
    '& .MuiSvgIcon-root': {
      transform: 'scale(1.2)',
      color: theme.palette.primary.main,
    },
    '& .MuiTypography-root': {
      transform: 'scale(1.05)',
    },
  },
  '&:active': {
    transform: 'translateY(-5px) scale(0.98)',
  },
  '& .MuiSvgIcon-root': {
    fontSize: 70,
    marginBottom: theme.spacing(2),
    color: theme.palette.mode === 'dark' ? theme.palette.primary.light : theme.palette.primary.main,
    transition: 'all 0.3s ease',
    filter: `drop-shadow(0 4px 6px ${alpha(theme.palette.primary.main, 0.4)})`,
  },
  '& .MuiTypography-root': {
    fontWeight: 700,
    transition: 'all 0.3s ease',
    background: theme.palette.mode === 'dark'
      ? 'linear-gradient(90deg, #9c64ff 0%, #b794ff 100%)'
      : 'linear-gradient(90deg, #673ab7 0%, #9575cd 100%)',
    backgroundClip: 'text',
    WebkitBackgroundClip: 'text',
    color: 'transparent',
    fontSize: '1.5rem',
  }
}));

// Typing effect component
const TypingEffect = ({ messages, typingSpeed = 100, deleteSpeed = 50, delayAfterType = 2000 }) => {
  const [displayText, setDisplayText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isWaiting, setIsWaiting] = useState(false);

  useEffect(() => {
    const currentMessage = messages[currentIndex];

    if (isWaiting) {
      const waitTimer = setTimeout(() => {
        setIsWaiting(false);
        setIsDeleting(true);
      }, delayAfterType);
      return () => clearTimeout(waitTimer);
    }

    if (isDeleting && displayText === '') {
      setIsDeleting(false);
      setCurrentIndex((currentIndex + 1) % messages.length);
      return;
    }

    if (!isDeleting && displayText === currentMessage) {
      setIsWaiting(true);
      return;
    }

    const timeout = setTimeout(() => {
      if (isDeleting) {
        setDisplayText(prev => prev.substring(0, prev.length - 1));
      } else {
        setDisplayText(currentMessage.substring(0, displayText.length + 1));
      }
    }, isDeleting ? deleteSpeed : typingSpeed);

    return () => clearTimeout(timeout);
  }, [displayText, isDeleting, currentIndex, messages, typingSpeed, deleteSpeed, delayAfterType, isWaiting]);

  return <>{displayText}</>
};

