// Modifica di App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeContextProvider } from './context/ThemeContext';

// Importa gli stili
import './styles/globals.css';
import './styles/login-scope.css';

// Importa i componenti
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Multiagent from './components/multiagent/Multiagent';
import ProfileSection from './components/profile/ProfileSection';
import AuthCallback from './pages/AuthCallback';

// Importare AuthProvider e useAuth dopo i componenti
import { AuthProvider, useAuth } from './context/AuthContext';

// Componente PrivateRoute separato
const PrivateRoute = (props) => {
  const auth = useAuth();

  if (auth && auth.loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (auth && auth.isAuthenticated) ? props.children : <Navigate to="/login" />;
};

function App() {
  return (
    <Router>
      <ThemeContextProvider>
        <AuthProvider>
          <div className="app-root full-width full-height">
            <div className="app-container full-width full-height">
              <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/auth/callback" element={<AuthCallback />} />
                <Route 
                  path="/" 
                  element={
                    <PrivateRoute>
                      <Dashboard />
                    </PrivateRoute>
                  } 
                />
                <Route 
                  path="/multiagente" 
                  element={
                    <PrivateRoute>
                      <Multiagent />
                    </PrivateRoute>
                  } 
                />
                <Route 
                  path="/profile" 
                  element={
                    <PrivateRoute>
                      <ProfileSection />
                    </PrivateRoute>
                  } 
                />
                <Route path="*" element={<Navigate to="/" />} />
              </Routes>
            </div>
          </div>
        </AuthProvider>
      </ThemeContextProvider>
    </Router>
  );
}

export default App;
