import React, { useState, useEffect, useMemo } from 'react';
import { 
  Box, 
  Typography, 
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton,
  Tooltip,
  Fade
} from '@mui/material';
import { styled, createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Sidebar from '../components/common/Sidebar';
import MenuIcon from '@mui/icons-material/Menu';
// More sophisticated icons for legal tech
import BalanceIcon from '@mui/icons-material/Balance';  // Scales of justice
import GavelIcon from '@mui/icons-material/Gavel';      // Legal judgment
import AccountBalanceIcon from '@mui/icons-material/AccountBalance'; // Institution
import PersonIcon from '@mui/icons-material/Person';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';
import SettingsIcon from '@mui/icons-material/Settings';

// Premium theme with legal-tech futuristic aesthetics
const getTheme = (mode) => createTheme({
  palette: {
    mode,
    primary: {
      // Deeper, more authoritative purple for legal tech
      main: mode === 'dark' ? '#9D5CFF' : '#5D3FD3',
      light: mode === 'dark' ? '#C4A1FF' : '#8F7FFF',
      dark: mode === 'dark' ? '#4A2B8C' : '#2F1C6A',
      contrastText: '#FFFFFF',
    },
    secondary: {
      // Sophisticated teal as accent
      main: mode === 'dark' ? '#00D0B0' : '#00A895',
      light: mode === 'dark' ? '#56FFEE' : '#40D7C2',
      dark: mode === 'dark' ? '#00876F' : '#006858',
      contrastText: mode === 'dark' ? '#000000' : '#000000',
    },
    tertiary: {
      // Gold for legal prestige
      main: mode === 'dark' ? '#FFD700' : '#BF9B30',
      light: mode === 'dark' ? '#FFEB80' : '#E6C55A',
      dark: mode === 'dark' ? '#C7A600' : '#8A6F00',
    },
    background: {
      default: mode === 'dark' ? '#111827' : '#F8FAFD',
      paper: mode === 'dark' ? '#1F2937' : '#FFFFFF',
      subtle: mode === 'dark' ? '#283548' : '#F1F5F9',
    },
    text: {
      primary: mode === 'dark' ? '#F3F4F6' : '#1E293B',
      secondary: mode === 'dark' ? '#B0B8C4' : '#64748B',
      disabled: mode === 'dark' ? '#4B5563' : '#CBD5E1',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontWeight: 800,
      letterSpacing: '-0.025em',
      lineHeight: 1.1,
    },
    h2: {
      fontWeight: 800,
      letterSpacing: '-0.025em',
      lineHeight: 1.2,
    },
    h3: {
      fontWeight: 700,
      letterSpacing: '-0.02em',
      lineHeight: 1.3,
    },
    h4: {
      fontWeight: 700,
      letterSpacing: '-0.015em',
      lineHeight: 1.35,
    },
    h5: {
      fontWeight: 600,
      letterSpacing: '-0.01em',
      lineHeight: 1.4,
    },
    h6: {
      fontWeight: 600,
      letterSpacing: '-0.005em',
      lineHeight: 1.45,
    },
    subtitle1: {
      fontWeight: 500,
      letterSpacing: '0.005em',
      lineHeight: 1.5,
    },
    subtitle2: {
      fontWeight: 500,
      letterSpacing: '0.005em',
      lineHeight: 1.5,
    },
    body1: {
      letterSpacing: '0.01em',
      lineHeight: 1.6,
    },
    body2: {
      letterSpacing: '0.01em',
      lineHeight: 1.6,
    },
    button: {
      fontWeight: 600,
      letterSpacing: '0.02em',
      textTransform: 'none',
    },
  },
  shape: {
    borderRadius: 12,
  },
  shadows: [
    'none',
    `0px 2px 4px ${alpha('#000000', mode === 'dark' ? 0.2 : 0.05)}, 0px 1px 2px ${alpha('#000000', mode === 'dark' ? 0.15 : 0.05)}`,
    `0px 4px 8px ${alpha('#000000', mode === 'dark' ? 0.25 : 0.07)}, 0px 2px 4px ${alpha('#000000', mode === 'dark' ? 0.2 : 0.05)}`,
    `0px 8px 16px ${alpha('#000000', mode === 'dark' ? 0.3 : 0.1)}, 0px 4px 8px ${alpha('#000000', mode === 'dark' ? 0.25 : 0.07)}`,
    `0px 12px 24px ${alpha('#000000', mode === 'dark' ? 0.35 : 0.12)}, 0px 6px 12px ${alpha('#000000', mode === 'dark' ? 0.3 : 0.08)}`,
    // ...defaults for the rest
  ],
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          padding: '10px 20px',
          boxShadow: 'none',
          fontWeight: 600,
          "&:hover": {
            boxShadow: mode === 'dark' 
              ? `0px 8px 16px ${alpha('#000000', 0.4)}`
              : `0px 8px 16px ${alpha('#000000', 0.1)}`,
          }
        },
        contained: {
          boxShadow: mode === 'dark' 
            ? `0px 4px 8px ${alpha('#000000', 0.25)}`
            : `0px 4px 8px ${alpha('#000000', 0.08)}`,
        }
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 12,
          backgroundImage: 'none',
          ...(mode === 'dark' && {
            boxShadow: `0px 4px 20px ${alpha('#000000', 0.35)}`,
            backgroundImage: 'none',
          }),
          ...(mode !== 'dark' && {
            boxShadow: `0px 2px 12px ${alpha('#000000', 0.08)}`,
          }),
        },
      },
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          scrollbarWidth: 'thin',
          '&::-webkit-scrollbar': {
            width: '6px',
            height: '6px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: mode === 'dark' ? '#4B5563' : '#CBD5E1',
            borderRadius: '3px',
          },
          '&::-webkit-scrollbar-track': {
            backgroundColor: mode === 'dark' ? '#1F2937' : '#F1F5F9',
          },
          // Legal-like subtle background texture
          backgroundImage: mode === 'dark' 
            ? 'linear-gradient(rgba(26, 32, 47, 0.9), rgba(26, 32, 47, 0.9)), url("data:image/svg+xml,%3Csvg width=\'100\' height=\'100\' viewBox=\'0 0 100 100\' xmlns=\'http://www.w3.org/2000/svg\'%3E%3Cpath d=\'M11 18c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm48 25c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm-43-7c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm63 31c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM34 90c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm56-76c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM12 86c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm28-65c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm23-11c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-6 60c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm29 22c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zM32 63c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm57-13c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-9-21c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM60 91c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM35 41c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM12 60c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2z\' fill=\'%23374151\' fill-opacity=\'0.05\' fill-rule=\'evenodd\'/%3E%3C/svg%3E")'
            : 'linear-gradient(rgba(255, 255, 255, 0.95), rgba(255, 255, 255, 0.95)), url("data:image/svg+xml,%3Csvg width=\'100\' height=\'100\' viewBox=\'0 0 100 100\' xmlns=\'http://www.w3.org/2000/svg\'%3E%3Cpath d=\'M11 18c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm48 25c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm-43-7c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm63 31c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM34 90c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm56-76c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM12 86c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm28-65c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm23-11c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-6 60c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm29 22c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zM32 63c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm57-13c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-9-21c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM60 91c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM35 41c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM12 60c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2z\' fill=\'%235D3FD3\' fill-opacity=\'0.03\' fill-rule=\'evenodd\'/%3E%3C/svg%3E")',
        },
      },
    },
    MuiTooltip: {
      styleOverrides: {
        tooltip: {
          background: mode === 'dark' 
            ? alpha('#000000', 0.85)
            : alpha('#000000', 0.75),
          backdropFilter: 'blur(4px)',
          padding: '8px 12px',
          fontSize: '0.75rem',
          borderRadius: 6,
        },
      },
    },
  },
});

// Enhanced Dashboard Container with elegant transitions
const DashboardContainer = styled(Box)(({ theme, open, isMobile }) => ({
  flexGrow: 1,
  paddingTop: theme.spacing(5),
  paddingBottom: theme.spacing(4),
  paddingLeft: theme.spacing(open ? 4 : 8),
  paddingRight: theme.spacing(open ? 4 : 8),
  transition: theme.transitions.create(['margin', 'padding'], {
    easing: theme.transitions.easing.easeInOut,
    duration: theme.transitions.duration.standard,
  }),
  marginLeft: 0,
  maxWidth: '100%',
  margin: '0 auto',
  ...(open && {
    transition: theme.transitions.create(['margin', 'padding'], {
      easing: theme.transitions.easing.easeInOut,
      duration: theme.transitions.duration.standard,
    }),
    marginLeft: isMobile ? 0 : 280,
