// src/components/common/Sidebar.js
import React, { useState } from 'react';
import {
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Divider,
  IconButton,
  Box,
  useTheme,
  useMediaQuery
} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import GroupsIcon from '@mui/icons-material/Groups';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PsychologyIcon from '@mui/icons-material/Psychology';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import styled from 'styled-components';

const DrawerHeader = styled(Box)`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  padding: ${props => props.theme.spacing(0, 1)};
  min-height: 64px;
`;

const sections = [
  { name: 'Profilo', icon: <PersonIcon />, id: 'profile' },
  { name: 'Multiagente', icon: <GroupsIcon />, id: 'multiagent' },
  { name: 'ALI', icon: <SmartToyIcon />, id: 'ali' },
  { name: 'Agenti', icon: <PsychologyIcon />, id: 'agents' },
];

function Sidebar({ activeSection, onSectionChange }) {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [open, setOpen] = useState(!isMobile);

  const toggleDrawer = () => {
    setOpen(!open);
  };

  const handleSectionClick = (sectionId) => {
    onSectionChange(sectionId);
    if (isMobile) setOpen(false);
  };

  const drawerWidth = 240;

  return (
    <>
      {isMobile && (
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={toggleDrawer}
          edge="start"
          sx={{
            position: 'fixed',
            zIndex: 1300,
            bottom: 16,
            left: 16,
            bgcolor: 'primary.main',
            color: 'white',
            '&:hover': {
              bgcolor: 'primary.dark',
            },
            boxShadow: '0 3px 10px rgba(0,0,0,0.2)',
          }}
        >
          <MenuIcon />
        </IconButton>
      )}

      <Drawer
        variant={isMobile ? 'temporary' : 'persistent'}
        open={open}
        onClose={isMobile ? toggleDrawer : undefined}
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
            background: theme.palette.mode === 'dark' 
              ? 'linear-gradient(to bottom, #1a237e, #121212)' 
              : 'linear-gradient(to bottom, #e8eaf6, #c5cae9)',
            boxShadow: '0 0 20px rgba(0, 0, 0, 0.1)',
          },
        }}
      >
        <DrawerHeader theme={theme}>
          {isMobile && (
            <IconButton onClick={toggleDrawer}>
              <ChevronLeftIcon />
            </IconButton>
          )}
        </DrawerHeader>
        <Divider />
        <List sx={{ pt: 1 }}>
          {sections.map((section) => (
            <ListItem key={section.id} disablePadding>
              <ListItemButton
                selected={activeSection === section.id}
                onClick={() => handleSectionClick(section.id)}
                sx={{
                  borderRadius: '0 24px 24px 0',
                  mx: 1,
                  '&.Mui-selected': {
                    backgroundColor: theme.palette.mode === 'dark' 
                      ? 'rgba(255, 255, 255, 0.1)' 
                      : 'rgba(63, 81, 181, 0.1)',
                    '&:hover': {
                      backgroundColor: theme.palette.mode === 'dark' 
                        ? 'rgba(255, 255, 255, 0.15)' 
                        : 'rgba(63, 81, 181, 0.15)',
                    }
                  }
                }}
              >
                <ListItemIcon
                  sx={{
                    minWidth: 42,
                    color: activeSection === section.id 
                      ? 'primary.main' 
                      : 'text.secondary',
                  }}
                >
                  {section.icon}
                </ListItemIcon>
                <ListItemText 
                  primary={section.name}
                  primaryTypographyProps={{
                    fontWeight: activeSection === section.id ? 600 : 400,
                  }}
                />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>
    </>
  );
}

export default Sidebar;
