// src/pages/Dashboard.js
import React, { useState, useEffect, useContext } from 'react';
import { 
  Box, 
  Typography, 
  Container, 
  Grid, 
  Paper, 
  useTheme,
  IconButton,
  Avatar,
  Zoom,
  Fade,
  ButtonBase,
  Badge,
  alpha
} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import GroupsIcon from '@mui/icons-material/Groups';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PsychologyIcon from '@mui/icons-material/Psychology';
import DescriptionIcon from '@mui/icons-material/Description';
import AssignmentIcon from '@mui/icons-material/Assignment';
import BarChartIcon from '@mui/icons-material/BarChart';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import MenuIcon from '@mui/icons-material/Menu';
import CircularProgress from '@mui/material/CircularProgress';
import { ThemeContext } from '../context/ThemeContext';
import DocumentUploader from '../components/common/DocumentUploader';
import Sidebar from '../components/common/Sidebar';

// Definizione delle sezioni con colori viola coordinati
const sections = [
  { 
    id: 'profile', 
    title: 'Profilo', 
    icon: <PersonIcon sx={{ fontSize: 40 }} />, 
    description: 'Gestisci il tuo profilo e le preferenze personali',
    color: '#673ab7'  // Viola primario
  },
  { 
    id: 'multiagent', 
    title: 'Sistema Multiagente', 
    icon: <GroupsIcon sx={{ fontSize: 40 }} />, 
    description: 'Configura e gestisci il sistema multiagente',
    color: '#7e57c2'  // Viola secondario
  },
  { 
    id: 'ali', 
    title: 'ALI', 
    icon: <SmartToyIcon sx={{ fontSize: 40 }} />, 
    description: 'Intelligenza artificiale legale integrata',
    color: '#9575cd'  // Viola chiaro
  },
  { 
    id: 'agents', 
    title: 'Agenti', 
    icon: <PsychologyIcon sx={{ fontSize: 40 }} />, 
    description: 'Gestisci i singoli agenti del sistema',
    color: '#5e35b1'  // Viola scuro
  },
  { 
    id: 'my-documents', 
    title: 'Documenti', 
    icon: <DescriptionIcon sx={{ fontSize: 40 }} />, 
    description: 'Accedi e gestisci i tuoi documenti',
    color: '#8e24aa'  // Viola tendente al magenta
  },
  { 
    id: 'legal-analysis', 
    title: 'Analisi Legale', 
    icon: <AssignmentIcon sx={{ fontSize: 40 }} />, 
    description: 'Analisi avanzata dei documenti legali',
    color: '#6a1b9a'  // Viola profondo
  },
  { 
    id: 'reports', 
    title: 'Reportistica', 
    icon: <BarChartIcon sx={{ fontSize: 40 }} />, 
    description: 'Visualizza report e statistiche',
    color: '#4527a0'  // Indaco-viola
  }
];

function Dashboard() {
  const theme = useTheme();
  const { toggleColorMode } = useContext(ThemeContext);
  const [activeSection, setActiveSection] = useState('welcome');
  const [loading, setLoading] = useState(true);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [userName, setUserName] = useState('');
  const [currentTime, setCurrentTime] = useState(new Date());

  // Effetto per simulare il caricamento e ottenere il nome utente
  useEffect(() => {
    // Simulazione di caricamento
    const timer = setTimeout(() => {
      setLoading(false);
      // Simuliamo un nome utente recuperato (in un caso reale verrebbe da un API)
      setUserName('Studio Legale Rossi');
    }, 1200);

    // Aggiorna l'ora corrente ogni minuto
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);

    return () => {
      clearTimeout(timer);
      clearInterval(timeInterval);
    };
  }, []);

  // Gestione del saluto in base all'ora del giorno
  const getGreeting = () => {
    const hour = currentTime.getHours();
    if (hour >= 5 && hour < 12) return 'Buongiorno';
    if (hour >= 12 && hour < 18) return 'Buon pomeriggio';
    return 'Buonasera';
  };

  // Gestione del cambio di sezione
  const handleSectionChange = (sectionId) => {
    setActiveSection(sectionId);
  };

  // Toggle della sidebar
  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // Renderizza il contenuto in base alla sezione attiva
  const renderContent = () => {
    // Se siamo nella welcome page
    if (activeSection === 'welcome') {
      return (
        <Box
          sx={{
            height: '100vh',
            display: 'flex',
            flexDirection: 'column',
            position: 'relative',
            backgroundImage: theme.palette.mode === 'dark' 
              ? 'radial-gradient(circle at top right, rgba(90, 60, 170, 0.15), transparent 70%)' 
              : 'radial-gradient(circle at top right, rgba(103, 58, 183, 0.08), transparent 70%)',
            backgroundSize: '100% 100%'
          }}
        >
          {/* Area principale con saluto e sezioni */}
          <Container maxWidth="lg" sx={{ flex: 1, display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
            <Fade in={!loading} timeout={1000}>
              <Box textAlign="center" mb={6}>
                <Typography 
                  variant="h3" 
                  component="h1" 
                  gutterBottom
                  sx={{ 
                    fontWeight: 700,
                    background: theme.palette.mode === 'dark' 
                      ? 'linear-gradient(45deg, #e9e1ff 30%, #b794ff 90%)'
                      : 'linear-gradient(45deg, #4527a0 30%, #673ab7 90%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    color: 'transparent',
                    textShadow: theme.palette.mode === 'dark' 
                      ? '0px 2px 4px rgba(0, 0, 0, 0.5)'
                      : '0px 2px 4px rgba(0, 0, 0, 0.15)',
                    letterSpacing: '-0.5px'
                  }}
                >
                  {getGreeting()}, {userName}
                </Typography>
                <Typography 
                  variant="h5" 
                  color="textSecondary"
                  sx={{ 
                    maxWidth: 600, 
                    mx: 'auto',
                    mb: 3,
                    fontSize: { xs: '1.1rem', md: '1.3rem' }
                  }}
                >
                  Benvenuto in Valis, la piattaforma legale avanzata con intelligenza artificiale multipla
                </Typography>
                <Typography 
                  variant="body1" 
                  color="textSecondary"
                  sx={{ fontStyle: 'italic', opacity: 0.8 }}
                >
                  Seleziona una sezione per iniziare
                </Typography>
              </Box>
            </Fade>

            <Fade in={!loading} timeout={1200}>
              <Grid container spacing={3} justifyContent="center">
                {sections.map((section, index) => (
                  <Grid item xs={12} sm={6} md={4} key={section.id}>
                    <Zoom in={!loading} style={{ transitionDelay: `${index * 100}ms` }}>
                      <ButtonBase
                        sx={{ 
                          width: '100%',
                          textAlign: 'left',
                          borderRadius: 3,
                          overflow: 'hidden',
                          transition: 'all 0.3s ease',
                          '&:hover': {
                            transform: 'translateY(-4px)',
                          }
                        }}
                        onClick={() => handleSectionChange(section.id)}
                      >
                        <Paper
                          elevation={3}
                          sx={{
                            p: 3,
                            width: '100%',
                            height: '100%',
                            display: 'flex',
                            flexDirection: 'column',
                            position: 'relative',
                            overflow: 'hidden',
                            transition: 'all 0.3s ease',
                            border: `1px solid ${alpha(section.color, 0.2)}`,
                            '&:hover': {
                              boxShadow: `0 8px 24px ${alpha(section.color, 0.25)}`,
                              borderColor: alpha(section.color, 0.5),
                              '& .section-icon': {
                                transform: 'scale(1.1)',
                                boxShadow: `0 6px 20px ${alpha(section.color, 0.4)}`,
                              }
                            },
                            '&::before': {
                              content: '""',
                              position: 'absolute',
                              top: 0,
                              left: 0,
                              width: '100%',
                              height: '4px',
                              bgcolor: section.color
                            }
                          }}
                        >
                          <Box 
                            className="section-icon"
                            sx={{ 
                              mb: 2,
                              display: 'flex', 
                              alignItems: 'center', 
                              justifyContent: 'center',
                              width: 65,
                              height: 65,
                              borderRadius: '50%',
                              color: 'white',
                              bgcolor: section.color,
                              boxShadow: `0 4px 12px ${alpha(section.color, 0.5)}`,
                              transition: 'all 0.3s ease',
                            }}
                          >
                            {section.icon}
                          </Box>
                          <Typography variant="h6" gutterBottom fontWeight={600}>
                            {section.title}
                          </Typography>
                          <Typography variant="body2" color="textSecondary">
                            {section.description}
                          </Typography>
                        </Paper>
                      </ButtonBase>
                    </Zoom>
                  </Grid>
                ))}
              </Grid>
            </Fade>
          </Container>

          {/* Pulsanti di azione in basso a destra */}
          <Box
            sx={{
              position: 'fixed',
              bottom: 30,
              right: 30,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: 2,
              zIndex: 1000
            }}
          >
            <IconButton
              color="primary"
              onClick={toggleColorMode}
              sx={{
                bgcolor: theme.palette.background.paper,
                boxShadow: theme.shadows[3],
                width: 45,
                height: 45,
                '&:hover': {
                  bgcolor: theme.palette.mode === 'dark' 
                    ? alpha(theme.palette.primary.main, 0.15)
                    : alpha(theme.palette.primary.main, 0.08),
                  transform: 'translateY(-2px)'
                },
                transition: 'all 0.2s ease'
              }}
            >
              {theme.palette.mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
            </IconButton>

            <IconButton
              color="primary"
              sx={{
                bgcolor: theme.palette.background.paper,
                boxShadow: theme.shadows[3],
                width: 45,
                height: 45,
                '&:hover': {
                  bgcolor: theme.palette.mode === 'dark' 
                    ? alpha(theme.palette.primary.main, 0.15)
                    : alpha(theme.palette.primary.main, 0.08),
                  transform: 'translateY(-2px)'
                },
                transition: 'all 0.2s ease'
              }}
            >
              <Badge color="error" variant="dot">
                <AccountCircleIcon />
              </Badge>
            </IconButton>

            <IconButton
              color="primary"
              onClick={toggleSidebar}
              sx={{
                bgcolor: theme.palette.background.paper,
                boxShadow: theme.shadows[3],
                width: 45,
                height: 45,
                '&:hover': {
                  bgcolor: theme.palette.mode === 'dark' 
                    ? alpha(theme.palette.primary.main, 0.15)
                    : alpha(theme.palette.primary.main, 0.08),
                  transform: 'translateY(-2px)'
                },
                transition: 'all 0.2s ease'
              }}
            >
              <MenuIcon />
            </IconButton>
          </Box>
        </Box>
      );
    } else {
      // Altrimenti, renderizza il contenuto della sezione selezionata
      return (
        <Box sx={{ 
          p: 3,
          backgroundImage: theme.palette.mode === 'dark' 
            ? 'radial-gradient(circle at top right, rgba(90, 60, 170, 0.1), transparent 70%)' 
            : 'radial-gradient(circle at top right, rgba(103, 58, 183, 0.05), transparent 70%)',
          minHeight: '100vh'
        }}>
          <IconButton
            color="primary"
            onClick={() => setActiveSection('welcome')}
            sx={{ 
              mb: 2,
              bgcolor: theme.palette.background.paper,
              boxShadow: theme.shadows[1],
              '&:hover': {
                bgcolor: theme.palette.mode === 'dark' 
                  ? alpha(theme.palette.primary.main, 0.15)
                  : alpha(theme.palette.primary.main, 0.08),
              },
            }}
          >
            &larr; Torna alla Dashboard
          </IconButton>

          {/* Intestazione della sezione */}
          <Box sx={{ mb: 4 }}>
            <Typography 
              variant="h4" 
              gutterBottom
              sx={{
                fontWeight: 600,
                background: theme.palette.mode === 'dark' 
                  ? 'linear-gradient(45deg, #e9e1ff 30%, #b794ff 90%)'
                  : 'linear-gradient(45deg, #4527a0 30%, #673ab7 90%)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                color: 'transparent',
              }}
            >
              {sections.find(s => s.id === activeSection)?.title || 'Sezione'}
            </Typography>
            <Typography variant="subtitle1" color="textSecondary">
              {sections.find(s => s.id === activeSection)?.description || 'Descrizione sezione'}
            </Typography>
          </Box>

          {/* Contenuto della sezione */}
          {activeSection === 'my-documents' && <DocumentUploader />}
          {activeSection === 'profile' && <Typography>Contenuto della sezione Profilo</Typography>}
          {activeSection === 'multiagent' && <Typography>Contenuto della sezione Sistema Multiagente</Typography>}
          {activeSection === 'ali' && <Typography>Contenuto della sezione ALI</Typography>}
          {activeSection === 'agents' && <Typography>Contenuto della sezione Agenti</Typography>}
          {activeSection === 'legal-analysis' && <Typography>Contenuto della sezione Analisi Legale</Typography>}
          {activeSection === 'reports' && <Typography>Contenuto della sezione Reportistica</Typography>}

          {/* Controlli in basso a destra (visibili anche nelle pagine di sezione) */}
          <Box
            sx={{
              position: 'fixed',
              bottom: 30,
              right: 30,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: 2,
              zIndex: 1000
            }}
          >
            <IconButton
              color="primary"
              onClick={toggleColorMode}
              sx={{
                bgcolor: theme.palette.background.paper,
                boxShadow: theme.shadows[3],
                width: 45,
                height: 45,
                '&:hover': {
                  bgcolor: theme.palette.mode === 'dark' 
                    ? alpha(theme.palette.primary.main, 0.15)
                    : alpha(theme.palette.primary.main, 0.08),
                  transform: 'translateY(-2px)'
                },
                transition: 'all 0.2s ease'
              }}
            >
              {theme.palette.mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
            </IconButton>

            <IconButton
              color="primary"
              sx={{
                bgcolor: theme.palette.background.paper,
                boxShadow: theme.shadows[3],
                width: 45,
                height: 45,
                '&:hover': {
                  bgcolor: theme.palette.mode === 'dark' 
                    ? alpha(theme.palette.primary.main, 0.15)
                    : alpha(theme.palette.primary.main, 0.08),
                  transform: 'translateY(-2px)'
                },
                transition: 'all 0.2s ease'
              }}
            >
              <Badge color="error" variant="dot">
                <AccountCircleIcon />
              </Badge>
            </IconButton>

            <IconButton
              color="primary"
              onClick={toggleSidebar}
              sx={{
                bgcolor: theme.palette.background.paper,
                boxShadow: theme.shadows[3],
                width: 45,
                height: 45,
                '&:hover': {
                  bgcolor: theme.palette.mode === 'dark' 
                    ? alpha(theme.palette.primary.main, 0.15)
                    : alpha(theme.palette.primary.main, 0.08),
                  transform: 'translateY(-2px)'
                },
                transition: 'all 0.2s ease'
              }}
            >
              <MenuIcon />
            </IconButton>
          </Box>
        </Box>
      );
    }
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100vh',
          flexDirection: 'column',
          bgcolor: theme.palette.background.default
        }}
      >
        <Box sx={{ position: 'relative', display: 'inline-flex' }}>
          <CircularProgress 
            size={64} 
            thickness={4} 
            sx={{ 
              color: theme.palette.mode === 'dark' ? '#9c64ff' : '#673ab7',
            }} 
          />
          <Box
            sx={{
              top: 0,
              left: 0,
              bottom: 0,
              right: 0,
              position: 'absolute',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <Typography
              variant="h5"
              component="div"
              sx={{ 
                color: theme.palette.primary.main,
                fontWeight: 'bold',
                fontFamily: 'Montserrat, sans-serif'
              }}
            >
              V
            </Typography>
          </Box>
        </Box>
        <Typography 
          variant="h6" 
          sx={{ 
            mt: 3, 
            fontWeight: 600,
            fontFamily: 'Montserrat, sans-serif',
            letterSpacing: '1px'
          }}
        >
          VALIS
        </Typography>
        <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
          Caricamento in corso...
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ display: 'flex', minHeight: '100vh' }}>
      {/* Sidebar collassabile */}
      {sidebarOpen && (
        <Sidebar 
          activeSection={activeSection} 
          onSectionChange={handleSectionChange} 
          onClose={toggleSidebar}
        />
      )}

      {/* Contenuto principale */}
      <Box sx={{ 
        flexGrow: 1, 
        transition: 'margin-left 0.3s ease-out',
        bgcolor: theme.palette.background.default,
      }}>
        {renderContent()}
      </Box>
    </Box>
  );
}

export default Dashboard;
