// valis/src/pages/Dashboard.js
import React, { useState, useEffect, useMemo } from 'react';
import { 
  Box, 
  Typography, 
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton,
  Tooltip,
  Container
} from '@mui/material';
import { styled, createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import PersonIcon from '@mui/icons-material/Person';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';
import LogoutIcon from '@mui/icons-material/Logout';

// Custom theme with a more vibrant violet palette and enhanced styles
const getTheme = (mode) => createTheme({
  palette: {
    mode,
    primary: {
      main: mode === 'dark' ? '#B388FF' : '#7C4DFF',
      light: mode === 'dark' ? '#D7B8FF' : '#B39DDB',
      dark: mode === 'dark' ? '#7B61FF' : '#5E35B1',
      contrastText: '#FFFFFF',
    },
    secondary: {
      main: mode === 'dark' ? '#03DAC5' : '#00BFA5',
      light: mode === 'dark' ? '#5EFFEE' : '#64FFDA',
      dark: mode === 'dark' ? '#00867D' : '#008975',
      contrastText: mode === 'dark' ? '#000000' : '#000000',
    },
    background: {
      default: mode === 'dark' ? '#0B0B1A' : '#F5F7FA',
      paper: mode === 'dark' ? '#14142B' : '#FFFFFF',
    },
    text: {
      primary: mode === 'dark' ? '#F3F4F6' : '#1E293B',
      secondary: mode === 'dark' ? '#B0B8C4' : '#64748B',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontWeight: 800,
      letterSpacing: '-0.025em',
    },
    h2: {
      fontWeight: 800,
      letterSpacing: '-0.025em',
    },
    h3: {
      fontWeight: 700,
      letterSpacing: '-0.02em',
    },
    h4: {
      fontWeight: 700,
      letterSpacing: '-0.015em',
    },
    h5: {
      fontWeight: 600,
      letterSpacing: '-0.01em',
    },
    h6: {
      fontWeight: 600,
      letterSpacing: '-0.005em',
    },
    button: {
      fontWeight: 600,
      letterSpacing: '0.02em',
      textTransform: 'none',
    },
  },
  shape: {
    borderRadius: 16,
  },
  shadows: [
    'none',
    '0px 2px 4px rgba(0, 0, 0, 0.05), 0px 1px 2px rgba(0, 0, 0, 0.05)',
    '0px 4px 8px rgba(0, 0, 0, 0.05), 0px 2px 4px rgba(0, 0, 0, 0.05)',
    '0px 8px 16px rgba(0, 0, 0, 0.05), 0px 4px 8px rgba(0, 0, 0, 0.05)',
    '0px 12px 24px rgba(0, 0, 0, 0.05), 0px 6px 12px rgba(0, 0, 0, 0.05)',
    '0 15px 30px rgba(27, 10, 59, 0.12), 0 8px 16px rgba(27, 10, 59, 0.08)',
  ],
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 12,
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 16,
          backgroundImage: 'none',
        },
      },
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          scrollbarWidth: 'thin',
          '&::-webkit-scrollbar': {
            width: '6px',
            height: '6px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: mode === 'dark' ? '#4B5563' : '#CBD5E1',
            borderRadius: '3px',
          },
          '&::-webkit-scrollbar-track': {
            backgroundColor: mode === 'dark' ? '#1F2937' : '#F1F5F9',
          },
        },
      },
    },
  },
});

// App Logo Component - Styled like authentication page
const Logo = ({ theme }) => (
  <Box sx={{ display: 'flex', alignItems: 'center' }}>
    <svg width="36" height="36" viewBox="0 0 36 36" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M18 3.00001C15.6131 3 13.3239 3.93032 11.636 5.63605C9.94821 7.34189 9.00001 9.65219 9.00001 12.06C9.00001 14.4678 9.94821 16.7781 11.636 18.484C13.3239 20.1897 15.6131 21.12 18 21.12C20.3869 21.12 22.6761 20.1897 24.364 18.484C26.0518 16.7781 27 14.4678 27 12.06C27 9.65219 26.0518 7.34189 24.364 5.63605C22.6761 3.93032 20.3869 3 18 3.00001Z" fill="url(#paint0_linear_logo)"/>
      <path d="M18 24.06C9.72 24.06 3 27.4199 3 31.56C3 35.7001 9.72 39.06 18 39.06C26.28 39.06 33 35.7001 33 31.56C33 27.4199 26.28 24.06 18 24.06Z" fill="url(#paint1_linear_logo)"/>
      <defs>
        <linearGradient id="paint0_linear_logo" x1="18" y1="3" x2="18" y2="21.12" gradientUnits="userSpaceOnUse">
          <stop stopColor={theme.palette.mode === 'dark' ? '#D7B8FF' : '#9C64FF'}/>
          <stop offset="1" stopColor={theme.palette.mode === 'dark' ? '#9C64FF' : '#7C4DFF'}/>
        </linearGradient>
        <linearGradient id="paint1_linear_logo" x1="18" y1="24.06" x2="18" y2="39.06" gradientUnits="userSpaceOnUse">
          <stop stopColor={theme.palette.mode === 'dark' ? '#D7B8FF' : '#9C64FF'}/>
          <stop offset="1" stopColor={theme.palette.mode === 'dark' ? '#9C64FF' : '#7C4DFF'}/>
        </linearGradient>
      </defs>
    </svg>
    <Typography
      variant="h4"
      fontFamily="Montserrat, sans-serif"
      fontWeight={700}
      letterSpacing="1.2px"
      sx={{
        ml: 1.5,
        background: theme.palette.mode === 'dark'
          ? 'linear-gradient(45deg, #D7B8FF 30%, #9C64FF 90%)'
          : 'linear-gradient(45deg, #9C64FF 30%, #7C4DFF 90%)',
        backgroundClip: 'text',
        WebkitBackgroundClip: 'text',
        color: 'transparent',
        textShadow: theme.palette.mode === 'dark'
          ? '0px 2px 4px rgba(0, 0, 0, 0.5)'
          : '0px 2px 4px rgba(0, 0, 0, 0.2)',
      }}
    >
      VALIS
    </Typography>
  </Box>
);

// Feature Button styled as authentication page buttons
const FeatureButton = styled(Button)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  padding: theme.spacing(5),
  backgroundColor: 'transparent',
  transition: 'all 0.5s cubic-bezier(0.05, 0.7, 0.1, 1)',
  position: 'relative',
  '&:hover': {
    backgroundColor: 'transparent',
    transform: 'translateY(-12px)',
    '& .feature-button-icon': {
      transform: 'scale(1.15) translateY(-5px)',
      filter: `drop-shadow(0 10px 15px ${alpha(theme.palette.primary.main, 0.5)})`,
      opacity: 1,
    },
    '& .feature-button-text': {
      transform: 'translateY(5px)',
      color: theme.palette.primary.main,
      fontWeight: 700,
      textShadow: `0 2px 4px ${alpha(theme.palette.primary.main, 0.3)}`,
    }
  },
  '&:active': {
    backgroundColor: 'transparent',
    transform: 'translateY(-5px) scale(0.98)',
  },
  '& .feature-button-icon': {
    fontSize: 80,
    marginBottom: theme.spacing(3),
    color: theme.palette.primary.main,
    transition: 'all 0.5s cubic-bezier(0.05, 0.7, 0.1, 1)',
    opacity: 0.85,
    filter: `drop-shadow(0 5px 10px ${alpha(theme.palette.primary.main, 0.3)})`,
  },
  '& .feature-button-text': {
    fontWeight: 600,
    fontSize: '1.3rem',
    transition: 'all 0.4s ease',
    letterSpacing: '0.01em',
    color: theme.palette.mode === 'dark' 
      ? theme.palette.primary.light 
      : theme.palette.primary.dark,
  }
}));

// Header Action Button
const HeaderActionButton = styled(IconButton)(({ theme }) => ({
  width: 40,
  height: 40,
  backgroundColor: alpha(theme.palette.background.paper, 0.8),
  backdropFilter: 'blur(8px)',
  boxShadow: theme.palette.mode === 'dark'
    ? `0 4px 8px ${alpha(theme.palette.common.black, 0.25)}, 
       inset 0 0 0 1px ${alpha(theme.palette.primary.light, 0.1)}`
    : `0 4px 8px ${alpha(theme.palette.common.black, 0.08)}, 
       inset 0 0 0 1px ${alpha(theme.palette.primary.main, 0.05)}`,
  transition: 'all 0.3s cubic-bezier(0.05, 0.7, 0.1, 1)',
  '&:hover': {
    backgroundColor: theme.palette.mode === 'dark'
      ? alpha(theme.palette.primary.main, 0.15)
      : alpha(theme.palette.primary.main, 0.08),
    transform: 'translateY(-2px)',
    boxShadow: theme.palette.mode === 'dark'
      ? `0 6px 12px ${alpha(theme.palette.common.black, 0.3)}, 
         inset 0 0 0 1px ${alpha(theme.palette.primary.main, 0.2)}`
      : `0 6px 12px ${alpha(theme.palette.primary.main, 0.15)}, 
         inset 0 0 0 1px ${alpha(theme.palette.primary.main, 0.1)}`,
  },
  '&:active': {
    transform: 'translateY(0) scale(0.97)',
  },
  '& .MuiSvgIcon-root': {
    fontSize: 20,
    color: theme.palette.primary.main,
  }
}));

// Enhanced typing effect with longer durations and smoother transitions
const TypingEffect = ({ messages, typingSpeed = 50, deleteSpeed = 25, delayAfterType = 7000 }) => {
  const [displayText, setDisplayText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isWaiting, setIsWaiting] = useState(false);

  useEffect(() => {
    const currentMessage = messages[currentIndex];

    if (isWaiting) {
      const waitTimer = setTimeout(() => {
        setIsWaiting(false);
        setIsDeleting(true);
      }, delayAfterType);
      return () => clearTimeout(waitTimer);
    }

    if (isDeleting && displayText === '') {
      setIsDeleting(false);
      setCurrentIndex((currentIndex + 1) % messages.length);
      return;
    }

    if (!isDeleting && displayText === currentMessage) {
      setIsWaiting(true);
      return;
    }

    const timeout = setTimeout(() => {
      if (isDeleting) {
        setDisplayText(prev => prev.substring(0, prev.length - 1));
      } else {
        setDisplayText(currentMessage.substring(0, displayText.length + 1));
      }
    }, isDeleting ? deleteSpeed : typingSpeed);

    return () => clearTimeout(timeout);
  }, [displayText, isDeleting, currentIndex, messages, typingSpeed, deleteSpeed, delayAfterType, isWaiting]);

  return <span>{displayText}</span>;
};

// Custom Icon Components with new designs
const NetworkGraphIcon = () => (
  <svg width="80" height="80" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M17.5,7A5.5,5.5 0 0,1 23,12.5A5.5,5.5 0 0,1 17.5,18C15.79,18 14.27,17.22 13.26,16H10.74C9.73,17.22 8.21,18 6.5,18A5.5,5.5 0 0,1 1,12.5A5.5,5.5 0 0,1 6.5,7C8.21,7 9.73,7.78 10.74,9H13.26C14.27,7.78 15.79,7 17.5,7M17.5,9A3.5,3.5 0 0,0 14,12.5A3.5,3.5 0 0,0 17.5,16A3.5,3.5 0 0,0 21,12.5A3.5,3.5 0 0,0 17.5,9M6.5,9A3.5,3.5 0 0,0 3,12.5A3.5,3.5 0 0,0 6.5,16A3.5,3.5 0 0,0 10,12.5A3.5,3.5 0 0,0 6.5,9M12,14A1,1 0 0,1 11,13A1,1 0 0,1 12,12A1,1 0 0,1 13,13A1,1 0 0,1 12,14Z" />
  </svg>
);

const MagnifyingGlassIcon = () => (
  <svg width="80" height="80" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M9.5,3A6.5,6.5 0 0,1 16,9.5C16,11.11 15.41,12.59 14.44,13.73L14.71,14H15.5L20.5,19L19,20.5L14,15.5V14.71L13.73,14.44C12.59,15.41 11.11,16 9.5,16A6.5,6.5 0 0,1 3,9.5A6.5,6.5 0 0,1 9.5,3M9.5,5C7,5 5,7 5,9.5C5,12 7,14 9.5,14C12,14 14,12 14,9.5C14,7 12,5 9.5,5Z" />
  </svg>
);

