// src/components/common/Sidebar.js - Versione avanzata
import React, { useState } from 'react';
import {
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Divider,
  IconButton,
  Box,
  useTheme,
  useMediaQuery,
  Collapse,
  Badge,
  Tooltip,
  Typography
} from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import GroupsIcon from '@mui/icons-material/Groups';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PsychologyIcon from '@mui/icons-material/Psychology';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import DashboardIcon from '@mui/icons-material/Dashboard';
import DescriptionIcon from '@mui/icons-material/Description';
import AssignmentIcon from '@mui/icons-material/Assignment';
import BarChartIcon from '@mui/icons-material/BarChart';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import { motion } from 'framer-motion';

const mainSections = [
  { name: 'Profilo', icon: <PersonIcon />, id: 'profile', badge: null },
  { name: 'Multiagente', icon: <GroupsIcon />, id: 'multiagent', badge: 2 },
  { name: 'ALI', icon: <SmartToyIcon />, id: 'ali', badge: null, new: true },
  { name: 'Agenti', icon: <PsychologyIcon />, id: 'agents', badge: null },
];

const documentSections = [
  { name: 'I miei documenti', icon: <DescriptionIcon />, id: 'my-documents' },
  { name: 'Analisi legali', icon: <AssignmentIcon />, id: 'legal-analysis' },
  { name: 'Reportistica', icon: <BarChartIcon />, id: 'reports' },
];

const MotionListItem = motion(ListItem);

function Sidebar({ activeSection, onSectionChange }) {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [open, setOpen] = useState(!isMobile);
  const [openDocuments, setOpenDocuments] = useState(false);

  const toggleDrawer = () => {
    setOpen(!open);
  };

  const handleSectionClick = (sectionId) => {
    onSectionChange(sectionId);
    if (isMobile) setOpen(false);
  };

  const handleDocumentsClick = () => {
    setOpenDocuments(!openDocuments);
  };

  const drawerWidth = 260;

  const itemVariants = {
    hidden: { opacity: 0, x: -20 },
    visible: (i) => ({
      opacity: 1,
      x: 0,
      transition: {
        delay: i * 0.05,
      }
    }),
  };

  return (
    <>
      {isMobile && (
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={toggleDrawer}
          edge="start"
          sx={{
            position: 'fixed',
            zIndex: 1300,
            bottom: 16,
            left: 16,
            bgcolor: 'primary.main',
            color: 'white',
            '&:hover': {
              bgcolor: 'primary.dark',
            },
            boxShadow: '0 3px 10px rgba(0,0,0,0.2)',
          }}
        >
          <MenuIcon />
        </IconButton>
      )}

      <Drawer
        variant={isMobile ? 'temporary' : 'permanent'}
        open={open}
        onClose={isMobile ? toggleDrawer : undefined}
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
            border: 'none',
            boxShadow: '2px 0 10px rgba(0, 0, 0, 0.1)',
          },
        }}
      >
        <Box sx={{ 
          height: 64, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'flex-end',
          px: 2
        }}>
          {isMobile && (
            <IconButton onClick={toggleDrawer}>
              <ChevronLeftIcon />
            </IconButton>
          )}
        </Box>

        <Divider sx={{ mx: 2 }} />

        <Box sx={{ p: 2, pb: 0 }}>
          <Typography 
            variant="overline" 
            color="text.secondary"
            fontWeight="bold"
            sx={{ letterSpacing: 1.2 }}
          >
            Principale
          </Typography>
        </Box>

        <List sx={{ px: 1 }}>
          {mainSections.map((section, index) => (
            <MotionListItem 
              key={section.id} 
              disablePadding
              custom={index}
              variants={itemVariants}
              initial="hidden"
              animate="visible"
            >
              <Tooltip title={section.name} placement="right">
                <ListItemButton
                  selected={activeSection === section.id}
                  onClick={() => handleSectionClick(section.id)}
                  sx={{
                    borderRadius: '8px',
                    mb: 0.5,
                    position: 'relative',
                    '&.Mui-selected': {
                      backgroundColor: theme.palette.mode === 'dark' 
                        ? 'rgba(66, 66, 255, 0.15)' 
                        : 'rgba(63, 81, 181, 0.12)',
                      '&:hover': {
                        backgroundColor: theme.palette.mode === 'dark' 
                          ? 'rgba(66, 66, 255, 0.25)' 
                          : 'rgba(63, 81, 181, 0.18)',
                      }
                    }
                  }}
                >
                  <ListItemIcon
                    sx={{
                      color: activeSection === section.id 
                        ? 'primary.main' 
                        : 'text.secondary',
                        minWidth: 40
                    }}
                  >
                    {section.badge ? (
                      <Badge badgeContent={section.badge} color="error">
                        {section.icon}
                      </Badge>
                    ) : section.icon}
                  </ListItemIcon>
                  <ListItemText 
                    primary={section.name}
                    primaryTypographyProps={{
                      fontWeight: activeSection === section.id ? 600 : 400,
                    }}
                  />

                  {section.new && (
                    <Box 
                      sx={{
                        bgcolor: 'success.main',
                        color: '#fff',
                        borderRadius: '4px',
                        px: 0.8,
                        py: 0.2,
                        fontSize: '0.6875rem',
                        fontWeight: 'bold',
                      }}
                    >
                      NUOVO
                    </Box>
                  )}
                </ListItemButton>
              </Tooltip>
            </MotionListItem>
          ))}
        </List>

        <Box sx={{ p: 2, pb: 0, mt: 2 }}>
          <Typography 
            variant="overline" 
            color="text.secondary"
            fontWeight="bold"
            sx={{ letterSpacing: 1.2 }}
          >
            Documenti
          </Typography>
        </Box>

        <List sx={{ px: 1 }}>
          <ListItemButton onClick={handleDocumentsClick} sx={{ borderRadius: '8px', mb: 0.5 }}>
            <ListItemIcon sx={{ minWidth: 40 }}>
              <DescriptionIcon />
            </ListItemIcon>
            <ListItemText primary="Documenti" />
            {openDocuments ? <ExpandLess /> : <ExpandMore />}
          </ListItemButton>

          <Collapse in={openDocuments} timeout="auto" unmountOnExit>
            <List component="div" disablePadding>
              {documentSections.map((section, index) => (
                <ListItemButton 
                  key={section.id}
                  selected={activeSection === section.id}
                  onClick={() => handleSectionClick(section.id)}
                  sx={{ 
                    pl: 4, 
                    borderRadius: '8px',
                    mb: 0.5,
                    '&.Mui-selected': {
                      backgroundColor: theme.palette.mode === 'dark' 
                        ? 'rgba(66, 66, 255, 0.15)' 
                        : 'rgba(63, 81, 181, 0.12)',
                    }
                  }}
                >
                  <ListItemIcon sx={{ minWidth: 36 }}>
                    {section.icon}
                  </ListItemIcon>
                  <ListItemText 
                    primary={section.name}
                    primaryTypographyProps={{ fontSize: '0.9rem' }}
                  />
                </ListItemButton>
              ))}
            </List>
          </Collapse>
        </List>

        <Box sx={{ flexGrow: 1 }} />

        <Box sx={{ p: 2, textAlign: 'center' }}>
          <Typography variant="caption" color="text.secondary">
            Valis v1.0.0 - Lexygraph AI Â© 2023
          </Typography>
        </Box>
      </Drawer>
    </>
  );
}

export default Sidebar;
