// src/pages/Dashboard.js
import React, { useState } from 'react';
import { Box, Typography, Container, Grid, useTheme } from '@mui/material';
import Header from '../components/common/Header';
import Sidebar from '../components/common/Sidebar';
import ProfileSection from '../components/profile/ProfileSection';
import MultiAgentSection from '../components/multiagent/MultiAgentSection';
import AliSection from '../components/ali/AliSection';
import AgentsSection from '../components/agents/AgentsSection';
import styled from 'styled-components';

const PageContainer = styled(Box)`
  display: flex;
  min-height: 100vh;
  background: ${props => 
    props.theme.palette.mode === 'dark' 
      ? 'linear-gradient(135deg, #121212 0%, #1e1e1e 100%)' 
      : 'linear-gradient(135deg, #f5f7fa 0%, #e4e8f0 100%)'
  };
`;

const MainContent = styled(Box)`
  flex-grow: 1;
  padding: ${props => props.theme.spacing(3)};
  transition: margin-left 0.3s;
  margin-left: ${props => props.sidebarOpen ? '240px' : 0};

  ${props => props.theme.breakpoints.down('md')} {
    margin-left: 0;
    width: 100%;
  }
`;

function Dashboard() {
  const theme = useTheme();
  const [activeSection, setActiveSection] = useState('profile');
  const isMobile = theme.breakpoints.down('md');

  // Determina se la sidebar è aperta (solo per desktop, su mobile è sempre chiusa di default)
  const sidebarOpen = !theme.breakpoints.values.md > window.innerWidth;

  const renderContent = () => {
    switch (activeSection) {
      case 'profile':
        return <ProfileSection />;
      case 'multiagent':
        return <MultiAgentSection />;
      case 'ali':
        return <AliSection />;
      case 'agents':
        return <AgentsSection />;
      default:
        return <ProfileSection />;
    }
  };

  return (
    <PageContainer theme={theme}>
      <Header />
      <Sidebar 
        activeSection={activeSection} 
        onSectionChange={setActiveSection} 
      />
      <MainContent theme={theme} sidebarOpen={sidebarOpen}>
        <Container maxWidth="xl">
          {renderContent()}
        </Container>
      </MainContent>
    </PageContainer>
  );
}

export default Dashboard;
