// valis/src/pages/Dashboard.js
import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Box, 
  Typography, 
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton,
  Tooltip,
  Container,
  Fade,
  Grow
} from '@mui/material';
import { styled } from '@mui/material/styles';
import PersonIcon from '@mui/icons-material/Person';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';
import LogoutIcon from '@mui/icons-material/Logout';
import HubIcon from '@mui/icons-material/Hub';
import RocketLaunchIcon from '@mui/icons-material/RocketLaunch';
import PsychologyAltIcon from '@mui/icons-material/PsychologyAlt';
import PeopleIcon from '@mui/icons-material/People'; 
import Sidebar from '../components/common/Sidebar';
import { ThemeContext } from '../context/ThemeContext';

// Logo VALIS esattamente come nell'immagine con effetti hover animati
const ValisLogo = ({ theme }) => {
  return (
    <Box sx={{ 
      display: 'flex', 
      alignItems: 'center', 
      position: 'relative',
      cursor: 'pointer',
      '&:hover .valis-dot': {
        transform: 'scale(1.5)',
        boxShadow: `0 0 12px ${theme.palette.primary.main}`,
      },
      '&:hover .valis-line': {
        width: '100%',
        boxShadow: `0 0 8px ${theme.palette.primary.main}`,
      }
    }}>
      {/* Punto viola in alto a destra del logo */}
      <Box 
        className="valis-dot"
        sx={{ 
          position: 'absolute', 
          width: 6, 
          height: 6, 
          borderRadius: '50%', 
          bgcolor: '#9A7CFF', 
          right: -6, 
          top: 0,
          transition: 'all 0.4s cubic-bezier(0.17, 0.67, 0.83, 0.67)',
        }} 
      />

      {/* Punto viola in basso a sinistra del logo */}
      <Box 
        className="valis-dot"
        sx={{ 
          position: 'absolute', 
          width: 6, 
          height: 6, 
          borderRadius: '50%', 
          bgcolor: '#9A7CFF', 
          left: -6, 
          bottom: 8,
          transition: 'all 0.4s cubic-bezier(0.17, 0.67, 0.83, 0.67)',
        }} 
      />

      {/* Logo text */}
      <Typography
        variant="h3"
        fontFamily="'Times New Roman', Times, serif"
        fontWeight={600}
        letterSpacing="0.02em"
        sx={{
          color: theme.palette.mode === 'dark' ? '#FFFFFF' : '#14142B',
          fontSize: '2.5rem',
          position: 'relative',
        }}
      >
        VALIS
      </Typography>

      {/* Linea decorativa sotto il logo - si allunga al passaggio del mouse */}
      <Box 
        className="valis-line"
        sx={{ 
          position: 'absolute', 
          height: 2.5, 
          width: '20%', 
          bgcolor: '#9A7CFF', 
          left: 0, 
          bottom: -4,
          borderRadius: 3,
          transition: 'all 0.4s cubic-bezier(0.17, 0.67, 0.83, 0.67)',
        }} 
      />

      {/* "by Lexygraph AI" text */}
      <Typography
        variant="subtitle2"
        fontFamily="'Inter', sans-serif"
        sx={{
          position: 'absolute',
          color: theme.palette.text.secondary,
          left: 0,
          bottom: -25,
          fontWeight: 400,
          fontSize: '0.875rem',
        }}
      >
        by Lexygraph AI
      </Typography>
    </Box>
  );
};

// Feature Button con stile per gli elementi sulla dashboard
const FeatureButton = styled(Button)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  padding: theme.spacing(5),
  height: '100%',
  backgroundColor: 'transparent',
  transition: 'all 0.5s cubic-bezier(0.05, 0.7, 0.1, 1)',
  position: 'relative',
  overflow: 'hidden',
  '&:hover': {
    backgroundColor: 'transparent',
    transform: 'translateY(-12px)',
    '& .feature-button-icon': {
      transform: 'scale(1.15) translateY(-5px)',
      filter: `drop-shadow(0 10px 15px ${alpha(theme.palette.primary.main, 0.5)})`,
      opacity: 1,
    },
    '& .feature-button-text': {
      transform: 'translateY(5px)',
      color: theme.palette.primary.main,
      fontWeight: 700,
      textShadow: `0 2px 4px ${alpha(theme.palette.primary.main, 0.3)}`,
    }
  },
  '&:active': {
    backgroundColor: 'transparent',
    transform: 'translateY(-5px) scale(0.98)',
  },
  '& .feature-button-icon': {
    fontSize: 80,
    marginBottom: theme.spacing(3),
    color: theme.palette.primary.main,
    transition: 'all 0.5s cubic-bezier(0.05, 0.7, 0.1, 1)',
    opacity: 0.85,
    filter: `drop-shadow(0 5px 10px ${alpha(theme.palette.primary.main, 0.3)})`,
  },
  '& .feature-button-text': {
    fontWeight: 600,
    fontSize: '1.3rem',
    transition: 'all 0.4s ease',
    letterSpacing: '0.01em',
    color: theme.palette.mode === 'dark' 
      ? theme.palette.primary.light 
      : theme.palette.primary.dark,
  }
}));

// Header Action Button
const HeaderActionButton = styled(IconButton)(({ theme }) => ({
  width: 40,
  height: 40,
  backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#FFFFFF', 0.8),
  backdropFilter: 'blur(8px)',
  border: `1px solid ${theme.palette.mode === 'dark' ? alpha('#FFFFFF', 0.1) : alpha('#7C4DFF', 0.1)}`,
  borderRadius: 12,
  transition: 'all 0.2s cubic-bezier(0.17, 0.67, 0.83, 0.67)',
  '&:hover': {
    backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.9) : alpha('#FFFFFF', 1),
    transform: 'translateY(-2px)',
    boxShadow: theme.palette.mode === 'dark' 
      ? '0 6px 12px rgba(0, 0, 0, 0.2), 0 0 0 1px rgba(154, 124, 255, 0.2)' 
      : '0 6px 12px rgba(124, 77, 255, 0.12), 0 0 0 1px rgba(124, 77, 255, 0.1)',
  },
  '&:active': {
    transform: 'translateY(-1px) scale(0.97)',
    boxShadow: theme.palette.mode === 'dark' 
      ? '0 3px 8px rgba(0, 0, 0, 0.15), 0 0 0 1px rgba(154, 124, 255, 0.15)' 
      : '0 3px 8px rgba(124, 77, 255, 0.08), 0 0 0 1px rgba(124, 77, 255, 0.05)',
  },
  '& .MuiSvgIcon-root': {
    fontSize: 20,
    color: theme.palette.mode === 'dark' ? theme.palette.primary.light : theme.palette.primary.main,
    transition: 'transform 0.2s ease',
  },
  '&:hover .MuiSvgIcon-root': {
    transform: 'scale(1.1)',
  }
}));

// Enhanced typing effect with longer durations and smoother transitions
const TypingEffect = ({ messages, typingSpeed = 40, deleteSpeed = 20, delayAfterType = 7000 }) => {
  const [displayText, setDisplayText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isWaiting, setIsWaiting] = useState(false);

  useEffect(() => {
    const currentMessage = messages[currentIndex];

    if (isWaiting) {
      const waitTimer = setTimeout(() => {
        setIsWaiting(false);
        setIsDeleting(true);
      }, delayAfterType);
      return () => clearTimeout(waitTimer);
    }

    if (isDeleting && displayText === '') {
      setIsDeleting(false);
      setCurrentIndex((currentIndex + 1) % messages.length);
      return;
    }

    if (!isDeleting && displayText === currentMessage) {
      setIsWaiting(true);
      return;
    }

    const timeout = setTimeout(() => {
      if (isDeleting) {
        setDisplayText(prev => prev.substring(0, prev.length - 1));
      } else {
        setDisplayText(currentMessage.substring(0, displayText.length + 1));
      }
    }, isDeleting ? deleteSpeed : typingSpeed);

    return () => clearTimeout(timeout);
  }, [displayText, isDeleting, currentIndex, messages, typingSpeed, deleteSpeed, delayAfterType, isWaiting]);

  return <span>{displayText}</span>;
};

// Custom Icon Components
const NetworkGraphIcon = () => (
  <svg width="80" height="80" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M17.5,7A5.5,5.5 0 0,1 23,12.5A5.5,5.5 0 0,1 17.5,18C15.79,18 14.27,17.22 13.26,16H10.74C9.73,17.22 8.21,18 6.5,18A5.5,5.5 0 0,1 1,12.5A5.5,5.5 0 0,1 6.5,7C8.21,7 9.73,7.78 10.74,9H13.26C14.27,7.78 15.79,7 17.5,7M17.5,9A3.5,3.5 0 0,0 14,12.5A3.5,3.5 0 0,0 17.5,16A3.5,3.5 0 0,0 21,12.5A3.5,3.5 0 0,0 17.5,9M6.5,9A3.5,3.5 0 0,0 3,12.5A3.5,3.5 0 0,0 6.5,16A3.5,3.5 0 0,0 10,12.5A3.5,3.5 0 0,0 6.5,9M12,14A1,1 0 0,1 11,13A1,1 0 0,1 12,12A1,1 0 0,1 13,13A1,1 0 0,1 12,14Z" />
  </svg>
);

const MagnifyingGlassIcon = () => (
  <svg width="80" height="80" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M9.5,3A6.5,6.5 0 0,1 16,9.5C16,11.11 15.41,12.59 14.44,13.73L14.71,14H15.5L20.5,19L19,20.5L14,15.5V14.71L13.73,14.44C12.59,15.41 11.11,16 9.5,16A6.5,6.5 0 0,1 3,9.5A6.5,6.5 0 0,1 9.5,3M9.5,5C7,5 5,7 5,9.5C5,12 7,14 9.5,14C12,14 14,12 14,9.5C14,7 12,5 9.5,5Z" />
  </svg>
);

const ButlerIcon = () => (
  <svg width="80" height="80" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M16,1H8A3,3 0 0,0 5,4V5H19V4A3,3 0 0,0 16,1M19,6H5V20A3,3 0 0,0 8,23H16A3,3 0 0,0 19,20V6M11.65,7.15L16.36,11.86L14.93,13.29L13.5,11.86V17H10.5V11.86L9.07,13.29L7.64,11.86L11.65,7.15Z" />
  </svg>
);

const ClientIcon = () => (
  <svg width="80" height="80" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M12,4A4,4 0 0,1 16,8A4,4 0 0,1 12,12A4,4 0 0,1 8,8A4,4 0 0,1 12,4M12,14C16.42,14 20,15.79 20,18V20H4V18C4,15.79 7.58,14 12,14Z" />
  </svg>
);

function Dashboard() {
  const navigate = useNavigate();
  const { toggleColorMode, theme } = useContext(ThemeContext);
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [sidebarOpen, setSidebarOpen] = useState(!isMobile);

  // Latin legal brocards (Latin only, brief)
  const legalBrocards = [
    "Dura lex, sed lex",
    "Pacta sunt servanda",
    "In dubio pro reo",
    "Ubi societas, ibi ius",
    "Ignorantia legis neminem excusat"
  ];

  // Handler for navigation to different sections
  const handleNavigation = (section) => {
    navigate(`/${section}`);
  };

  // Handle logout
  const handleLogout = () => {
    try {
      // Remove tokens from local storage
      localStorage.removeItem('access_token');
      localStorage.removeItem('refresh_token');

      // Redirect to login page
      window.location.href = '/login';
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  return (
    <Box sx={{ 
      display: 'flex', 
      flexDirection: 'column',
      minHeight: '100vh', 
      background: theme.palette.mode === 'dark' 
        ? `radial-gradient(circle at 0% 20%, ${alpha('#2D2B55', 0.7)} 0%, transparent 30%),
           radial-gradient(circle at 100% 80%, ${alpha('#28284D', 0.7)} 0%, transparent 30%),
           ${theme.palette.background.default}`
        : `radial-gradient(circle at 0% 20%, ${alpha('#F0E7FF', 0.5)} 0%, transparent 30%),
           radial-gradient(circle at 100% 80%, ${alpha('#EDF5FF', 0.5)} 0%, transparent 30%),
           ${theme.palette.background.default}`,
      backgroundAttachment: 'fixed',
      position: 'relative',
      transition: 'background 0.5s ease-in-out',
    }}>
      {/* Background particle pattern */}
      <Box sx={{ 
        position: 'absolute', 
        top: 0, 
        left: 0, 
        right: 0, 
        bottom: 0, 
        pointerEvents: 'none', 
        zIndex: 0, 
        opacity: 0.4, 
        backgroundImage: theme.palette.mode === 'dark' 
          ? 'url("data:image/svg+xml,%3Csvg width=\'20\' height=\'20\' viewBox=\'0 0 20 20\' xmlns=\'http://www.w3.org/2000/svg\'%3E%3Cg fill=\'%239C92AC\' fill-opacity=\'0.03\' fill-rule=\'evenodd\'%3E%3Ccircle cx=\'3\' cy=\'3\' r=\'3\'/%3E%3Ccircle cx=\'13\' cy=\'13\' r=\'3\'/%3E%3C/g%3E%3C/svg%3E")'
          : 'url("data:image/svg+xml,%3Csvg width=\'20\' height=\'20\' viewBox=\'0 0 20 20\' xmlns=\'http://www.w3.org/2000/svg\'%3E%3Cg fill=\'%23B39DDB\' fill-opacity=\'0.05\' fill-rule=\'evenodd\'%3E%3Ccircle cx=\'3\' cy=\'3\' r=\'3\'/%3E%3Ccircle cx=\'13\' cy=\'13\' r=\'3\'/%3E%3C/g%3E%3C/svg%3E")'
      }} />

      {/* Sidebar */}
      <Sidebar 
        open={sidebarOpen} 
        onClose={() => setSidebarOpen(false)}
        activeSection="dashboard"
      />

      {/* Header */}
      <Box sx={{ 
        p: 3, 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        position: 'relative',
        zIndex: 10
      }}>
        <ValisLogo theme={theme} />

        <Box sx={{ display: 'flex', gap: 1.5 }}>
          <Tooltip title="Profilo" arrow>
            <HeaderActionButton onClick={() => handleNavigation('profilo')}>
              <PersonIcon />
            </HeaderActionButton>
          </Tooltip>
          <Tooltip title={theme.palette.mode === 'dark' ? "Modalità chiara" : "Modalità scura"} arrow>
            <HeaderActionButton onClick={toggleColorMode}>
              {theme.palette.mode === 'dark' 
                ? <LightModeIcon />
                : <DarkModeIcon />
              }
            </HeaderActionButton>
          </Tooltip>
          <Tooltip title="Logout" arrow>
            <HeaderActionButton onClick={handleLogout}>
              <LogoutIcon />
            </HeaderActionButton>
          </Tooltip>
        </Box>
      </Box>

      {/* Main content */}
      <Container maxWidth="lg" sx={{ 
        position: 'relative', 
        zIndex: 2, 
        flexGrow: 1,
        display: 'flex',
        flexDirection: 'column'
      }}>
        <Box component="main" sx={{ 
          flexGrow: 1,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          py: 4
        }}>
          {/* Welcome message - Latin brocards with enhanced styling */}
          <Fade in={true} timeout={1000}>
            <Box sx={{ 
              mb: 8, 
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              textAlign: 'center',
              maxWidth: '900px'
            }}>
              <Typography 
                variant="h1" 
                sx={{ 
                  fontWeight: 700,
                  mb: 2,
                  fontSize: { xs: '2.5rem', sm: '3.5rem', md: '4.5rem' },
                  color: theme.palette.mode === 'dark' ? theme.palette.primary.light : theme.palette.primary.main,
                  letterSpacing: '-0.02em',
                  lineHeight: 1.2,
                  fontStyle: 'italic',
                  padding: '0 20px',
                  background: theme.palette.mode === 'dark'
                    ? `linear-gradient(135deg, ${theme.palette.primary.light} 0%, ${theme.palette.primary.main} 100%)`
                    : `linear-gradient(135deg, ${theme.palette.primary.dark} 0%, ${theme.palette.primary.main} 100%)`,
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  color: 'transparent',
                  textShadow: theme.palette.mode === 'dark'
                    ? '0 5px 30px rgba(179, 136, 255, 0.5)'
                    : '0 5px 30px rgba(124, 77, 255, 0.3)',
                }}
              >
                <TypingEffect messages={legalBrocards} />
              </Typography>
              <Typography 
                variant="h5" 
                color="textSecondary"
                sx={{
                  mt: 4,
                  fontWeight: 400,
                  letterSpacing: '0.01em',
                  opacity: 0.9,
                }}
              >
                Seleziona una funzionalità per iniziare
              </Typography>
            </Box>
          </Fade>

          {/* Main navigation - grid with premium feature buttons */}
          <Grid container spacing={6} justifyContent="center" sx={{ maxWidth: '1200px', mx: 'auto' }}>
            {[
              { id: 'multiagente', name: 'Multiagente', icon: <NetworkGraphIcon />, delay: 200 },
              { id: 'ali', name: 'ALI', icon: <MagnifyingGlassIcon />, delay: 400 },
              { id: 'agenti', name: 'Agenti', icon: <ButlerIcon />, delay: 600 },
              { id: 'clienti', name: 'Clienti', icon: <ClientIcon />, delay: 800 }
            ].map((item) => (
              <Grid item xs={12} sm={6} md={3} key={item.id}>
                <Grow in={true} timeout={800} style={{ transformOrigin: '0 0 0' }} {...{ timeout: 1000 + item.delay }}>
                  <Box>
                    <FeatureButton 
                      onClick={() => handleNavigation(item.id)}
                      sx={{ width: '100%', height: '100%' }}
                    >
                      {item.icon}
                      <Typography className="feature-button-text">{item.name}</Typography>
                    </FeatureButton>
                  </Box>
                </Grow>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>

      {/* Footer */}
      <Fade in={true} timeout={1500}>
        <Box 
          component="footer" 
          sx={{ 
            p: 2, 
            textAlign: 'center',
            position: 'relative',
            zIndex: 2,
            mt: 'auto'
          }}
        >
          <Typography variant="body2" color="text.secondary">
            Valis by Lexygraph AI &copy; {new Date().getFullYear()} - Tutti i diritti riservati
          </Typography>
        </Box>
      </Fade>
    </Box>
  );
}

export default Dashboard;
