// src/App.js
import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeContextProvider } from './context/ThemeContext';
import { AuthProvider, useAuth } from './context/AuthContext';

// Importa gli stili - l'ordine è importante!
import './styles/globals.css';
import './styles/login-scope.css';

// Pages & Components
import Dashboard from './pages/Dashboard';
import Multiagent from './components/multiagent/Multiagent';
import ProfileSection from './components/profile/ProfileSection';
import Login from './pages/Login';

// Auth Guard Component
const PrivateRoute = ({ children }) => {
  const auth = useAuth();

  if (auth.loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return auth.isAuthenticated ? children : <Navigate to="/login" />;
};

function App() {
  return (
    <ThemeContextProvider>
      <AuthProvider>
        <div className="app-root full-width full-height">
          <Router>
            <div className="app-container full-width full-height">
              <Routes>
                <Route path="/login" element={<Login />} />
                <Route 
                  path="/" 
                  element={
                    <PrivateRoute>
                      <Dashboard />
                    </PrivateRoute>
                  } 
                />
                <Route 
                  path="/multiagente" 
                  element={
                    <PrivateRoute>
                      <Multiagent />
                    </PrivateRoute>
                  } 
                />
                <Route 
                  path="/profile" 
                  element={
                    <PrivateRoute>
                      <ProfileSection />
                    </PrivateRoute>
                  } 
                />
                <Route path="*" element={<Navigate to="/" />} />
              </Routes>
            </div>
          </Router>
        </div>
      </AuthProvider>
    </ThemeContextProvider>
  );
}

export default App;
