import React, { useState } from 'react';
import { Box, Container, useTheme, useMediaQuery } from '@mui/material';
import Header from '../components/common/Header';
import Sidebar from '../components/common/Sidebar';
import ProfileSection from '../components/profile/ProfileSection';
import MultiAgentSection from '../components/multiagent/MultiAgentSection';
import AliSection from '../components/ali/AliSection';
import AgentsSection from '../components/agents/AgentsSection';

function Dashboard() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [activeSection, setActiveSection] = useState('profile');

  const renderContent = () => {
    switch (activeSection) {
      case 'profile':
        return <ProfileSection />;
      case 'multiagent':
        return <MultiAgentSection />;
      case 'ali':
        return <AliSection />;
      case 'agents':
        return <AgentsSection />;
      default:
        return <ProfileSection />;
    }
  };

  return (
    <Box sx={{ display: 'flex', minHeight: '100vh', bgcolor: 'background.default' }}>
      <Header />
      <Sidebar activeSection={activeSection} onSectionChange={setActiveSection} />
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          mt: 8, // Spazio per l'header
          ml: { xs: 0, md: '240px' }, // Spazio per la sidebar su desktop
          width: { xs: '100%', md: 'calc(100% - 240px)' },
        }}
      >
        <Container maxWidth="xl">
          {renderContent()}
        </Container>
      </Box>
    </Box>
  );
}

export default Dashboard;
