// src/components/multiagent/Multiagent.js
import React, { useState, useEffect, useContext } from 'react';
import { 
  Box, 
  Typography, 
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton,
  Tooltip,
  Container,
  Paper,
  TextField,
  Chip,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  ListItemIcon,
  Divider,
  MenuItem,
  Menu,
  Card,
  CardContent,
  CardActions,
  CircularProgress,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle
} from '@mui/material';
import { useTheme, styled } from '@mui/material/styles';
import PersonIcon from '@mui/icons-material/Person';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';
import LogoutIcon from '@mui/icons-material/Logout';
import AddIcon from '@mui/icons-material/Add';
import FolderIcon from '@mui/icons-material/Folder';
import DescriptionIcon from '@mui/icons-material/Description';
import SendIcon from '@mui/icons-material/Send';
import DownloadIcon from '@mui/icons-material/Download';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import PsychologyIcon from '@mui/icons-material/Psychology';
import HubIcon from '@mui/icons-material/Hub';
import FormatListBulletedIcon from '@mui/icons-material/FormatListBulleted';
import Sidebar from '../common/Sidebar';
import { ThemeContext } from '../../context/ThemeContext';

// Container per il MultiagentPage - centrato con sidebar
const PageContainer = styled(Box)(({ theme, open, isMobile }) => ({
  flexGrow: 1,
  padding: theme.spacing(3),
  transition: theme.transitions.create(['margin', 'padding'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  marginLeft: 0,
  maxWidth: '100%',
  margin: '0 auto',
  minHeight: '100vh',
  ...(open && {
    transition: theme.transitions.create(['margin', 'padding'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: isMobile ? 0 : 280,
    maxWidth: isMobile ? '100%' : 'calc(100% - 280px)',
  }),
  [theme.breakpoints.down('sm')]: {
    paddingLeft: theme.spacing(2),
    paddingRight: theme.spacing(2),
  }
}));

// Quadrante stilizzato
const Quadrant = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  borderRadius: 16,
  backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.8) : alpha('#FFFFFF', 0.95),
  backdropFilter: 'blur(10px)',
  border: `1px solid ${theme.palette.mode === 'dark' ? alpha('#FFFFFF', 0.08) : alpha('#7C4DFF', 0.08)}`,
  boxShadow: theme.palette.mode === 'dark' 
    ? '0 8px 24px rgba(0, 0, 0, 0.2)' 
    : '0 8px 24px rgba(124, 77, 255, 0.08)',
  transition: 'all 0.3s ease',
  '&:hover': {
    boxShadow: theme.palette.mode === 'dark' 
      ? '0 10px 28px rgba(0, 0, 0, 0.3)' 
      : '0 10px 28px rgba(124, 77, 255, 0.12)',
  },
  position: 'relative',
  overflow: 'hidden',
}));

// Stile per il titolo del quadrante
const QuadrantTitle = styled(Typography)(({ theme }) => ({
  marginBottom: theme.spacing(2),
  fontWeight: 600,
  display: 'flex',
  alignItems: 'center',
  '& .MuiSvgIcon-root': {
    marginRight: theme.spacing(1),
    color: theme.palette.primary.main,
  }
}));

// Card del progetto
const ProjectCard = styled(Card)(({ theme }) => ({
  height: '100%',
  borderRadius: 16,
  backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.8) : alpha('#FFFFFF', 0.95),
  backdropFilter: 'blur(10px)',
  border: `1px solid ${theme.palette.mode === 'dark' ? alpha('#FFFFFF', 0.08) : alpha('#7C4DFF', 0.08)}`,
  boxShadow: theme.palette.mode === 'dark' 
    ? '0 8px 24px rgba(0, 0, 0, 0.2)' 
    : '0 8px 24px rgba(124, 77, 255, 0.08)',
  transition: 'all 0.3s ease',
  '&:hover': {
    transform: 'translateY(-5px)',
    boxShadow: theme.palette.mode === 'dark' 
      ? '0 12px 28px rgba(0, 0, 0, 0.3)' 
      : '0 12px 28px rgba(124, 77, 255, 0.12)',
  },
  cursor: 'pointer',
}));

// Chip Agente
const AgentChip = styled(Chip)(({ theme }) => ({
  margin: theme.spacing(0.5),
  backgroundColor: theme.palette.mode === 'dark' 
    ? alpha(theme.palette.primary.main, 0.2) 
    : alpha(theme.palette.primary.main, 0.1),
  border: `1px solid ${theme.palette.mode === 'dark' 
    ? alpha(theme.palette.primary.main, 0.3) 
    : alpha(theme.palette.primary.main, 0.2)}`,
  '& .MuiChip-deleteIcon': {
    color: theme.palette.mode === 'dark' 
      ? alpha(theme.palette.primary.light, 0.7) 
      : theme.palette.primary.main,
    '&:hover': {
      color: theme.palette.mode === 'dark' 
        ? theme.palette.primary.light 
        : theme.palette.primary.dark,
    }
  }
}));

// Componente per selezionare gli agenti
const AgentSelector = ({ selectedAgents, onAgentChange }) => {
  const theme = useTheme();
  // Elenco agenti disponibili
  const availableAgents = [
    { id: 'researcher', name: 'Ricercatore', description: 'Ricerca informazioni' },
    { id: 'writer', name: 'Scrittore', description: 'Genera testo di qualità' },
    { id: 'lawyer', name: 'Avvocato', description: 'Analisi legale' },
    { id: 'coder', name: 'Programmatore', description: 'Genera codice' },
    { id: 'analyst', name: 'Analista', description: 'Analizza dati' },
  ];

  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleAgentSelect = (agent) => {
    onAgentChange([...selectedAgents, agent]);
    handleClose();
  };

  const handleDelete = (agentToDelete) => {
    onAgentChange(selectedAgents.filter((agent) => agent.id !== agentToDelete.id));
  };

  return (
    <Box>
      <Box display="flex" flexWrap="wrap" mb={2}>
        {selectedAgents.map((agent) => (
          <AgentChip
            key={agent.id}
            label={agent.name}
            onDelete={() => handleDelete(agent)}
            avatar={<PsychologyIcon style={{ width: 18, height: 18 }} />}
          />
        ))}
        <Button 
          startIcon={<AddIcon />} 
          onClick={handleClick}
          variant="outlined" 
          size="small"
          sx={{ 
            borderRadius: '16px', 
            margin: 0.5,
            minHeight: '32px',
            border: '1px dashed',
            '&:hover': {
              border: '1px dashed',
            }
          }}
        >
          Aggiungi Agente
        </Button>
      </Box>

      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleClose}
        PaperProps={{
          elevation: 3,
          sx: {
            maxHeight: 300,
            borderRadius: 2,
            mt: 1,
          },
        }}
      >
        {availableAgents
          .filter(agent => !selectedAgents.some(selected => selected.id === agent.id))
          .map(agent => (
            <MenuItem 
              key={agent.id} 
              onClick={() => handleAgentSelect(agent)}
              sx={{ 
                py: 1,
                '&:hover': {
                  bgcolor: theme => alpha(theme.palette.primary.main, 0.1),
                }
              }}
            >
              <ListItemIcon>
                <PsychologyIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText 
                primary={agent.name} 
                secondary={agent.description} 
              />
            </MenuItem>
          ))}
      </Menu>
    </Box>
  );
};

function Multiagent() {
  const { toggleColorMode, theme } = useContext(ThemeContext);
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [sidebarOpen, setSidebarOpen] = useState(!isMobile);

  // Stato per la modalità della pagina (seleziona progetto o lavora su progetto)
  const [pageMode, setPageMode] = useState('select'); // 'select' o 'work'

  // Stato per i progetti
  const [projects, setProjects] = useState([
    { id: '1', name: 'Analisi contratto di locazione', date: '10/03/2025', agents: ['researcher', 'lawyer'] },
    { id: '2', name: 'Pianificazione strategia legale', date: '05/03/2025', agents: ['analyst', 'lawyer', 'writer'] }
  ]);
  const [selectedProject, setSelectedProject] = useState(null);

  // Stato per il dialogo di creazione nuovo progetto
  const [newProjectDialog, setNewProjectDialog] = useState(false);
  const [newProjectName, setNewProjectName] = useState('');

  // Stato per il prompt ed agenti
  const [prompt, setPrompt] = useState('');
  const [selectedAgents, setSelectedAgents] = useState([]);

  // Stato per l'output
  const [output, setOutput] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  // Stato per l'artifact
  const [artifact, setArtifact] = useState(null);

  const handleCreateProject = () => {
    if (newProjectName.trim()) {
      const newProject = {
        id: Date.now().toString(),
        name: newProjectName,
        date: new Date().toLocaleDateString(),
        agents: []
      };
      setProjects([newProject, ...projects]);
      setNewProjectDialog(false);
      setNewProjectName('');
      setSelectedProject(newProject);
      setPageMode('work');
    }
  };

  const handleSelectProject = (project) => {
    setSelectedProject(project);
    setPageMode('work');
    // Reset degli stati di lavoro
    setPrompt('');
    setSelectedAgents([]);
    setOutput('');
    setArtifact(null);
  };

  const handleSendPrompt = () => {
    if (prompt.trim() && selectedAgents.length > 0) {
      setIsProcessing(true);

      // Simulazione di elaborazione
      setTimeout(() => {
        setOutput(`Risposta generata per: "${prompt}"\n\nLa risposta è stata elaborata utilizzando ${selectedAgents.length} agenti:\n${selectedAgents.map(a => `- ${a.name}`).join('\n')}\n\nQuesto è un esempio di output testuale che verrebbe generato dal sistema multiagente. In una implementazione reale, questo sarebbe il risultato dell'elaborazione effettuata dagli agenti selezionati sulla base del prompt fornito dall'utente.`);

        // Simulazione di un artifact
        setArtifact({
          name: 'Documento Analisi.docx',
          size: '245 KB',
          type: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
          preview: `# Analisi Documento\n\n## Introduzione\nQuesto documento è stato generato in risposta al prompt: "${prompt}"\n\n## Sezioni Principali\n1. Analisi preliminare\n2. Considerazioni legali\n3. Raccomandazioni\n\n## Allegati\n- Riferimenti normativi\n- Giurisprudenza rilevante`
        });

        setIsProcessing(false);
      }, 3000);
    }
  };

  const handleDownloadArtifact = () => {
    if (artifact) {
      // In una implementazione reale, qui ci sarebbe il codice per scaricare il file
      alert(`Scaricamento di ${artifact.name} iniziato.`);
    }
  };

  return (
    <Box sx={{ 
      display: 'flex', 
      minHeight: '100vh',
      background: theme.palette.mode === 'dark' 
        ? `radial-gradient(circle at 0% 20%, ${alpha('#2D2B55', 0.6)} 0%, transparent 30%),
           radial-gradient(circle at 100% 80%, ${alpha('#28284D', 0.6)} 0%, transparent 30%),
           ${theme.palette.background.default}`
        : `radial-gradient(circle at 0% 20%, ${alpha('#F0E7FF', 0.4)} 0%, transparent 30%),
           radial-gradient(circle at 100% 80%, ${alpha('#EDF5FF', 0.4)} 0%, transparent 30%),
           ${theme.palette.background.default}`,
      backgroundAttachment: 'fixed',
    }}>
      {/* Sidebar */}
      <Sidebar 
        open={sidebarOpen} 
        onClose={() => setSidebarOpen(false)}
        activeSection="multiagente"
      />

      {/* Main Content */}
      <PageContainer open={sidebarOpen} isMobile={isMobile}>
        {/* Header */}
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          mb: 4,
          flexWrap: 'wrap'
        }}>
          <Box>
            <Typography variant="h4" fontWeight={600} color="primary" sx={{ display: 'flex', alignItems: 'center' }}>
              <HubIcon sx={{ mr: 1 }} />
              Multiagente
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              {pageMode === 'select' 
                ? 'Seleziona o crea un nuovo progetto' 
                : `Progetto: ${selectedProject?.name}`}
            </Typography>
          </Box>

          <Box sx={{ display: 'flex', gap: 1 }}>
            {pageMode === 'work' && (
              <Button
                startIcon={<FormatListBulletedIcon />}
                variant="outlined"
                onClick={() => setPageMode('select')}
                sx={{ mr: 1 }}
              >
                Progetti
              </Button>
            )}
            <Tooltip title="Tema" arrow>
              <IconButton onClick={toggleColorMode} sx={{ bgcolor: 'background.paper', boxShadow: 1 }}>
                {theme.palette.mode === 'dark' ? <LightModeIcon /> : <DarkModeIcon />}
              </IconButton>
            </Tooltip>
          </Box>
        </Box>

        {/* Sezione selezione progetto */}
        {pageMode === 'select' && (
          <>
            <Box sx={{ mb: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="h5">I tuoi progetti</Typography>
              <Button 
                variant="contained" 
                startIcon={<AddIcon />}
                onClick={() => setNewProjectDialog(true)}
              >
                Nuovo Progetto
              </Button>
            </Box>

            <Grid container spacing={3}>
              {projects.map(project => (
                <Grid item xs={12} sm={6} md={4} key={project.id}>
                  <ProjectCard onClick={() => handleSelectProject(project)}>
                    <CardContent>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <FolderIcon sx={{ color: 'primary.main', fontSize: 32, mr: 1 }} />
                        <Typography variant="h6" noWrap sx={{ maxWidth: '90%' }}>
                          {project.name}
                        </Typography>
                      </Box>
                      <Typography color="text.secondary" variant="body2" gutterBottom>
                        Creato il {project.date}
                      </Typography>
                      <Typography variant="body2" sx={{ mt: 1 }}>
                        {project.agents.length > 0 
                          ? `${project.agents.length} agenti configurati` 
                          : 'Nessun agente configurato'}
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <Button size="small" startIcon={<EditIcon />}>
                        Modifica
                      </Button>
                      <Box sx={{ flexGrow: 1 }} />
                      <IconButton size="small" color="error">
                        <DeleteIcon fontSize="small" />
                      </IconButton>
                    </CardActions>
                  </ProjectCard>
                </Grid>
              ))}
            </Grid>
          </>
        )}

        {/* Sezione di lavoro sul progetto */}
        {pageMode === 'work' && (
          <Grid container spacing={3} sx={{ height: 'calc(100vh - 180px)' }}>
            {/* Quadrante 1: Input e selezione agenti */}
            <Grid item xs={12} md={4} sx={{ height: { md: '100%' } }}>
              <Quadrant>
                <QuadrantTitle variant="h6">
                  <EditIcon /> Prompt e Agenti
                </QuadrantTitle>
                <TextField
                  label="Inserisci il tuo prompt"
                  multiline
                  rows={6}
                  fullWidth
                  value={prompt}
                  onChange={(e) => setPrompt(e.target.value)}
                  placeholder="Descrivi il compito che vuoi far svolgere agli agenti..."
                  variant="outlined"
                  sx={{ mb: 3 }}
                />

                <Typography variant="subtitle2" gutterBottom>
                  Seleziona gli agenti:
                </Typography>

                <AgentSelector 
                  selectedAgents={selectedAgents} 
                  onAgentChange={setSelectedAgents} 
                />

                <Box sx={{ flexGrow: 1 }} />

                <Button
                  variant="contained"
                  color="primary"
                  fullWidth
                  size="large"
                  startIcon={<SendIcon />}
                  disabled={!prompt.trim() || selectedAgents.length === 0 || isProcessing}
                  onClick={handleSendPrompt}
                  sx={{ mt: 2 }}
                >
                  {isProcessing ? (
                    <>
                      <CircularProgress size={24} sx={{ mr: 1, color: 'white' }} />
                      Elaborazione...
                    </>
                  ) : 'Invia Prompt'}
                </Button>
              </Quadrant>
            </Grid>

            {/* Quadrante 2: Output testuale */}
            <Grid item xs={12} md={4} sx={{ height: { md: '100%' } }}>
              <Quadrant>
                <QuadrantTitle variant="h6">
                  <DescriptionIcon /> Risposta
                </QuadrantTitle>

                {isProcessing ? (
                  <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100%' }}>
                    <CircularProgress size={40} sx={{ mb: 2 }} />
                    <Typography variant="body1">Elaborazione in corso...</Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                      Gli agenti stanno analizzando il prompt
                    </Typography>
                  </Box>
                ) : output ? (
                  <Box sx={{ 
                    flexGrow: 1, 
                    overflow: 'auto',
                    p: 2,
                    bgcolor: theme => alpha(theme.palette.background.default, 0.5),
                    borderRadius: 2
                  }}>
                    <Typography
                      component="pre"
                      sx={{
                        fontFamily: '"Inter", sans-serif',
                        whiteSpace: 'pre-wrap',
                        wordBreak: 'break-word',
                      }}
                    >
                      {output}
                    </Typography>
                  </Box>
                ) : (
                  <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100%', p: 3 }}>
                    <DescriptionIcon sx={{ fontSize: 60, color: 'text.disabled', mb: 2 }} />
                    <Typography variant="body1" align="center">
                      Invia un prompt per generare una risposta
                    </Typography>
                    <Typography variant="body2" color="text.secondary" align="center" sx={{ mt: 1 }}>
                      La risposta testuale apparirà qui
                    </Typography>
                  </Box>
                )}
              </Quadrant>
            </Grid>

            {/* Quadrante 3: Artifact */}
            <Grid item xs={12} md={4} sx={{ height: { md: '100%' } }}>
              <Quadrant>
                <QuadrantTitle variant="h6">
                  <DescriptionIcon /> Documento Generato
                </QuadrantTitle>

                {isProcessing ? (
                  <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100%' }}>
                    <CircularProgress size={40} sx={{ mb: 2 }} />
                    <Typography variant="body1">Preparando documento...</Typography>
                  </Box>
                ) : artifact ? (
                  <>
                    <Box sx={{ 
                      p: 2, 
                      mb: 2,
                      border: '1px solid',
                      borderColor: 'divider',
                      borderRadius: 2,
                      bgcolor: theme => alpha(theme.palette.background.default, 0.5),
                      minHeight: '60%',
                      overflow: 'auto'
                    }}>
                      <Typography sx={{ mb: 1, fontWeight: 500 }}>
                        {artifact.name} <Chip size="small" label={artifact.size} />
                      </Typography>
                      <Divider sx={{ mb: 2 }} />
                      <Typography
                        component="pre"
                        sx={{
                          fontFamily: '"Inter", sans-serif',
                          whiteSpace: 'pre-wrap',
                          wordBreak: 'break-word',
                        }}
                      >
                        {artifact.preview}
                      </Typography>
                    </Box>
                    <Button
                      variant="contained"
                      color="primary"
                      fullWidth
                      startIcon={<DownloadIcon />}
                      onClick={handleDownloadArtifact}
                    >
                      Scarica come Word
                    </Button>
                  </>
                ) : (
                  <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100%', p: 3 }}>
                    <DescriptionIcon sx={{ fontSize: 60, color: 'text.disabled', mb: 2 }} />
                    <Typography variant="body1" align="center">
                      Nessun documento generato
                    </Typography>
                    <Typography variant="body2" color="text.secondary" align="center" sx={{ mt: 1 }}>
                      Il documento generato apparirà qui e sarà scaricabile
                    </Typography>
                  </Box>
                )}
              </Quadrant>
            </Grid>
          </Grid>
        )}
      </PageContainer>

      {/* Dialog per nuovo progetto */}
      <Dialog open={newProjectDialog} onClose={() => setNewProjectDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Crea Nuovo Progetto</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Nome del progetto"
            fullWidth
            variant="outlined"
            value={newProjectName}
            onChange={(e) => setNewProjectName(e.target.value)}
            sx={{ mt: 1 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setNewProjectDialog(false)}>Annulla</Button>
          <Button 
            onClick={handleCreateProject} 
            variant="contained"
            disabled={!newProjectName.trim()}
          >
            Crea
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default Multiagent;
