// src/components/multiagent/Multiagent.js
import React, { useState, useEffect, useContext } from 'react';
import { 
  Box, 
  Typography, 
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton,
  Tooltip,
  Container,
  Paper,
  TextField,
  Chip,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  ListItemIcon,
  Divider,
  MenuItem,
  Menu,
  Card,
  CardContent,
  CardActions,
  CircularProgress,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormControl,
  InputLabel,
  Select,
  Autocomplete,
  Stack,
  InputAdornment
} from '@mui/material';
import { useTheme, styled } from '@mui/material/styles';
import PersonIcon from '@mui/icons-material/Person';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';
import LogoutIcon from '@mui/icons-material/Logout';
import AddIcon from '@mui/icons-material/Add';
import FolderIcon from '@mui/icons-material/Folder';
import DescriptionIcon from '@mui/icons-material/Description';
import SendIcon from '@mui/icons-material/Send';
import DownloadIcon from '@mui/icons-material/Download';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import PsychologyIcon from '@mui/icons-material/Psychology';
import HubIcon from '@mui/icons-material/Hub';
import FormatListBulletedIcon from '@mui/icons-material/FormatListBulleted';
import BusinessIcon from '@mui/icons-material/Business';
import EventIcon from '@mui/icons-material/Event';
import LockIcon from '@mui/icons-material/Lock';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import SupervisorAccountIcon from '@mui/icons-material/SupervisorAccount';
import GavelIcon from '@mui/icons-material/Gavel';
import Sidebar from '../common/Sidebar';
import { ThemeContext } from '../../context/ThemeContext';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider, DatePicker } from '@mui/x-date-pickers';
import it from 'date-fns/locale/it';

// Container per il MultiagentPage - centrato con sidebar
const PageContainer = styled(Box)(({ theme, open, isMobile }) => ({
  flexGrow: 1,
  padding: theme.spacing(3),
  transition: theme.transitions.create(['margin', 'padding'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  marginLeft: 0,
  maxWidth: '100%',
  margin: '0 auto',
  minHeight: '100vh',
  ...(open && {
    transition: theme.transitions.create(['margin', 'padding'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: isMobile ? 0 : 280,
    maxWidth: isMobile ? '100%' : 'calc(100% - 280px)',
  }),
  [theme.breakpoints.down('sm')]: {
    paddingLeft: theme.spacing(2),
    paddingRight: theme.spacing(2),
  }
}));

// Quadrante stilizzato
const Quadrant = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  borderRadius: 16,
  backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.8) : alpha('#FFFFFF', 0.95),
  backdropFilter: 'blur(10px)',
  border: `1px solid ${theme.palette.mode === 'dark' ? alpha('#FFFFFF', 0.08) : alpha('#7C4DFF', 0.08)}`,
  boxShadow: theme.palette.mode === 'dark' 
    ? '0 8px 24px rgba(0, 0, 0, 0.2)' 
    : '0 8px 24px rgba(124, 77, 255, 0.08)',
  transition: 'all 0.3s ease',
  '&:hover': {
    boxShadow: theme.palette.mode === 'dark' 
      ? '0 10px 28px rgba(0, 0, 0, 0.3)' 
      : '0 10px 28px rgba(124, 77, 255, 0.12)',
  },
  position: 'relative',
  overflow: 'hidden',
}));

// Stile per il titolo del quadrante
const QuadrantTitle = styled(Typography)(({ theme }) => ({
  marginBottom: theme.spacing(2),
  fontWeight: 600,
  display: 'flex',
  alignItems: 'center',
  '& .MuiSvgIcon-root': {
    marginRight: theme.spacing(1),
    color: theme.palette.primary.main,
  }
}));

// Card del progetto
const ProjectCard = styled(Card)(({ theme }) => ({
  height: '100%',
  borderRadius: 16,
  backgroundColor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.8) : alpha('#FFFFFF', 0.95),
  backdropFilter: 'blur(10px)',
  border: `1px solid ${theme.palette.mode === 'dark' ? alpha('#FFFFFF', 0.08) : alpha('#7C4DFF', 0.08)}`,
  boxShadow: theme.palette.mode === 'dark' 
    ? '0 8px 24px rgba(0, 0, 0, 0.2)' 
    : '0 8px 24px rgba(124, 77, 255, 0.08)',
  transition: 'all 0.3s ease',
  '&:hover': {
    transform: 'translateY(-5px)',
    boxShadow: theme.palette.mode === 'dark' 
      ? '0 12px 28px rgba(0, 0, 0, 0.3)' 
      : '0 12px 28px rgba(124, 77, 255, 0.12)',
  },
  cursor: 'pointer',
}));

// Chip Agente
const AgentChip = styled(Chip)(({ theme }) => ({
  margin: theme.spacing(0.5),
  backgroundColor: theme.palette.mode === 'dark' 
    ? alpha(theme.palette.primary.main, 0.2) 
    : alpha(theme.palette.primary.main, 0.1),
  border: `1px solid ${theme.palette.mode === 'dark' 
    ? alpha(theme.palette.primary.main, 0.3) 
    : alpha(theme.palette.primary.main, 0.2)}`,
  '& .MuiChip-deleteIcon': {
    color: theme.palette.mode === 'dark' 
      ? alpha(theme.palette.primary.light, 0.7) 
      : theme.palette.primary.main,
    '&:hover': {
      color: theme.palette.mode === 'dark' 
        ? theme.palette.primary.light 
        : theme.palette.primary.dark,
    }
  }
}));

// Componente per selezionare gli agenti
const AgentSelector = ({ selectedAgents, onAgentChange }) => {
  const theme = useTheme();
  // Elenco agenti disponibili con descrizioni utili per avvocati
  const availableAgents = [
    { id: 'researcher', name: 'Ricercatore', description: 'Ricerca giurisprudenza e normative', nickName: '@ricercatore' },
    { id: 'writer', name: 'Redattore', description: 'Genera documenti legali di qualitÃ ', nickName: '@redattore' },
    { id: 'lawyer', name: 'Avvocato', description: 'Analisi legale e strategia processuale', nickName: '@avvocato' },
    { id: 'coder', name: 'Analista Dati', description: 'Analisi quantitativa di documenti e pratiche', nickName: '@analista' },
    { id: 'notary', name: 'Notaio', description: 'Verifica formale di documenti', nickName: '@notaio' },
    { id: 'mediator', name: 'Mediatore', description: 'Supporto per conciliazioni e mediazioni', nickName: '@mediatore' },
    { id: 'executor', name: 'Esecutore', description: 'Automazione di pratiche standardizzate', nickName: '@esecutore' },
  ];

  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleAgentSelect = (agent) => {
    onAgentChange([...selectedAgents, agent]);
    handleClose();
  };

  const handleDelete = (agentToDelete) => {
    onAgentChange(selectedAgents.filter((agent) => agent.id !== agentToDelete.id));
  };

  return (
    <Box>
      <Box display="flex" flexWrap="wrap" mb={2}>
        {selectedAgents.map((agent) => (
          <AgentChip
            key={agent.id}
            label={agent.name}
            onDelete={() => handleDelete(agent)}
            avatar={<PsychologyIcon style={{ width: 18, height: 18 }} />}
          />
        ))}
        <Button 
          startIcon={<AddIcon />} 
          onClick={handleClick}
          variant="outlined" 
          size="small"
          sx={{ 
            borderRadius: '16px', 
            margin: 0.5,
            minHeight: '32px',
            border: '1px dashed',
            '&:hover': {
              border: '1px dashed',
            }
          }}
        >
          Aggiungi Agente
        </Button>
      </Box>

      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleClose}
        PaperProps={{
          elevation: 3,
          sx: {
            maxHeight: 300,
            borderRadius: 2,
            mt: 1,
          },
        }}
      >
        {availableAgents
          .filter(agent => !selectedAgents.some(selected => selected.id === agent.id))
          .map(agent => (
            <MenuItem 
              key={agent.id} 
              onClick={() => handleAgentSelect(agent)}
              sx={{ 
                py: 1,
                '&:hover': {
                  bgcolor: theme => alpha(theme.palette.primary.main, 0.1),
                }
              }}
            >
              <ListItemIcon>
                <PsychologyIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText 
                primary={agent.name} 
                secondary={`${agent.description} (${agent.nickName})`}
              />
            </MenuItem>
          ))}
      </Menu>
    </Box>
  );
};

// Componente popup per la creazione di un nuovo progetto
const NewProjectDialog = ({ open, onClose, onCreate }) => {
  const [projectName, setProjectName] = useState('');
  const [clientName, setClientName] = useState('');
  const [practiceArea, setPracticeArea] = useState('');
  const [deadline, setDeadline] = useState(null);
  const [priority, setPriority] = useState('medium');
  const [confidentiality, setConfidentiality] = useState('standard');
  const [notes, setNotes] = useState('');
  const [colleagues, setColleagues] = useState([]);

  // Lista delle aree di pratica comuni per studi legali
  const practiceAreas = [
    'Diritto Civile',
    'Diritto Commerciale',
    'Diritto Penale',
    'Diritto del Lavoro',
    'Diritto Tributario',
    'Diritto Amministrativo',
    'Diritto di Famiglia',
    'Diritto Immobiliare',
    'ProprietÃ  Intellettuale',
    'Diritto Bancario',
    'Diritto delle Assicurazioni',
    'Contenzioso',
    'Diritto dell\'Immigrazione',
    'Diritto Ambientale',
    'Diritto Internazionale',
    'Altro'
  ];

  // Lista di colleghi dello studio
  const colleaguesList = [
    { id: '1', name: 'Avv. Marco Rossi' },
    { id: '2', name: 'Avv. Giulia Bianchi' },
    { id: '3', name: 'Avv. Antonio Esposito' },
    { id: '4', name: 'Avv. Laura Ferretti' },
    { id: '5', name: 'Notaio Stefano Verdi' }
  ];

  const handleCreate = () => {
    if (projectName.trim()) {
      onCreate({
        name: projectName,
        client: clientName,
        practiceArea,
        deadline,
        priority,
        confidentiality,
        notes,
        colleagues,
        date: new Date().toLocaleDateString()
      });

      // Reset dei campi
      setProjectName('');
      setClientName('');
      setPracticeArea('');
      setDeadline(null);
      setPriority('medium');
      setConfidentiality('standard');
      setNotes('');
      setColleagues([]);
    }
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="md" fullWidth>
      <DialogTitle sx={{ pb: 1 }}>Crea Nuovo Progetto</DialogTitle>
      <DialogContent>
        <Grid container spacing={2} sx={{ mt: 0 }}>
          <Grid item xs={12}>
            <TextField
              autoFocus
              margin="dense"
              label="Nome del progetto"
              fullWidth
              variant="outlined"
              value={projectName}
              onChange={(e) => setProjectName(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <FolderIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              margin="dense"
              label="Nome del cliente"
              fullWidth
              variant="outlined"
              value={clientName}
              onChange={(e) => setClientName(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <BusinessIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>Area di pratica</InputLabel>
              <Select
                value={practiceArea}
                onChange={(e) => setPracticeArea(e.target.value)}
                label="Area di pratica"
                startAdornment={
                  <InputAdornment position="start" sx={{ ml: -0.5, mr: 0.5 }}>
                    <GavelIcon color="primary" />
                  </InputAdornment>
                }
              >
                {practiceAreas.map((area) => (
                  <MenuItem key={area} value={area}>{area}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={4}>
            <LocalizationProvider dateAdapter={AdapterDateFns} adapterLocale={it}>
              <DatePicker
                label="Scadenza"
                value={deadline}
                onChange={(newValue) => setDeadline(newValue)}
                renderInput={(params) => (
                  <TextField 
                    {...params} 
                    fullWidth 
                    margin="dense" 
                    InputProps={{
                      ...params.InputProps,
                      startAdornment: (
                        <InputAdornment position="start">
                          <EventIcon color="primary" />
                          {params.InputProps.startAdornment}
                        </InputAdornment>
                      ),
                    }}
                  />
                )}
              />
            </LocalizationProvider>
          </Grid>

          <Grid item xs={12} md={4}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>PrioritÃ </InputLabel>
              <Select
                value={priority}
                onChange={(e) => setPriority(e.target.value)}
                label="PrioritÃ "
              >
                <MenuItem value="low">Bassa</MenuItem>
                <MenuItem value="medium">Media</MenuItem>
                <MenuItem value="high">Alta</MenuItem>
                <MenuItem value="urgent">Urgente</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={4}>
            <FormControl fullWidth margin="dense" variant="outlined">
              <InputLabel>Riservatezza</InputLabel>
              <Select
                value={confidentiality}
                onChange={(e) => setConfidentiality(e.target.value)}
                label="Riservatezza"
                startAdornment={
                  <InputAdornment position="start" sx={{ ml: -0.5, mr: 0.5 }}>
                    <LockIcon color="primary" />
                  </InputAdornment>
                }
              >
                <MenuItem value="standard">Standard</MenuItem>
                <MenuItem value="sensitive">Sensibile</MenuItem>
                <MenuItem value="highly_confidential">Altamente confidenziale</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12}>
            <Autocomplete
              multiple
              options={colleaguesList}
              getOptionLabel={(option) => option.name}
              value={colleagues}
              onChange={(event, newValue) => setColleagues(newValue)}
              renderInput={(params) => (
                <TextField
                  {...params}
                  variant="outlined"
                  label="Colleghi coinvolti"
                  margin="dense"
                  InputProps={{
                    ...params.InputProps,
                    startAdornment: (
                      <>
                        <InputAdornment position="start">
                          <SupervisorAccountIcon color="primary" />
                        </InputAdornment>
                        {params.InputProps.startAdornment}
                      </>
                    ),
                  }}
                />
              )}
            />
          </Grid>

          <Grid item xs={12}>
            <TextField
              label="Note aggiuntive"
              multiline
              rows={3}
              fullWidth
              variant="outlined"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              margin="dense"
            />
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions sx={{ px: 3, pb: 3 }}>
        <Button onClick={onClose} variant="outlined">Annulla</Button>
        <Button 
          onClick={handleCreate} 
          variant="contained"
          disabled={!projectName.trim()}
          startIcon={<AddIcon />}
        >
          Crea Progetto
        </Button>
      </DialogActions>
    </Dialog>
  );
};

function Multiagent() {
  const { toggleColorMode, theme } = useContext(ThemeContext);
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [sidebarOpen, setSidebarOpen] = useState(!isMobile);

  // Stato per la modalitÃ  della pagina (seleziona progetto o lavora su progetto)
  const [pageMode, setPageMode] = useState('select'); // 'select' o 'work'

  // Stato per i progetti
  const [projects, setProjects] = useState([
    { 
