  // Stato per il dialogo di creazione nuovo progetto
  const [newProjectDialog, setNewProjectDialog] = useState(false);

  // Stato per il prompt ed agenti
  const [prompt, setPrompt] = useState('');
  const [selectedAgents, setSelectedAgents] = useState([]);

  // Stato per l'output
  const [output, setOutput] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  // Stato per l'artifact (Lexychain)
  const [artifact, setArtifact] = useState(null);

  // Helper per inserire menzioni degli agenti nel prompt
  const insertAgentMention = (agent) => {
    setPrompt((currentPrompt) => {
      const mention = `${agent.nickName} `;
      return currentPrompt + mention;
    });
  };

  const handleCreateProject = (newProject) => {
    const projectWithId = {
      ...newProject,
      id: Date.now().toString(),
      agents: []
