  const handleSelectProject = (project) => {
    setSelectedProject(project);
    setPageMode('work');
    // Reset degli stati di lavoro
    setPrompt('');
    setSelectedAgents([]);
    setOutput('');
    setArtifact(null);
  };

  const handleSendPrompt = () => {
    if (prompt.trim() && selectedAgents.length > 0) {
      setIsProcessing(true);

      // Simulazione di elaborazione
      setTimeout(() => {
        // Creazione di una risposta che incorpora gli agenti selezionati
        let agentResponses = "";

        selectedAgents.forEach(agent => {
          agentResponses += `\n\n${agent.nickName}: `;

          switch(agent.id) {
            case 'researcher':
              agentResponses += "Ho analizzato la giurisprudenza pertinente e ho identificato 3 precedenti rilevanti che supportano la nostra interpretazione del contratto.";
              break;
            case 'lawyer':
              agentResponses += "Basandomi sugli articoli 1362-1371 del Codice Civile, la clausola in questione dovrebbe essere interpretata a nostro favore.";
              break;
            case 'writer':
              agentResponses += "Ho preparato una bozza di memoria difensiva basata sugli elementi forniti. Ã disponibile nella sezione Lexychain.";
              break;
            case 'notary':
              agentResponses += "Ho verificato la conformitÃ  del documento alle norme vigenti. Ci sono alcune irregolaritÃ  formali che possiamo sfruttare.";
              break;
            case 'mediator':
              agentResponses += "Suggerisco di esplorare una soluzione transattiva. Ho predisposto una strategia di negoziazione nella sezione Lexychain.";
              break;
            case 'coder':
              agentResponses += "L'analisi quantitativa dei precedenti mostra un 78% di successo in casi analoghi con simili importi in gioco.";
              break;
            default:
              agentResponses += "Ho analizzato la richiesta e preparato i materiali necessari per procedere.";
          }
        });

        setOutput(`Risposta generata per: "${prompt}"\n${agentResponses}\n\nLa Lexychain ha generato un documento basato sul lavoro congiunto degli agenti selezionati.`);

        // Simulazione di un artifact nella Lexychain
        setArtifact({
          name: 'Analisi_Giuridica.docx',
          size: '245 KB',
          type: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
          preview: `# Analisi Giuridica\n\n## Introduzione\nQuesto documento Ã¨ stato generato dalla Lexychain in risposta al prompt: "${prompt}"\n\n## Interpretazione Giuridica\n1. Articoli di riferimento: Art. 1362-1371 c.c.\n2. Giurisprudenza rilevante: Cass. civ. n. 8312/2024, Trib. Milano n. 3421/2024\n3. Dottrina pertinente: AutoritÃ  sul tema (Bianca, Galgano)\n\n## Strategia Legale Consigliata\n1. Impugnazione della clausola contrattuale\n2. Proposta transattiva con i seguenti parametri: [dettagli]\n3. Pianificazione cronologica delle azioni legali\n\n## Bozza di Documento\nIn allegato la bozza di memoria difensiva elaborata dall'agente Redattore\n\n## Note aggiuntive\nRaccomandazioni per la gestione del cliente e previsioni di costi`
        });

        setIsProcessing(false);
      }, 3000);
    }
  };

  const handleDownloadArtifact = () => {
    if (artifact) {
      // In una implementazione reale, qui ci sarebbe il codice per scaricare il file
      alert(`Scaricamento di ${artifact.name} iniziato.`);
    }
  };

  return (
    <Box sx={{ 
      display: 'flex', 
      minHeight: '100vh',
      background: theme.palette.mode === 'dark' 
        ? `radial-gradient(circle at 0% 20%, ${alpha('#2D2B55', 0.6)} 0%, transparent 30%),
