            </Typography>
          </Box>

          <Box sx={{ display: 'flex', gap: 1 }}>
            {pageMode === 'work' && (
              <Button
                startIcon={<FormatListBulletedIcon />}
                variant="outlined"
                onClick={() => setPageMode('select')}
