                          label={
                            project.priority === 'high' ? 'Alta prioritÃ ' :
                            project.priority === 'urgent' ? 'Urgente' :
                            project.priority === 'medium' ? 'Media prioritÃ ' : 'Bassa prioritÃ '
                          }
                          color={
                            project.priority === 'high' ? 'warning' :
