                  ) : (
                    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100%', p: 3 }}>
                      <AttachFileIcon sx={{ fontSize: 60, color: 'text.disabled', mb: 2 }} />
                      <Typography variant="body1" align="center" fontWeight={500}>
                        Lexychain in attesa
                      </Typography>
                      <Typography variant="body2" color="text.secondary" align="center" sx={{ mt: 1 }}>
                        I documenti generati dagli agenti appariranno qui
                      </Typography>
                    </Box>
                  )}
                </Quadrant>
              </Grid>
            </Grid>

            {/* Quadrante 3: Output testuale (occupa tutta la parte destra) */}
            <Grid item xs={12} md={6} sx={{ height: '100%' }}>
              <Quadrant>
                <QuadrantTitle variant="h6">
                  <HubIcon /> Risposta del Team di Agenti
                </QuadrantTitle>

                {isProcessing ? (
                  <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100%' }}>
                    <CircularProgress size={40} sx={{ mb: 2 }} />
                    <Typography variant="body1">Elaborazione in corso...</Typography>
                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                      Gli agenti stanno collaborando all'analisi
                    </Typography>
                  </Box>
                ) : output ? (
                  <Box sx={{ 
                    flexGrow: 1, 
                    overflow: 'auto',
                    height: 'calc(100% - 40px)',
                    p: 2,
                    bgcolor: theme => alpha(theme.palette.background.default, 0.5),
                    borderRadius: 2
                  }}>
                    <Typography
                      sx={{
                        fontFamily: '"Inter", sans-serif',
                        whiteSpace: 'pre-wrap',
                        wordBreak: 'break-word',
                      }}
                    >
                      {output.split('\n').map((line, index) => {
                        // Evidenzia i nickname degli agenti
                        if (line.match(/^@[a-z]+:/)) {
                          const [nickname, ...rest] = line.split(':');
                          return (
                            <Box key={index} sx={{ mb: 2 }}>
                              <Chip 
                                label={nickname} 
                                color="primary" 
                                size="small" 
                                sx={{ mb: 1, fontWeight: 'bold' }} 
                              />
