// valis/src/pages/Dashboard.js
import React, { useState, useEffect, useMemo } from 'react';
import { 
  Box, 
  Typography, 
  Button,
  Grid,
  useMediaQuery,
  alpha,
  IconButton,
  Tooltip,
  Container
} from '@mui/material';
import { styled, createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Sidebar from '../components/common/Sidebar';
import MenuIcon from '@mui/icons-material/Menu';
// Icons consistent with sidebar
import HubIcon from '@mui/icons-material/Hub';
import RocketLaunchIcon from '@mui/icons-material/RocketLaunch';
import PsychologyAltIcon from '@mui/icons-material/PsychologyAlt';
import PersonIcon from '@mui/icons-material/Person';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import LightModeIcon from '@mui/icons-material/LightMode';

// Custom theme with a more vibrant violet palette and enhanced styles
const getTheme = (mode) => createTheme({
  palette: {
    mode,
    primary: {
      main: mode === 'dark' ? '#B388FF' : '#7C4DFF',
      light: mode === 'dark' ? '#D7B8FF' : '#B39DDB',
      dark: mode === 'dark' ? '#7B61FF' : '#5E35B1',
      contrastText: '#FFFFFF',
    },
    secondary: {
      main: mode === 'dark' ? '#03DAC5' : '#00BFA5',
      light: mode === 'dark' ? '#5EFFEE' : '#64FFDA',
      dark: mode === 'dark' ? '#00867D' : '#008975',
      contrastText: mode === 'dark' ? '#000000' : '#000000',
    },
    background: {
      default: mode === 'dark' ? '#0B0B1A' : '#F5F7FA',
      paper: mode === 'dark' ? '#14142B' : '#FFFFFF',
    },
    text: {
      primary: mode === 'dark' ? '#F3F4F6' : '#1E293B',
      secondary: mode === 'dark' ? '#B0B8C4' : '#64748B',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontWeight: 800,
      letterSpacing: '-0.025em',
    },
    h2: {
      fontWeight: 800,
      letterSpacing: '-0.025em',
    },
    h3: {
      fontWeight: 700,
      letterSpacing: '-0.02em',
    },
    h4: {
      fontWeight: 700,
      letterSpacing: '-0.015em',
    },
    h5: {
      fontWeight: 600,
      letterSpacing: '-0.01em',
    },
    h6: {
      fontWeight: 600,
      letterSpacing: '-0.005em',
    },
    button: {
      fontWeight: 600,
      letterSpacing: '0.02em',
      textTransform: 'none',
    },
  },
  shape: {
    borderRadius: 16,
  },
  shadows: [
    'none',
    '0px 2px 4px rgba(0, 0, 0, 0.05), 0px 1px 2px rgba(0, 0, 0, 0.05)',
    '0px 4px 8px rgba(0, 0, 0, 0.05), 0px 2px 4px rgba(0, 0, 0, 0.05)',
    '0px 8px 16px rgba(0, 0, 0, 0.05), 0px 4px 8px rgba(0, 0, 0, 0.05)',
    '0px 12px 24px rgba(0, 0, 0, 0.05), 0px 6px 12px rgba(0, 0, 0, 0.05)',
    '0 15px 30px rgba(27, 10, 59, 0.12), 0 8px 16px rgba(27, 10, 59, 0.08)',
  ],
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 12,
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 16,
          backgroundImage: 'none',
        },
      },
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          scrollbarWidth: 'thin',
          '&::-webkit-scrollbar': {
            width: '6px',
            height: '6px',
          },
          '&::-webkit-scrollbar-thumb': {
            backgroundColor: mode === 'dark' ? '#4B5563' : '#CBD5E1',
            borderRadius: '3px',
          },
          '&::-webkit-scrollbar-track': {
            backgroundColor: mode === 'dark' ? '#1F2937' : '#F1F5F9',
          },
        },
      },
    },
  },
});

// Dashboard Container - centered
const DashboardContainer = styled(Box)(({ theme, open, isMobile }) => ({
  flexGrow: 1,
  paddingTop: theme.spacing(4),
  paddingBottom: theme.spacing(3),
  paddingLeft: theme.spacing(3),
  paddingRight: theme.spacing(3),
  transition: theme.transitions.create(['margin', 'padding'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  marginLeft: 0,
  maxWidth: '100%',
  margin: '0 auto',
  ...(open && {
    transition: theme.transitions.create(['margin', 'padding'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: isMobile ? 0 : 280,
    maxWidth: isMobile ? '100%' : 'calc(100% - 280px)',
  }),
  [theme.breakpoints.down('sm')]: {
    paddingLeft: theme.spacing(2),
    paddingRight: theme.spacing(2),
  }
}));

// Sidebar toggle button with enhanced styling
const SidebarToggle = styled(IconButton)(({ theme }) => ({
  position: 'fixed',
  top: 20,
  left: 20,
  zIndex: 1200,
  backgroundColor: alpha(theme.palette.background.paper, 0.8),
  backdropFilter: 'blur(12px)',
  boxShadow: theme.shadows[3],
  '&:hover': {
    backgroundColor: alpha(theme.palette.primary.main, 0.15),
    transform: 'scale(1.08)',
  },
  transition: 'all 0.25s cubic-bezier(0.4, 0, 0.2, 1)'
}));

// Enhanced Feature Button with modern styling and animations
const FeatureButton = styled(Button)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  padding: theme.spacing(6, 4),
  backgroundColor: alpha(theme.palette.background.paper, 0.05),
  backdropFilter: 'blur(8px)',
  borderRadius: 24,
  transition: 'all 0.5s cubic-bezier(0.05, 0.7, 0.1, 1)',
  overflow: 'hidden',
  position: 'relative',
  border: `1px solid ${alpha(theme.palette.primary.main, 0.15)}`,
  boxShadow: theme.palette.mode === 'dark' 
    ? `0 8px 32px ${alpha(theme.palette.primary.main, 0.15)}`
    : `0 8px 32px ${alpha(theme.palette.primary.main, 0.1)}`,
  '&::before': {
    content: '""',
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: `linear-gradient(135deg, ${alpha(theme.palette.primary.main, 0.1)} 0%, transparent 100%)`,
    borderRadius: 24,
    opacity: 0.5,
  },
  '&:hover': {
    transform: 'translateY(-12px)',
    boxShadow: theme.palette.mode === 'dark' 
      ? `0 15px 40px ${alpha(theme.palette.primary.main, 0.3)}`
      : `0 15px 40px ${alpha(theme.palette.primary.main, 0.2)}`,
    backgroundColor: alpha(theme.palette.primary.main, 0.12),
    '& .feature-button-icon': {
      transform: 'scale(1.15) translateY(-8px)',
      filter: `drop-shadow(0 10px 15px ${alpha(theme.palette.primary.main, 0.5)})`,
      opacity: 1,
    },
    '& .feature-button-text': {
      transform: 'translateY(5px) scale(1.05)',
      color: theme.palette.primary.main,
      fontWeight: 700,
      textShadow: `0 2px 4px ${alpha(theme.palette.primary.main, 0.3)}`,
    }
  },
  '&:active': {
    transform: 'translateY(-5px) scale(0.98)',
    boxShadow: theme.palette.mode === 'dark' 
      ? `0 8px 20px ${alpha(theme.palette.primary.main, 0.25)}`
      : `0 8px 20px ${alpha(theme.palette.primary.main, 0.15)}`,
  },
  '& .feature-button-icon': {
    fontSize: 70,
    marginBottom: theme.spacing(3),
    color: theme.palette.primary.main,
    transition: 'all 0.5s cubic-bezier(0.05, 0.7, 0.1, 1)',
    opacity: 0.85,
    filter: `drop-shadow(0 5px 10px ${alpha(theme.palette.primary.main, 0.3)})`,
  },
  '& .feature-button-text': {
    fontWeight: 600,
    fontSize: '1.3rem',
    transition: 'all 0.4s ease',
    letterSpacing: '0.01em',
    color: theme.palette.mode === 'dark' 
      ? theme.palette.primary.light 
      : theme.palette.primary.dark,
  }
}));

// Enhanced Floating Action Buttons
const ActionButton = styled(IconButton)(({ theme }) => ({
  width: 60,
  height: 60,
  backgroundColor: alpha(theme.palette.background.paper, 0.8),
  backdropFilter: 'blur(12px)',
  borderRadius: 20,
  boxShadow: theme.palette.mode === 'dark'
    ? `0 10px 20px ${alpha(theme.palette.common.black, 0.3)}, 
       inset 0 0 0 1px ${alpha(theme.palette.primary.light, 0.2)}`
    : `0 10px 20px ${alpha(theme.palette.common.black, 0.1)}, 
       inset 0 0 0 1px ${alpha(theme.palette.primary.main, 0.1)}`,
  transition: 'all 0.4s cubic-bezier(0.05, 0.7, 0.1, 1)',
  '&:hover': {
    backgroundColor: theme.palette.mode === 'dark'
      ? alpha(theme.palette.primary.main, 0.2)
      : alpha(theme.palette.primary.main, 0.1),
    transform: 'translateY(-8px)',
    boxShadow: theme.palette.mode === 'dark'
      ? `0 15px 25px ${alpha(theme.palette.common.black, 0.4)}, 
         inset 0 0 0 1.5px ${alpha(theme.palette.primary.main, 0.4)}`
      : `0 15px 25px ${alpha(theme.palette.primary.main, 0.2)}, 
         inset 0 0 0 1.5px ${alpha(theme.palette.primary.main, 0.3)}`,
  },
  '&:active': {
    transform: 'translateY(-4px) scale(0.95)',
  },
  '& .MuiSvgIcon-root': {
    fontSize: 28,
    color: theme.palette.primary.main,
    transition: 'all 0.3s ease',
    filter: `drop-shadow(0 2px 5px ${alpha(theme.palette.primary.main, 0.4)})`,
  }
}));

// Enhanced typing effect with smoother transitions
const TypingEffect = ({ messages, typingSpeed = 60, deleteSpeed = 30, delayAfterType = 4000 }) => {
  const [displayText, setDisplayText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isWaiting, setIsWaiting] = useState(false);

  useEffect(() => {
    const currentMessage = messages[currentIndex];

    if (isWaiting) {
      const waitTimer = setTimeout(() => {
        setIsWaiting(false);
        setIsDeleting(true);
      }, delayAfterType);
      return () => clearTimeout(waitTimer);
    }

    if (isDeleting && displayText === '') {
      setIsDeleting(false);
      setCurrentIndex((currentIndex + 1) % messages.length);
      return;
    }

    if (!isDeleting && displayText === currentMessage) {
      setIsWaiting(true);
      return;
    }

    const timeout = setTimeout(() => {
      if (isDeleting) {
        setDisplayText(prev => prev.substring(0, prev.length - 1));
      } else {
        setDisplayText(currentMessage.substring(0, displayText.length + 1));
      }
    }, isDeleting ? deleteSpeed : typingSpeed);

    return () => clearTimeout(timeout);
  }, [displayText, isDeleting, currentIndex, messages, typingSpeed, deleteSpeed, delayAfterType, isWaiting]);

  return <>{displayText}</>
};

// Custom Icon Components for more modern look
const CustomHubIcon = () => (
  <svg width="70" height="70" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M12,2C6.477,2,2,6.477,2,12c0,5.523,4.477,10,10,10s10-4.477,10-10C22,6.477,17.523,2,12,2z M12,5 c1.654,0,3,1.346,3,3s-1.346,3-3,3s-3-1.346-3-3S10.346,5,12,5z M5,11c1.104,0,2,0.896,2,2s-0.896,2-2,2s-2-0.896-2-2 S3.896,11,5,11z M7.5,19.5c-1.104,0-2-0.896-2-2s0.896-2,2-2s2,0.896,2,2S8.604,19.5,7.5,19.5z M16.5,19.5c-1.104,0-2-0.896-2-2 s0.896-2,2-2s2,0.896,2,2S17.604,19.5,16.5,19.5z M19,13c-1.104,0-2-0.896-2-2s0.896-2,2-2s2,0.896,2,2S20.104,13,19,13z"/>
  </svg>
);

const CustomRocketIcon = () => (
  <svg width="70" height="70" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M13.13,22.19L11.5,18.36C13.07,17.78 14.54,17 15.9,16.09L13.13,22.19M5.64,12.5L1.81,10.87L7.91,8.1C7,9.46 6.22,10.93 5.64,12.5M21.61,2.39C21.61,2.39 16.66,0.269 11,5.93C8.81,8.12 7.5,10.53 6.65,12.64C6.37,13.39 6.56,14.21 7.11,14.77L9.24,16.89C9.79,17.45 10.61,17.63 11.36,17.35C13.5,16.53 15.88,15.19 18.07,13C23.73,7.34 21.61,2.39 21.61,2.39M14.54,9.46C13.76,8.68 13.76,7.41 14.54,6.63S16.59,5.85 17.37,6.63C18.14,7.41 18.15,8.68 17.37,9.46C16.59,10.24 15.32,10.24 14.54,9.46M8.88,16.53L7.47,15.12L8.88,16.53Z"/>
  </svg>
);

const CustomAgentIcon = () => (
  <svg width="70" height="70" viewBox="0 0 24 24" className="feature-button-icon">
    <path fill="currentColor" d="M13,3V9H21V3M13,21H21V11H13M3,21H11V15H3M3,13H11V3H3V13Z"/>
  </svg>
);

function Dashboard() {
  const [mode, setMode] = useState(() => {
    // Retrieve theme from local settings or use system preferences
    const savedMode = localStorage.getItem('themeMode');
    if (savedMode) {
      return savedMode;
    }
    return window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
  });

  const theme = useMemo(() => getTheme(mode), [mode]);
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // Latin legal brocards (Latin only, brief)
  const legalBrocards = [
    "Dura lex, sed lex",
    "Pacta sunt servanda",
    "In dubio pro reo",
    "Ubi societas, ibi ius",
    "Ignorantia legis neminem excusat"
  ];

  // Set theme in localStorage when it changes
  useEffect(() => {
    localStorage.setItem('themeMode', mode);
  }, [mode]);

  // Toggle theme function
  const toggleColorMode = () => {
    setMode(prevMode => prevMode === 'light' ? 'dark' : 'light');
  };

  const handleSidebarToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  // Handler for navigation to different sections
  const handleSectionChange = (section) => {
    console.log(`Navigating to ${section}`);
    // Add navigation logic here
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ 
        display: 'flex', 
        minHeight: '100vh', 
        background: theme.palette.mode === 'dark' 
          ? `radial-gradient(circle at 0% 0%, ${alpha(theme.palette.primary.dark, 0.4)} 0%, transparent 30%),
             radial-gradient(circle at 100% 0%, ${alpha(theme.palette.primary.main, 0.3)} 0%, transparent 40%),
             radial-gradient(circle at 50% 100%, ${alpha(theme.palette.primary.dark, 0.25)} 0%, transparent 50%),
             linear-gradient(180deg, ${theme.palette.background.default} 0%, ${alpha('#050510', 0.95)} 100%)`
          : `radial-gradient(circle at 0% 0%, ${alpha(theme.palette.primary.light, 0.1)} 0%, transparent 30%), 
             radial-gradient(circle at 100% 0%, ${alpha(theme.palette.primary.light, 0.08)} 0%, transparent 30%),
             radial-gradient(circle at 50% 100%, ${alpha(theme.palette.primary.light, 0.1)} 0%, transparent 50%),
             ${theme.palette.background.default}`,
        backgroundAttachment: 'fixed',
        position: 'relative'
      }}>
        {/* Animated Particles Effect */}
        <Box sx={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          pointerEvents: 'none',
          zIndex: 0,
          opacity: 0.6,
          backgroundImage: theme.palette.mode === 'dark'
            ? 'url("data:image/svg+xml,%3Csvg width=\'20\' height=\'20\' viewBox=\'0 0 20 20\' xmlns=\'http://www.w3.org/2000/svg\'%3E%3Cg fill=\'%239C92AC\' fill-opacity=\'0.03\' fill-rule=\'evenodd\'%3E%3Ccircle cx=\'3\' cy=\'3\' r=\'3\'/%3E%3Ccircle cx=\'13\' cy=\'13\' r=\'3\'/%3E%3C/g%3E%3C/svg%3E")'
            : 'url("data:image/svg+xml,%3Csvg width=\'20\' height=\'20\' viewBox=\'0 0 20 20\' xmlns=\'http://www.w3.org/2000/svg\'%3E%3Cg fill=\'%23B39DDB\' fill-opacity=\'0.05\' fill-rule=\'evenodd\'%3E%3Ccircle cx=\'3\' cy=\'3\' r=\'3\'/%3E%3Ccircle cx=\'13\' cy=\'13\' r=\'3\'/%3E%3C/g%3E%3C/svg%3E")'
        }} />

        {/* Sidebar */}
        <Sidebar 
          open={sidebarOpen} 
          onClose={() => setSidebarOpen(false)}
          activeSection="dashboard"
          onSectionChange={handleSectionChange}
        />

        {/* Sidebar toggle button */}
        {!sidebarOpen && (
          <SidebarToggle
            color="inherit"
            aria-label="open drawer"
            onClick={handleSidebarToggle}
            edge="start"
          >
            <MenuIcon />
          </SidebarToggle>
        )}

        {/* Main content */}
        <DashboardContainer open={sidebarOpen} isMobile={isMobile}>
          <Container maxWidth="lg" sx={{ position: 'relative', zIndex: 2 }}>
            <Box component="main" sx={{ 
              flexGrow: 1,
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              alignItems: 'center', // Center horizontally
              pt: 4,
              height: '100%',
              minHeight: 'calc(100vh - 120px)',
            }}>
              {/* Welcome message - Latin brocards with enhanced styling */}
              <Box sx={{ 
                mb: 8, 
                mt: { xs: 4, md: 0 },
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                textAlign: 'center',
                maxWidth: '900px'
              }}>
                <Typography 
                  variant="h1" 
                  sx={{ 
                    fontWeight: 800,
                    mb: 2,
                    fontSize: { xs: '2.5rem', sm: '3.5rem', md: '4.5rem' },
                    background: theme.palette.mode === 'dark'
                      ? `linear-gradient(135deg, ${theme.palette.primary.light} 0%, ${theme.palette.primary.main} 100%)`
                      : `linear-gradient(135deg, ${theme.palette.primary.dark} 0%, ${theme.palette.primary.main} 100%)`,
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    color: 'transparent',
                    letterSpacing: '-0.02em',
                    lineHeight: 1.1,
                    fontStyle: 'italic',
                    textShadow: theme.palette.mode === 'dark'
                      ? '0 5px 30px rgba(179, 136, 255, 0.4)'
                      : '0 5px 30px rgba(124, 77, 255, 0.2)',
                    position: 'relative',
                    '&::after': {
                      content: '""',
                      position: 'absolute',
                      width: '30%',
                      height: '3px',
                      left: '35%',
                      bottom: '-10px',
                      background: theme.palette.mode === 'dark' 
                        ? `linear-gradient(90deg, transparent 0%, ${theme.palette.primary.main} 50%, transparent 100%)`
                        : `linear-gradient(90deg, transparent 0%, ${theme.palette.primary.main} 50%, transparent 100%)`,
                      borderRadius: '4px',
                    }
                  }}
                >
                  <TypingEffect messages={legalBrocards} />
                </Typography>
                <Typography 
                  variant="h5" 
                  color="textSecondary"
                  sx={{
                    mt: 4,
                    fontWeight: 500,
                    letterSpacing: '0.01em',
                    opacity: 0.9,
                  }}
                >
                  Seleziona una funzionalità per iniziare
                </Typography>
              </Box>

              {/* Main navigation - centered with enhanced styling */}
              <Box sx={{ 
                width: '100%',
                maxWidth: '1100px', 
                display: 'flex',
                justifyContent: 'center',
                position: 'relative',
                zIndex: 10
              }}>
                <Grid container spacing={4} justifyContent="center">
                  <Grid item xs={12} sm={6} md={4} sx={{ display: 'flex', justifyContent: 'center' }}>
                    <FeatureButton onClick={() => handleSectionChange('multiagente')}>
                      <CustomHubIcon />
                      <Typography className="feature-button-text">Multiagente</Typography>
                    </FeatureButton>
                  </Grid>
                  <Grid item xs={12} sm={6} md={4} sx={{ display: 'flex', justifyContent: 'center' }}>
                    <FeatureButton onClick={() => handleSectionChange('ali')}>
                      <CustomRocketIcon />
                      <Typography className="feature-button-text">ALI</Typography>
                    </FeatureButton>
                  </Grid>
                  <Grid item xs={12} sm={6} md={4} sx={{ display: 'flex', justifyContent: 'center' }}>
                    <FeatureButton onClick={() => handleSectionChange('agenti')}>
                      <CustomAgentIcon />
                      <Typography className="feature-button-text">Agenti</Typography>
                    </FeatureButton>
                  </Grid>
                </Grid>
              </Box>
            </Box>
          </Container>
        </DashboardContainer>

        {/* Profile and theme buttons - bottom right with enhanced styling */}
        <Box sx={{ 
          position: 'fixed', 
          bottom: 30, 
          right: 30, 
          display: 'flex', 
          flexDirection: 'column', 
          gap: 2,
          zIndex: 100
        }}>
          <Tooltip title="Profilo" placement="left" arrow>
            <ActionButton onClick={() => handleSectionChange('profilo')}>
              <PersonIcon />
            </ActionButton>
          </Tooltip>
          <Tooltip title={mode === 'dark' ? "Modalità chiara" : "Modalità scura"} placement="left" arrow>
            <ActionButton onClick={toggleColorMode}>
              {theme.palette.mode === 'dark' 
                ? <LightModeIcon />
                : <DarkModeIcon />
              }
            </ActionButton>
          </Tooltip>
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default Dashboard;
