// src/components/Dashboard/Dashboard.jsx
import React, { useState } from 'react';
import { 
  Box, 
  Grid, 
  Typography, 
  useTheme, 
  Paper,
  Card,
  CardContent,
  CardHeader,
  Avatar,
  Button,
  Divider,
  Chip,
  Tooltip,
  Fade,
  LinearProgress,
  Badge,
  IconButton,
  useMediaQuery
} from '@mui/material';
import { styled } from '@mui/system';
import Header from '../Header/Header';
import MenuIcon from '@mui/icons-material/Menu';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import DescriptionIcon from '@mui/icons-material/Description';
import BusinessCenterIcon from '@mui/icons-material/BusinessCenter';
import GroupWorkIcon from '@mui/icons-material/GroupWork';
import AutoGraphIcon from '@mui/icons-material/AutoGraph';
import StorageIcon from '@mui/icons-material/Storage';
import NewReleasesIcon from '@mui/icons-material/NewReleases';
import LightbulbIcon from '@mui/icons-material/Lightbulb';
import FolderSpecialIcon from '@mui/icons-material/FolderSpecial';
import AutoAwesomeIcon from '@mui/icons-material/AutoAwesome';
import TimelineIcon from '@mui/icons-material/Timeline';

// Animated Container for Dashboard
const DashboardContainer = styled(Box)(({ theme }) => ({
  flexGrow: 1,
  paddingTop: theme.spacing(3),
  paddingBottom: theme.spacing(3),
  paddingLeft: theme.spacing(4),
  paddingRight: theme.spacing(4),
  transition: theme.transitions.create(['margin', 'padding'], {
    easing: theme.transitions.easing.easeOut,
    duration: theme.transitions.duration.enteringScreen,
  }),
  [theme.breakpoints.down('sm')]: {
    paddingLeft: theme.spacing(2),
    paddingRight: theme.spacing(2),
  }
}));

// Styled button for sidebar toggle
const SidebarToggle = styled(IconButton)(({ theme }) => ({
  position: 'fixed',
  top: 70,
  left: 15,
  zIndex: 1200,
  backgroundColor: theme.palette.background.paper,
  boxShadow: theme.palette.mode === 'dark'
    ? '0 2px 10px rgba(0, 0, 0, 0.5)'
    : '0 2px 10px rgba(103, 58, 183, 0.2)',
  '&:hover': {
    backgroundColor: theme.palette.mode === 'dark'
      ? theme.palette.background.default
      : theme.palette.primary.light,
    color: theme.palette.mode === 'dark' 
      ? theme.palette.primary.light 
      : theme.palette.common.white
  }
}));

// Styled cards for dashboard elements
const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  transition: 'all 0.3s ease',
  boxShadow: theme.palette.mode === 'dark'
    ? '0 4px 20px rgba(0, 0, 0, 0.4)'
    : '0 4px 20px rgba(103, 58, 183, 0.1)',
  '&:hover': {
    transform: 'translateY(-5px)',
    boxShadow: theme.palette.mode === 'dark'
      ? '0 8px 25px rgba(0, 0, 0, 0.5)'
      : '0 8px 25px rgba(103, 58, 183, 0.15)',
  }
}));

// Status indicator for agents
const StatusIndicator = styled('div')(({ status, theme }) => {
  let color;
  switch(status) {
    case 'active':
      color = theme.palette.success.main;
      break;
    case 'idle':
      color = theme.palette.warning.main;
      break;
    case 'offline':
      color = theme.palette.text.disabled;
      break;
    default:
      color = theme.palette.info.main;
  }

  return {
    width: 10,
    height: 10,
    borderRadius: '50%',
    backgroundColor: color,
    marginRight: 8,
    display: 'inline-block'
  };
});

// Gradient heading for section titles
const GradientHeading = styled(Typography)(({ theme }) => ({
  fontWeight: 700,
  fontSize: '1.8rem',
  marginBottom: theme.spacing(3),
  background: theme.palette.mode === 'dark'
    ? 'linear-gradient(90deg, #9c64ff 0%, #b794ff 100%)'
    : 'linear-gradient(90deg, #673ab7 0%, #9575cd 100%)',
  backgroundClip: 'text',
  WebkitBackgroundClip: 'text',
  color: 'transparent',
  display: 'inline-block',
  position: 'relative',
  '&::after': {
    content: '""',
    position: 'absolute',
    bottom: -8,
    left: 0,
    width: '50%',
    height: 3,
    background: theme.palette.mode === 'dark'
      ? 'linear-gradient(90deg, #9c64ff 0%, transparent 100%)'
      : 'linear-gradient(90deg, #673ab7 0%, transparent 100%)',
    borderRadius: 2,
  }
}));

// Agent card component
const AgentCard = ({ agent, onActivate }) => {
  const theme = useTheme();

  return (
    <StyledCard>
      <CardHeader
        avatar={
          <Avatar 
            sx={{ 
              bgcolor: theme.palette.mode === 'dark' ? theme.palette.primary.dark : theme.palette.primary.main,
              width: 50,
              height: 50
            }}
          >
            {agent.icon}
          </Avatar>
        }
        action={
          <Box display="flex" alignItems="center">
            <StatusIndicator status={agent.status} />
            <Typography variant="caption" sx={{ mr: 1 }}>
              {agent.status === 'active' ? 'Attivo' : 
               agent.status === 'idle' ? 'In attesa' : 'Offline'}
            </Typography>
            <Chip 
              size="small" 
              label={agent.version} 
              color={agent.isNew ? "secondary" : "default"}
              sx={{ fontWeight: agent.isNew ? 'bold' : 'normal' }}
            />
          </Box>
        }
        title={
          <Typography variant="h6" fontWeight="bold">
            {agent.isNew && (
              <Badge 
                color="secondary"
                badgeContent={<NewReleasesIcon fontSize="small" />}
                sx={{ '& .MuiBadge-badge': { top: -5, right: -20 } }}
              >
                {agent.name}
              </Badge>
            ) || agent.name}
          </Typography>
        }
        subheader={agent.specialty}
      />
      <Divider />
      <CardContent>
        <Typography variant="body2" paragraph color="textSecondary">
          {agent.description}
        </Typography>

        <Box sx={{ mt: 1, mb: 2 }}>
          {agent.capabilities.map((cap, idx) => (
            <Chip
              key={idx}
              size="small"
              label={cap}
              sx={{ 
                mr: 0.5, 
                mb: 0.5,
                background: theme.palette.mode === 'dark' 
                  ? 'rgba(156, 100, 255, 0.1)'
                  : 'rgba(103, 58, 183, 0.08)',
                borderColor: theme.palette.mode === 'dark' 
                  ? 'rgba(156, 100, 255, 0.3)'
                  : 'rgba(103, 58, 183, 0.2)',
                borderWidth: 1,
                borderStyle: 'solid'
              }}
            />
          ))}
        </Box>

        <Box sx={{ mt: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Box>
            <Typography variant="caption" color="textSecondary">Efficienza</Typography>
            <LinearProgress 
              variant="determinate" 
              value={agent.efficiency} 
              sx={{ 
                width: 100, 
                height: 5, 
                borderRadius: 2,
                bgcolor: theme.palette.mode === 'dark' ? 'rgba(255,255,255,0.08)' : 'rgba(0,0,0,0.08)'
              }} 
            />
          </Box>

          <Button 
            variant="contained" 
            color="primary" 
            startIcon={agent.status === 'active' ? <SmartToyIcon /> : <AutoAwesomeIcon />}
            onClick={() => onActivate(agent.id)}
            size="small"
            sx={{ 
              borderRadius: 20,
              textTransform: 'none',
              boxShadow: theme.palette.mode === 'dark'
                ? '0 2px 10px rgba(156, 100, 255, 0.25)'
                : '0 2px 10px rgba(103, 58, 183, 0.3)'
            }}
          >
            {agent.status === 'active' ? 'Interagisci' : 'Attiva'}
          </Button>
        </Box>
      </CardContent>
    </StyledCard>
  );
};

// MultiAgent ALI card component
const MultiAgentSystemCard = ({ system, onActivate }) => {
  const theme = useTheme();

  return (
    <StyledCard sx={{ position: 'relative', overflow: 'visible' }}>
      {system.isNew && (
        <Chip 
          label="Nuovo" 
          color="secondary"
          size="small"
          icon={<NewReleasesIcon fontSize="small" />}
          sx={{ 
            position: 'absolute',
            top: -10,
            right: 30,
            fontWeight: 'bold',
            zIndex: 1,
            boxShadow: '0 2px 10px rgba(0,0,0,0.2)'
          }}
        />
      )}

      <CardHeader
        avatar={
          <Box 
            sx={{ 
              backgroundColor: theme.palette.mode === 'dark' ? 'rgba(156, 100, 255, 0.15)' : 'rgba(103, 58, 183, 0.08)',
              borderRadius: '50%',
              padding: 1.5,
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              border: `2px solid ${theme.palette.primary.main}`,
              width: 60,
              height: 60,
            }}
          >
            {system.icon}
          </Box>
        }
        title={
          <Typography variant="h6" fontWeight="bold">
            {system.name}
          </Typography>
        }
        subheader={
          <Box display="flex" alignItems="center" mt={0.5}>
            <StatusIndicator status={system.status} />
            <Typography variant="body2">
              {system.status === 'active' ? 'Sistema Operativo' : 
               system.status === 'idle' ? 'In Standby' : 'Non Attivo'}
            </Typography>
          </Box>
        }
      />
      <Divider />
      <CardContent>
        <Typography variant="body2" paragraph color="textSecondary">
          {system.description}
        </Typography>

        <Box sx={{ my: 2 }}>
          <Typography variant="subtitle2" fontWeight="bold">
            Agenti nel sistema
          </Typography>
          <Box display="flex" mt={1} flexWrap="wrap">
            {system.agents.map((agent, idx) => (
              <Tooltip 
                title={agent.role} 
                key={idx}
                TransitionComponent={Fade}
                TransitionProps={{ timeout: 600 }}
                arrow
              >
                <Chip
                  avatar={
                    <Avatar 
                      sx={{ 
                        bgcolor: theme.palette.mode === 'dark' ? theme.palette.primary.dark : theme.palette.primary.light,
                        '&.MuiAvatar-root': {
                          width: 24,
                          height: 24
                        }
                      }}
                    >
                      {agent.icon}
                    </Avatar>
                  }
                  label={agent.name}
                  variant="outlined"
                  sx={{ 
                    mr: 0.5, 
                    mb: 0.5, 
                    borderColor: theme.palette.primary.main,
                    '& .MuiChip-avatar': {
                      width: 24,
                      height: 24
                    }
                  }}
                />
              </Tooltip>
            ))}
          </Box>
        </Box>

        <Box sx={{ mt: 2 }}>
          <Typography variant="subtitle2" fontWeight="bold">
            Funzionalità principali
          </Typography>
          <Box sx={{ mt: 1 }}>
            {system.features.map((feature, idx) => (
              <Box 
                key={idx} 
                sx={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  mb: 0.5,
                }}
              >
                <LightbulbIcon 
                  color="primary" 
                  fontSize="small" 
                  sx={{ mr: 1, opacity: 0.7 }} 
                />
                <Typography variant="body2">
                  {feature}
                </Typography>
              </Box>
            ))}
          </Box>
        </Box>

        <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3, alignItems: 'center' }}>
          <Chip 
            label={`v${system.version}`} 
            size="small"
            sx={{ 
              bgcolor: theme.palette.mode === 'dark' ? 'rgba(156, 100, 255, 0.15)' : 'rgba(103, 58, 183, 0.08)',
            }}
          />

          <Button 
            variant="contained" 
            color="primary" 
            startIcon={<GroupWorkIcon />}
            onClick={() => onActivate(system.id)}
            sx={{ 
              borderRadius: 20,
              textTransform: 'none',
              boxShadow: theme.palette.mode === 'dark'
                ? '0 2px 10px rgba(156, 100, 255, 0.25)'
                : '0 2px 10px rgba(103, 58, 183, 0.3)'
            }}
          >
            Attiva Sistema
          </Button>
        </Box>
      </CardContent>
    </StyledCard>
  );
};

function Dashboard({ onOpenSidebar }) {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  // Agents data
  const [agents, setAgents] = useState([
    {
      id: 1,
      name: "LexAnalyzer",
      specialty: "Analisi Contrattuale",
      status: "active",
      icon: <DescriptionIcon />,
      description: "Specializzato nell'analisi e verifica di documenti legali, individua clausole critiche e potenziali rischi contrattuali con precisione avanzata.",
      capabilities: ["Analisi semantica", "Identificazione clausole", "Valutazione rischi"],
      efficiency: 92,
      version: "2.3.0",
      isNew: false
    },
    {
      id: 2,
      name: "CaseBrief AI",
      specialty: "Ricerca Giurisprudenziale",
      status: "idle",
      icon: <BusinessCenterIcon />,
      description: "Ricerca e sintetizza precedenti giurisprudenziali, fornendo riferimenti legali rilevanti per casi specifici. Sfrutta database completi e aggiornati.",
      capabilities: ["Ricerca semantica", "Sintesi sentenze", "Correlazione casi"],
      efficiency: 87,
      version: "3.1.5",
      isNew: true
    },
    {
      id: 3,
      name: "DocDrafter",
      specialty: "Redazione Atti",
      status: "offline",
      icon: <AutoGraphIcon />,
      description: "Assistente avanzato per la redazione di documenti legali, offre template personalizzabili e suggerimenti stilistici conformi alle normative vigenti.",
      capabilities: ["Templates adattivi", "Verifica normativa", "Editing collaborativo"],
      efficiency: 78,
      version: "1.8.4",
      isNew: false
    },
    {
      id: 4,
      name: "LegalPrecedent",
      specialty: "Strategie Processuali",
      status: "idle",
      icon: <TimelineIcon />,
      description: "Analizza strategie processuali storiche e suggerisce approcci ottimali basati su casistiche simili, utilizzando algoritmi predittivi avanzati.",
      capabilities: ["Analisi predittiva", "Mappatura strategica", "Valutazione probabilistica"],
      efficiency: 85,
      version: "2.0.1",
      isNew: false
    }
  ]);

  // MultiAgent systems data
  const [multiAgentSystems, setMultiAgentSystems] = useState([
    {
      id: 101,
      name: "Litigation Intelligence Suite",
      status: "active",
      icon: <StorageIcon fontSize="large" />,
