// Componente messaggi nell'output
const Message = ({ type, content, timestamp, agent }) => {
  const theme = useTheme();

  // Determina lo stile del messaggio in base al tipo
  const getMessageStyle = () => {
    switch (type) {
      case 'system':
        return {
          bgcolor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#F5F5F5', 0.8),
          borderLeft: `3px solid ${theme.palette.info.main}`,
        };
      case 'agent':
        return {
          bgcolor: theme.palette.mode === 'dark' ? alpha('#2D2B55', 0.6) : alpha('#EDF7FF', 0.8),
          borderLeft: `3px solid ${theme.palette.primary.main}`,
        };
      case 'artifact':
        return {
          bgcolor: theme.palette.mode === 'dark' ? alpha('#253D35', 0.6) : alpha('#E8F5E9', 0.8),
          borderLeft: `3px solid ${theme.palette.success.main}`,
        };
      default:
        return {
          bgcolor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#FFFFFF', 0.8),
        };
    }
  };

  return (
    <Box
      sx={{
        p: 2,
        borderRadius: 2,
        mb: 2,
        ...getMessageStyle(),
      }}
    >
      {agent && (
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <Chip 
            label={agent.name} 
            size="small" 
            color="primary" 
            avatar={<PsychologyIcon />} 
            sx={{ mr: 1 }} 
          />
          {timestamp && (
            <Typography variant="caption" color="text.secondary" sx={{ display: 'flex', alignItems: 'center' }}>
              <AccessTimeIcon fontSize="inherit" sx={{ mr: 0.5 }} />
              {timestamp}
            </Typography>
          )}
        </Box>
      )}

      <Typography
        sx={{
          fontFamily: '"Inter", sans-serif',
          whiteSpace: 'pre-wrap',
          wordBreak: 'break-word',
        }}
      >
        {content}
      </Typography>
    </Box>
  );
};

// Componente per log attività nel quadrante Lexychain
const ActivityLog = ({ activities }) => {
  const theme = useTheme();

  const getStatusIcon = (status) => {
    switch(status) {
      case 'thinking':
        return <ThinkingIcon />;
      case 'viewing':
        return <VisibilityIcon />;
      case 'editing':
        return <EditIcon />;
      case 'browsing':
        return <TerminalIcon />;
      default:
        return <AccessTimeIcon />;
    }
  };

  return (
    <Box sx={{ mb: 2 }}>
      <List dense>
        {activities.map((activity, index) => (
          <ListItem 
            key={index} 
            disablePadding 
            sx={{ 
              mb: 1,
              bgcolor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#F5F5F5', 0.5),
              borderRadius: 1,
              overflow: 'hidden'
            }}
          >
            <ListItemButton dense sx={{ py: 0.75 }}>
              <ListItemIcon sx={{ minWidth: 36 }}>
                {getStatusIcon(activity.status)}
              </ListItemIcon>
              <ListItemText 
                primary={activity.description}
                secondary={activity.timestamp}
                primaryTypographyProps={{ fontSize: '0.9rem' }}
                secondaryTypographyProps={{ fontSize: '0.75rem' }}
              />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

function Multiagent() {
  const { toggleColorMode, theme } = useContext(ThemeContext);
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();

  // Stato per la modalità della pagina (seleziona progetto o lavora su progetto)
  const [pageMode, setPageMode] = useState('select'); // 'select' o 'work'

  // Stato per i progetti
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);

  // Stato per il dialogo di creazione nuovo progetto
  const [newProjectDialog, setNewProjectDialog] = useState(false);

  // Stato per il prompt ed agenti
  const [prompt, setPrompt] = useState('');
  const [selectedAgents, setSelectedAgents] = useState([]);

  // Stato per l'output
  const [messages, setMessages] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);

  // Stato per l'artifact (Lexychain)
  const [artifact, setArtifact] = useState(null);
  const [activities, setActivities] = useState([]);

  // Helper per inserire menzioni degli agenti nel prompt
  const insertAgentMention = (agent) => {
    setPrompt((currentPrompt) => {
      const mention = `${agent.nickName} `;
      return currentPrompt + mention;
    });
  };

  const handleCreateProject = (newProject) => {
    const projectWithId = {
      ...newProject,
      id: Date.now().toString(),
      agents: []
    };

    setProjects([projectWithId, ...projects]);
    setNewProjectDialog(false);
    setSelectedProject(projectWithId);
    setPageMode('work');
  };

  const handleSelectProject = (project) => {
    setSelectedProject(project);
    setPageMode('work');
    // Reset degli stati di lavoro
    setPrompt('');
    setSelectedAgents([]);
    setMessages([{
      type: 'system',
      content: `Progetto "${project.name}" caricato. Seleziona gli agenti e inserisci un prompt per iniziare.`,
      timestamp: new Date().toLocaleTimeString()
    }]);
    setArtifact(null);
    setActivities([{
      status: 'viewing',
      description: 'Caricamento del progetto',
      timestamp: new Date().toLocaleTimeString()
    }]);
  };

  const handleSendPrompt = () => {
    if (prompt.trim() && selectedAgents.length > 0) {
      setIsProcessing(true);

      // Aggiungi il messaggio dell'utente
      setMessages(prev => [...prev, {
        type: 'user',
        content: prompt,
        timestamp: new Date().toLocaleTimeString()
      }]);

      // Aggiungi attività
      setActivities(prev => [
        {
          status: 'thinking',
          description: 'Analisi del prompt in corso',
          timestamp: new Date().toLocaleTimeString()
        },
        ...prev
      ]);

      // Simulazione di elaborazione
      setTimeout(() => {
        // Generazione risposta degli agenti
        const agentMessages = selectedAgents.map(agent => ({
          type: 'agent',
          content: `Risposta dell'agente ${agent.name} al prompt: "${prompt}"`,
          timestamp: new Date().toLocaleTimeString(),
          agent
        }));

        setMessages(prev => [...prev, ...agentMessages]);

        // Simulazione artifact
        setArtifact({
          name: 'Documento_Generato.docx',
          size: '256 KB',
          type: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
          content: `Contenuto del documento generato in risposta al prompt: "${prompt}"`
        });

        // Aggiorna attività
        setActivities(prev => [
          {
            status: 'editing',
            description: 'Documento generato',
            timestamp: new Date().toLocaleTimeString()
          },
          ...prev
        ]);

        setIsProcessing(false);
      }, 2000);
    }
  };

  const handleDownloadArtifact = () => {
    if (artifact) {
      // In una implementazione reale, qui ci sarebbe il codice per scaricare il file
      console.log(`Downloading: ${artifact.name}`);

      // Aggiorna attività
      setActivities(prev => [
        {
          status: 'browsing',
          description: `Download di ${artifact.name}`,
          timestamp: new Date().toLocaleTimeString()
        },
        ...prev
      ]);
    }
  };

  return (
    <Box sx={{ 
      display: 'flex',
      flexDirection: 'column',
      minHeight: '100vh',
      background: theme.palette.mode === 'dark' 
        ? `radial-gradient(circle at 0% 20%, ${alpha('#2D2B55', 0.6)} 0%, transparent 30%),
           radial-gradient(circle at 100% 80%, ${alpha('#28284D', 0.6)} 0%, transparent 30%),
           ${theme.palette.background.default}`
        : `radial-gradient(circle at 0% 20%, ${alpha('#F0E7FF', 0.4)} 0%, transparent 30%),
           radial-gradient(circle at 100% 80%, ${alpha('#EDF5FF', 0.4)} 0%, transparent 30%),
           ${theme.palette.background.default}`,
      backgroundAttachment: 'fixed',
      p: 2,
    }}>
      {/* Header con pulsante per tornare alla dashboard */}
    <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        mb: 2,
        pb: 2,
        borderBottom: `1px solid ${alpha(theme.palette.divider, 0.5)}`
      }}>
        <Typography variant="h4" fontWeight={600} color="primary" sx={{ display: 'flex', alignItems: 'center' }}>
          <HubIcon sx={{ mr: 1 }} />
          Multiagente
        </Typography>

        <Box sx={{ display: 'flex', gap: 1 }}>
          <Tooltip title="Tema" arrow>
            <IconButton onClick={toggleColorMode} sx={{ bgcolor: 'background.paper', boxShadow: 1 }}>
              {theme.palette.mode === 'dark' ? <LightModeIcon /> : <DarkModeIcon />}
            </IconButton>
          </Tooltip>
          <Tooltip title="Torna alla Dashboard" arrow>
            <Button
              variant="outlined"
              startIcon={<HomeIcon />}
              onClick={() => navigate('/')}
