                    <Box sx={{ 
                      p: 1.5, 
                      bgcolor: theme.palette.mode === 'dark' ? alpha('#1C1C3C', 0.6) : alpha('#F5F5F5', 0.5),
                      borderBottom: '1px solid',
                      borderColor: 'divider',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'space-between'
                    }}>
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <FileIcon fontSize="small" sx={{ mr: 1, color: 'primary.main' }} />
                        <Typography variant="subtitle2">
                          {artifact ? artifact.name : 'Nessun documento'}
                        </Typography>
                      </Box>

                      {artifact && (
                        <Chip 
                          label={artifact.size} 
                          size="small" 
                          variant="outlined" 
                        />
                      )}
                    </Box>

                    <Box sx={{ p: 2, flexGrow: 1, overflow: 'auto' }}>
                      {artifact ? (
                        <Typography
                          sx={{
                            fontFamily: '"Inter", sans-serif',
                            whiteSpace: 'pre-wrap',
                            wordBreak: 'break-word',
                            fontSize: '0.85rem'
                          }}
                        >
                          {artifact.content}
                        </Typography>
                      ) : (
                        <Box sx={{ 
                          display: 'flex', 
                          flexDirection: 'column', 
                          alignItems: 'center', 
                          justifyContent: 'center',
                          height: '100%',
                          textAlign: 'center'
                        }}>
                          <DescriptionIcon sx={{ fontSize: 40, color: 'text.disabled', mb: 1 }} />
                          <Typography variant="body2" color="text.secondary">
                            I documenti generati appariranno qui
                          </Typography>
                        </Box>
                      )}
                    </Box>

                    {artifact && (
                      <Box sx={{ p: 1.5, borderTop: '1px solid', borderColor: 'divider' }}>
                        <Button
                          fullWidth
                          variant="outlined"
                          startIcon={<FileDownloadIcon />}
                          onClick={handleDownloadArtifact}
                        >
                          Scarica Documento
                        </Button>
                      </Box>
                    )}
                  </Box>
                </Box>
              </Quadrant>
            </Box>
          </Box>

          {/* Input area (fissata in basso) */}
          <PromptInputBox sx={{ mt: 'auto' }}>
            <Box sx={{ width: '100%' }}>
              <Box sx={{ mb: 1 }}>
                <AgentSelector
                  selectedAgents={selectedAgents}
                  onAgentChange={setSelectedAgents}
                />
              </Box>

              <Box sx={{ display: 'flex' }}>
                <TextField
                  placeholder="Inserisci il tuo prompt..."
                  fullWidth
                  multiline
                  maxRows={3}
                  variant="standard"
                  InputProps={{ disableUnderline: true }}
                  value={prompt}
                  onChange={(e) => setPrompt(e.target.value)}
                  disabled={isProcessing}
                />
                <Button
                  variant="contained"
                  color="primary"
                  endIcon={<SendIcon />}
                  sx={{ ml: 2, alignSelf: 'flex-end' }}
                  disabled={!prompt.trim() || selectedAgents.length === 0 || isProcessing}
                  onClick={handleSendPrompt}
                >
                  Invia
                </Button>
              </Box>
            </Box>
          </PromptInputBox>
        </Box>
      )}

      {/* Dialog per nuovo progetto */}
      <NewProjectDialog
        open={newProjectDialog}
        onClose={() => setNewProjectDialog(false)}
        onCreate={handleCreateProject}
      />
    </Box>
  );
}

export default Multiagent;
