// src/components/common/Sidebar.js
import React from 'react';
import {
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Divider,
  Box,
  IconButton,
  Typography,
  useTheme,
  alpha
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import PersonIcon from '@mui/icons-material/Person';
import GroupsIcon from '@mui/icons-material/Groups';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PsychologyIcon from '@mui/icons-material/Psychology';
import DescriptionIcon from '@mui/icons-material/Description';
import AssignmentIcon from '@mui/icons-material/Assignment';
import BarChartIcon from '@mui/icons-material/BarChart';
import DashboardIcon from '@mui/icons-material/Dashboard';

const sections = [
  { name: 'Dashboard', icon: <DashboardIcon />, id: 'welcome' },
  { name: 'Profilo', icon: <PersonIcon />, id: 'profile' },
  { name: 'Multiagente', icon: <GroupsIcon />, id: 'multiagent' },
  { name: 'ALI', icon: <SmartToyIcon />, id: 'ali' },
  { name: 'Agenti', icon: <PsychologyIcon />, id: 'agents' },
  { name: 'Documenti', icon: <DescriptionIcon />, id: 'my-documents' },
  { name: 'Analisi Legali', icon: <AssignmentIcon />, id: 'legal-analysis' },
  { name: 'Reportistica', icon: <BarChartIcon />, id: 'reports' },
];

function Sidebar({ activeSection, onSectionChange, onClose }) {
  const theme = useTheme();

  return (
    <Drawer
      anchor="left"
      open={true}
      variant="persistent"
      sx={{
        '& .MuiDrawer-paper': {
          width: 260,
          boxSizing: 'border-box',
          boxShadow: theme.shadows[6],
          border: 'none',
          backgroundImage: theme.palette.mode === 'dark' 
            ? 'linear-gradient(to bottom, rgba(90, 60, 170, 0.15), transparent)' 
            : 'linear-gradient(to bottom, rgba(103, 58, 183, 0.08), transparent)',
          backgroundSize: '100% 30%',
          backgroundRepeat: 'no-repeat'
        },
      }}
    >
      <Box sx={{ 
        p: 2, 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center'
      }}>
        <Typography 
          variant="h6" 
          sx={{ 
            fontWeight: 700,
            fontFamily: 'Montserrat, sans-serif',
            letterSpacing: '1px',
            background: theme.palette.mode === 'dark' 
              ? 'linear-gradient(45deg, #e9e1ff 30%, #b794ff 90%)'
              : 'linear-gradient(45deg, #4527a0 30%, #673ab7 90%)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            color: 'transparent',
          }}
        >
          VALIS
        </Typography>
        <IconButton 
          onClick={onClose} 
          size="small"
          sx={{ 
            color: theme.palette.text.secondary,
            '&:hover': {
              backgroundColor: alpha(theme.palette.primary.main, 0.1)
            }
          }}
        >
          <CloseIcon fontSize="small" />
        </IconButton>
      </Box>

      <Divider sx={{ mx: 2, borderColor: alpha(theme.palette.primary.main, 0.1) }} />

      <List sx={{ px: 2, py: 1 }}>
        {sections.map((section) => {
          const isActive = activeSection === section.id;
          return (
            <ListItem key={section.id} disablePadding sx={{ mb: 0.75 }}>
              <ListItemButton
                selected={isActive}
                onClick={() => {
                  onSectionChange(section.id);
                  if (section.id !== 'welcome') onClose();
                }}
                sx={{
                  borderRadius: 2,
                  position: 'relative',
                  transition: 'all 0.2s ease',
                  overflow: 'hidden',
                  '&.Mui-selected': {
                    backgroundColor: theme.palette.mode === 'dark' 
                      ? alpha(theme.palette.primary.main, 0.15) 
                      : alpha(theme.palette.primary.main, 0.1),
                    '&::before': {
                      content: '""',
                      position: 'absolute',
                      left: 0,
                      top: 0,
                      bottom: 0,
                      width: 4,
                      backgroundColor: theme.palette.primary.main,
                      borderTopRightRadius: 4,
                      borderBottomRightRadius: 4,
                    }
                  },
                  '&:hover': {
                    backgroundColor: theme.palette.mode === 'dark' 
                      ? alpha(theme.palette.primary.main, 0.1) 
                      : alpha(theme.palette.primary.main, 0.06),
                  }
                }}
              >
                <ListItemIcon
                  sx={{
                    color: isActive 
                      ? 'primary.main' 
                      : 'text.secondary',
                      minWidth: 40,
                      transition: 'color 0.2s ease'
                  }}
                >
                  {section.icon}
                </ListItemIcon>
                <ListItemText 
                  primary={section.name}
                  primaryTypographyProps={{
                    fontWeight: isActive ? 600 : 400,
                    transition: 'font-weight 0.2s ease',
                  }}
                />
              </ListItemButton>
            </ListItem>
          )
        })}
      </List>

      <Box sx={{ flexGrow: 1 }} />

      <Box sx={{ p: 2, textAlign: 'center' }}>
        <Typography variant="body2" color="textSecondary">
          Valis by Lexygraph AI
        </Typography>
        <Typography variant="caption" color="textSecondary" sx={{ opacity: 0.7 }}>
          &copy; {new Date().getFullYear()} - Tutti i diritti riservati
        </Typography>
      </Box>
    </Drawer>
  );
}

export default Sidebar;
